// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.20;

import "./TestSetup.sol";

contract CreateIncentiveTest is TestSetup {

    function testCreateIncentive(
        uint112 amount,
        uint32 startTime,
        uint32 endTime
    ) public {
        _createIncentive(address(tokenA), address(tokenB), amount, startTime, endTime);
    }

    function testFailCreateIncentiveInvalidRewardToken(uint32 startTime, uint32 endTime) public {
        _createIncentive(address(tokenA), zeroAddress, 1, startTime, endTime);
    }

    function testUpdateIncentive(
        int112 changeAmount0,
        int112 changeAmount1,
        uint32 startTime0,
        uint32 startTime1,
        uint32 endTime0,
        uint32 endTime1
    ) public {
        _updateIncentive(ongoingIncentive, changeAmount0, startTime0, endTime0);
        _updateIncentive(ongoingIncentive, changeAmount1, startTime1, endTime1);
    }

    function testStake(uint112 amount0, uint112 amount1) public {
        _stake(address(tokenA), amount0, janeDoe, true);
        _stake(address(tokenA), amount0, janeDoe, true);
        _stake(address(tokenA), amount1, janeDoe, true);
        _stake(address(tokenA), amount1, johnDoe, true);
    }

    function testSubscribe() public {
        _subscribeToIncentive(pastIncentive, johnDoe);
        _subscribeToIncentive(futureIncentive, johnDoe);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
    }

    function testStakeAndSubscribeSeparate(uint112 amount) public {
        _stake(address(tokenA), amount, johnDoe, true);
        _subscribeToIncentive(pastIncentive, johnDoe);
        _subscribeToIncentive(futureIncentive, johnDoe);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
    }

    function testStakeAndSubscribe(uint112 amount) public {
      uint256[] memory idsToSubscribe = new uint256[](2);
      idsToSubscribe[0] = pastIncentive;
      idsToSubscribe[1] = ongoingIncentive;
      _stakeAndSubscribeToIncentives(address(tokenA), amount, idsToSubscribe, johnDoe, true);
    }

    function testAccrue(uint112 amount) public {
        _stake(address(tokenA), amount, johnDoe, true);
        _accrueRewards(pastIncentive);
        _accrueRewards(ongoingIncentive);
        _accrueRewards(futureIncentive);
        _subscribeToIncentive(pastIncentive, johnDoe);
        _subscribeToIncentive(futureIncentive, johnDoe);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        _accrueRewards(pastIncentive);
        _accrueRewards(ongoingIncentive);
        _accrueRewards(futureIncentive);
        uint256 step = testIncentiveDuration / 2;
        vm.warp(block.timestamp + step);
        _accrueRewards(pastIncentive);
        _accrueRewards(ongoingIncentive);
        _accrueRewards(futureIncentive);
        vm.warp(block.timestamp + step);
        _accrueRewards(ongoingIncentive);
        _accrueRewards(futureIncentive);
        vm.warp(block.timestamp + step);
        _accrueRewards(futureIncentive);
        vm.warp(block.timestamp + step);
        _accrueRewards(futureIncentive);
        vm.warp(block.timestamp + step);
        _accrueRewards(futureIncentive);
        _accrueRewards(0);
        _accrueRewards(stakingContract.incentiveCount() + 1);
    }

    function testClaimRewards0() public {
        _claimReward(pastIncentive, johnDoe);
        _claimReward(pastIncentive, johnDoe);
        _claimReward(futureIncentive, johnDoe);
        _claimReward(futureIncentive, johnDoe);
        _claimReward(ongoingIncentive, johnDoe);
        _claimReward(ongoingIncentive, johnDoe);
        _stake(address(tokenA), 1, johnDoe, true);
        _subscribeToIncentive(pastIncentive, johnDoe);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        _subscribeToIncentive(futureIncentive, johnDoe);
        _claimReward(pastIncentive, johnDoe);
        _claimReward(pastIncentive, johnDoe);
        _claimReward(futureIncentive, johnDoe);
        _claimReward(futureIncentive, johnDoe);
        _claimReward(ongoingIncentive, johnDoe);
        _claimReward(ongoingIncentive, johnDoe);
    }

    function testClaimRewards1(uint112 amount) public {
        if (amount == 0) return;
        _stake(address(tokenA), amount, johnDoe, true);
        StakingContractMainnet.Incentive memory incentive = _getIncentive(ongoingIncentive);
        uint256 totalReward = incentive.rewardRemaining;
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        vm.warp(incentive.endTime);
        uint256 reward = _claimReward(ongoingIncentive, johnDoe);
        assertEqInexact(reward, totalReward, 1);
        incentive = _getIncentive(ongoingIncentive);
        assertEq(incentive.rewardRemaining, 0);
    }

    function testClaimRewards2(uint96 amount0, uint96 amount1) public {
        if (amount0 == 0 || amount1 == 0) return;
        uint256 maxRatio = 1000000;
        if (amount0 / amount1 > 1000000) return; // to avoid rounding innacuracies for easier testing
        if (amount1 / amount0 > 1000000) return;

        StakingContractMainnet.Incentive memory incentive = _getIncentive(ongoingIncentive);
        uint256 totalReward = incentive.rewardRemaining;
        _stake(address(tokenA), amount0, johnDoe, true);
        _stake(address(tokenA), amount1, janeDoe, true);
        _subscribeToIncentive(ongoingIncentive, johnDoe);

        vm.warp((incentive.lastRewardTime + incentive.endTime) / 2);
        uint256 soloReward = _claimReward(ongoingIncentive, johnDoe);

        _subscribeToIncentive(ongoingIncentive, janeDoe);
        vm.warp(incentive.endTime);

        uint256 reward0 = _claimReward(ongoingIncentive, johnDoe);
        uint256 reward1 = _claimReward(ongoingIncentive, janeDoe);

        incentive = _getIncentive(ongoingIncentive);
        uint256 ratio;
        if (amount0 / amount1 > 0) {
            ratio = maxRatio * amount0 / amount1;
            assertEqInexact(maxRatio * reward0 / reward1, ratio, 10);
        }
        if (amount1 / amount0 > 0) {
            ratio = maxRatio * amount1 / amount0;
            assertEqInexact(maxRatio * reward1 / reward0, ratio, 10);
        }
        assertEqInexact(reward0 + reward1 + soloReward, totalReward, 10);
    }

    function testUnstakeSaveRewards() public {
        _stake(address(tokenA), 1, johnDoe, true);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        StakingContractMainnet.Incentive memory incentive = _getIncentive(ongoingIncentive);
        uint256 rewardRemaining = incentive.rewardRemaining;
        vm.warp((incentive.lastRewardTime + incentive.endTime) / 2);
        _stake(address(tokenA), 1, johnDoe, false);
        vm.warp((incentive.lastRewardTime + incentive.endTime) / 2 + 100);
        _stake(address(tokenA), 1, johnDoe, false);
        vm.warp(incentive.endTime);
        _unstake(address(tokenA), 1, johnDoe, false);
        uint256 reward = _claimReward(ongoingIncentive, johnDoe);
        assertEq(reward, rewardRemaining);
        reward = _claimReward(ongoingIncentive, johnDoe);
        assertEq(reward, 0);
    }

    function testFalseStakeAndSubscribe(uint112 amount) public {
        _stake(address(tokenA), amount, johnDoe, true);
        _subscribeToIncentive(0, johnDoe);
        _subscribeToIncentive(stakingContract.incentiveCount() + 1, johnDoe);
    }

    function testStakeInvalidToken() public {
        vm.prank(johnDoe);
        vm.expectRevert(noToken);
        stakingContract.stakeToken(janeDoe, 1, true);
    }

    function testRewardRate() public {
        _stake(address(tokenA), 1, johnDoe, true);
        _subscribeToIncentive(ongoingIncentive, johnDoe);
        uint256 oldRate = _rewardRate(ongoingIncentive);
        vm.warp(block.timestamp + testIncentiveDuration / 2);
        stakingContract.accrueRewards(ongoingIncentive);
        uint256 newRate = _rewardRate(ongoingIncentive);
        assertEq(oldRate, newRate);
    }

    function testBatch() public {
        bytes[] memory data = new bytes[](2);
        data[0] = abi.encodeCall(stakingContract.stakeToken, (address(tokenA), 1, true));
        data[1] = abi.encodeCall(stakingContract.subscribeToIncentive, (ongoingIncentive));
        vm.prank(johnDoe);
        stakingContract.batch(data);
        (uint112 liquidity, uint144 subscriptions) = stakingContract.userStakes(johnDoe, address(tokenA));
        assertEq(liquidity, 1);
        assertEq(subscriptions, ongoingIncentive);
    }

    function testBatch2() public {
      bytes[] memory data = new bytes[](3);
      uint256[] memory idsToSubscribe = new uint256[](3);
      idsToSubscribe[0] = pastIncentive;
      idsToSubscribe[1] = ongoingIncentive;
      idsToSubscribe[2] = futureIncentive;

      data[0] = abi.encodeCall(stakingContract.stakeAndSubscribeToIncentives, (address(tokenA), 1, idsToSubscribe, true));
      data[1] = abi.encodeCall(stakingContract.unsubscribeFromIncentive, (address(tokenA), 1, false));
      data[2] = abi.encodeCall(stakingContract.unsubscribeFromIncentive, (address(tokenA), 0, false));

      vm.prank(johnDoe);
      stakingContract.batch(data);
      (uint112 liquidity, uint144 subscriptions) = stakingContract.userStakes(johnDoe, address(tokenA));

      assertEq(liquidity, 1);
      assertEq(subscriptions, pastIncentive);
    }

    function testFailedBatchRaisesProperError() public {
        bytes[] memory data = new bytes[](3);
        data[0] = abi.encodeCall(stakingContract.stakeToken, (address(tokenA), 1, true));
        data[1] = abi.encodeCall(stakingContract.subscribeToIncentive, (ongoingIncentive));
        data[2] = abi.encodeCall(stakingContract.subscribeToIncentive, (ongoingIncentive));
        vm.prank(johnDoe);
        vm.expectRevert(alreadySubscribed);
        stakingContract.batch(data);
    }

}

contracts/Rewards/test/TestSetup.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.20;

import "forge-std/Test.sol";
import "../StakingContractMainnet.sol";
import "./mock/Token.sol";

contract TestSetup is Test {

    address johnDoe = 0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266;
    address janeDoe = 0x70997970C51812dc3A010C7d01b50e0d17dc79C8;
    address zeroAddress = 0x0000000000000000000000000000000000000000;

    uint256 MAX_UINT256 = type(uint256).max;
    uint112 MAX_UINT112 = type(uint112).max;
    uint32 testIncentiveDuration = 2592000;
    uint112 testIncentiveAmount = 1e21;

    bytes4 invalidTimeFrame = bytes4(keccak256("InvalidTimeFrame()"));
    bytes4 notSubscribed = bytes4(keccak256("NotSubscribed()"));
    bytes4 alreadySubscribed = bytes4(keccak256("AlreadySubscribed()"));
    bytes4 noToken = bytes4(keccak256("NoToken()"));
    bytes4 invalidInput = bytes4(keccak256("InvalidInput()"));
    bytes4 insufficientStakedAmount = bytes4(keccak256("InsufficientStakedAmount"));
    bytes4 notStaked = bytes4(keccak256("NotStaked()"));
    bytes4 invalidIndex = bytes4(keccak256("InvalidIndex()"));
    bytes4 panic = 0x4e487b71;
    bytes overflow = abi.encodePacked(panic, bytes32(uint256(0x11)));

    StakingContractMainnet stakingContract = new StakingContractMainnet();

    Token tokenA = new Token();
    Token tokenB = new Token();
    Token tokenC = new Token();

    uint256 pastIncentive;
    uint256 ongoingIncentive;
    uint256 futureIncentive;

    function setUp() public {
        tokenA.mint(MAX_UINT256);
        tokenB.mint(MAX_UINT256);
        tokenC.mint(MAX_UINT256);

        tokenA.approve(address(stakingContract), MAX_UINT256);
        tokenB.approve(address(stakingContract), MAX_UINT256);
        tokenC.approve(address(stakingContract), MAX_UINT256);

        tokenA.transfer(johnDoe, MAX_UINT112);
        tokenA.transfer(janeDoe, MAX_UINT112);

        vm.prank(johnDoe);
        tokenA.approve(address(stakingContract), MAX_UINT256);

        vm.prank(janeDoe);
        tokenA.approve(address(stakingContract), MAX_UINT256);

        uint112 amount = testIncentiveAmount;
        uint256 currentTime = testIncentiveDuration * 30;
        uint256 duration = testIncentiveDuration;

        vm.warp(currentTime - duration);
        pastIncentive = _createIncentive(address(tokenA), address(tokenB), amount, uint32(block.timestamp), uint32(block.timestamp + duration));
        vm.warp(currentTime);
        ongoingIncentive = _createIncentive(address(tokenA), address(tokenB), amount, uint32(block.timestamp), uint32(block.timestamp + duration));
        futureIncentive = _createIncentive(address(tokenA), address(tokenB), amount, uint32(block.timestamp + duration), uint32(block.timestamp + duration * 2));
    }

    function testFail_basic_sanity() public {
        assertTrue(false);
    }

    function test_basic_sanity() public {
        assertTrue(true);
    }

    function _createIncentive(
        address token,
        address rewardToken,
        uint112 amount,
        uint32 startTime,
        uint32 endTime
    ) public returns(uint256) {
        uint256 count = stakingContract.incentiveCount();
        uint256 thisBalance = Token(rewardToken).balanceOf(address(this));
        uint256 stakingContractBalance = Token(rewardToken).balanceOf(address(stakingContract));

        if (amount <= 0) {
          vm.expectRevert(invalidInput);
          return stakingContract.createIncentive(token, rewardToken, amount, startTime, endTime);
        }

        if (endTime <= startTime || endTime <= block.timestamp) {
            vm.expectRevert(invalidTimeFrame);
            return stakingContract.createIncentive(token, rewardToken, amount, startTime, endTime);
        }

        uint256 id = stakingContract.createIncentive(token, rewardToken, amount, startTime, endTime);

        StakingContractMainnet.Incentive memory incentive = _getIncentive(id);

        assertEq(incentive.creator, address(this));
        assertEq(incentive.token, token);
        assertEq(incentive.rewardToken, rewardToken);
        assertEq(incentive.lastRewardTime, startTime < block.timestamp ? uint32(block.timestamp) : startTime);
        assertEq(incentive.endTime, endTime);
        assertEq(incentive.rewardRemaining, amount);
        assertEq(incentive.liquidityStaked, 0);
        assertEq(incentive.rewardPerLiquidity, type(uint256).max / 2);
        assertEq(count + 1, id);
        assertEq(stakingContract.incentiveCount(), id);
        assertEq(thisBalance - amount, Token(rewardToken).balanceOf(address(this)));
        assertEq(stakingContractBalance + amount, Token(rewardToken).balanceOf(address(stakingContract)));
        return id;
    }

    function _updateIncentive(
        uint256 incentiveId,
        int112 changeAmount,
        uint32 startTime,
        uint32 endTime
    ) public {
      StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
      Token(incentive.rewardToken).balanceOf(address(this));
      Token(incentive.rewardToken).balanceOf(address(stakingContract));
      uint32 newStartTime = startTime == 0 ? incentive.lastRewardTime : (startTime < uint32(block.timestamp) ? uint32(block.timestamp) : startTime);
      uint32 newEndTime = endTime == 0 ? incentive.endTime : (endTime < uint32(block.timestamp) ? uint32(block.timestamp) : endTime);

      if (newStartTime >= newEndTime) {
          vm.expectRevert(invalidTimeFrame);
          stakingContract.updateIncentive(incentiveId, changeAmount, startTime, endTime);
          return;
      }

      if (changeAmount == type(int112).min) {
          vm.expectRevert(overflow);
          stakingContract.updateIncentive(incentiveId, changeAmount, startTime, endTime);
          return;
      }

      if (changeAmount > 0 && uint112(changeAmount) + uint256(incentive.rewardRemaining) > type(uint112).max) {
          vm.expectRevert(overflow);
          stakingContract.updateIncentive(incentiveId, changeAmount, startTime, endTime);
          return;
      }

      stakingContract.updateIncentive(incentiveId, changeAmount, startTime, endTime);

      if (changeAmount < 0 && uint112(-changeAmount) > incentive.rewardRemaining) {
          changeAmount = -int112(incentive.rewardRemaining);
      }

      StakingContractMainnet.Incentive memory updatedIncentive = _getIncentive(incentiveId);
      assertEq(updatedIncentive.lastRewardTime, newStartTime);
      assertEq(updatedIncentive.endTime, newEndTime);
      assertEq(updatedIncentive.rewardRemaining, changeAmount < 0 ? incentive.rewardRemaining - uint112(-changeAmount) : incentive.rewardRemaining + uint112(changeAmount));
      assertEq(updatedIncentive.creator, incentive.creator);
      assertEq(updatedIncentive.token, incentive.token);
      assertEq(updatedIncentive.rewardToken, incentive.rewardToken);
      assertEq(updatedIncentive.liquidityStaked, incentive.liquidityStaked);
    }

    function _stake(address token, uint112 amount, address from, bool transferRewards) public {
        // todo check if current incentives stakes got updates correctly
        uint256 userBalanceBefore = Token(token).balanceOf(from);
        uint256 stakingContractBalanceBefore = Token(token).balanceOf(address(stakingContract));
        uint256 userLiquidityBefore = _getUsersLiquidityStaked(from, token);

        if (amount > userBalanceBefore) {
            vm.expectRevert("TRANSFER_FROM_FAILED");
            vm.prank(from);
            stakingContract.stakeToken(token, amount, transferRewards);
            return;
        }

        if (amount > type(uint112).max - userLiquidityBefore) {
            vm.expectRevert(overflow);
            vm.prank(from);
            stakingContract.stakeToken(token, amount, transferRewards);
            return;
        }

        vm.prank(from);
        stakingContract.stakeToken(token, amount, transferRewards);

        uint256 userBalanceAfter = Token(token).balanceOf(from);
        uint256 stakingContractBalanceAfter = Token(token).balanceOf(address(stakingContract));
        uint256 userLiquidityAfter = _getUsersLiquidityStaked(from, token);

        assertEq(userBalanceBefore - amount, userBalanceAfter);
        assertEq(userLiquidityBefore + amount, userLiquidityAfter);
        assertEq(stakingContractBalanceBefore + amount, stakingContractBalanceAfter);
    }

    function _unstake(address token, uint112 amount, address from, bool transferRewards) public {
        uint256 userBalanceBefore = Token(token).balanceOf(from);
        uint256 userLiquidityBefore = _getUsersLiquidityStaked(from, token);

        if (amount > userLiquidityBefore) {
            vm.expectRevert(insufficientStakedAmount);
            vm.prank(from);
            stakingContract.unstakeToken(token, amount, transferRewards);
            return;
        }

        vm.prank(from);
        stakingContract.unstakeToken(token, amount, transferRewards);

        uint256 userBalanceAfter = Token(token).balanceOf(from);
        uint256 userLiquidityAfter = _getUsersLiquidityStaked(from, token);

        assertEq(userBalanceBefore + amount, userBalanceAfter);
        assertEq(userLiquidityBefore - amount, userLiquidityAfter);
    }

    function _subscribeToIncentive(uint256 incentiveId, address from) public {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        uint112 liquidity = _getUsersLiquidityStaked(from, incentive.token);

        (uint256 rpl) = stakingContract.rewardPerLiquidityLast(from, incentiveId);

        if (incentiveId > stakingContract.incentiveCount() || incentiveId <= 0) {
          vm.prank(from);
          vm.expectRevert(invalidInput);
          return stakingContract.subscribeToIncentive(incentiveId);
        }

        if (rpl != 0) {
            vm.prank(from);
            vm.expectRevert(alreadySubscribed);
            return stakingContract.subscribeToIncentive(incentiveId);
        }

        if (liquidity == 0) {
          vm.prank(from);
          vm.expectRevert(notStaked);
          return stakingContract.subscribeToIncentive(incentiveId);
        }

        vm.prank(from);
        stakingContract.subscribeToIncentive(incentiveId);

        StakingContractMainnet.Incentive memory incentiveAfter = _getIncentive(incentiveId);
        uint144 subscribedIncentives = _getUsersSubscribedIncentives(from, incentive.token);

        assertEq(incentive.liquidityStaked + liquidity, incentiveAfter.liquidityStaked);
        assertEq(uint24(subscribedIncentives), uint24(incentiveId));
        assertEq(stakingContract.rewardPerLiquidityLast(from, incentiveId), incentiveAfter.rewardPerLiquidity);
        if (block.timestamp > incentive.lastRewardTime) {
          assertEq(incentiveAfter.lastRewardTime, block.timestamp > incentiveAfter.endTime ? incentiveAfter.endTime : block.timestamp);
        } else {
          assertEq(incentiveAfter.lastRewardTime, incentive.lastRewardTime);
        }
    }

    function _stakeAndSubscribeToIncentives(address token, uint112 amount, uint256[] memory incentiveIds, address from, bool transferRewards) public {
        uint256 userBalanceBefore = Token(token).balanceOf(from);
        uint256 stakingContractBalanceBefore = Token(token).balanceOf(address(stakingContract));
        uint256 userLiquidityBefore = _getUsersLiquidityStaked(from, token);
        uint144 subscribedIncentivesBefore = _getUsersSubscribedIncentives(from, token);

        if (amount > userBalanceBefore) {
            vm.expectRevert(overflow);
            vm.prank(from);
            stakingContract.stakeAndSubscribeToIncentives(token, amount, incentiveIds, transferRewards);
            return;
        }

        if (amount > type(uint112).max - userLiquidityBefore) {
            vm.expectRevert(overflow);
            vm.prank(from);
            stakingContract.stakeAndSubscribeToIncentives(token, amount, incentiveIds, transferRewards);
            return;
        }

        if (amount == 0) {
          vm.prank(from);
          vm.expectRevert(notStaked);
          stakingContract.stakeAndSubscribeToIncentives(token, amount, incentiveIds, transferRewards);
          return;
        }

        vm.prank(from);
        stakingContract.stakeAndSubscribeToIncentives(token, amount, incentiveIds, transferRewards);

        uint256 userBalanceAfter = Token(token).balanceOf(from);
        uint256 stakingContractBalanceAfter = Token(token).balanceOf(address(stakingContract));
        uint256 userLiquidityAfter = _getUsersLiquidityStaked(from, token);
        uint144 subscribedIncentivesAfter = _getUsersSubscribedIncentives(from, token);

        assertEq(userBalanceBefore - amount, userBalanceAfter);
        assertEq(userLiquidityBefore + amount, userLiquidityAfter);
        assertEq(stakingContractBalanceBefore + amount, stakingContractBalanceAfter);
        assertLt(subscribedIncentivesBefore, subscribedIncentivesAfter);
    }

    function _accrueRewards(uint256 incentiveId) public {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        uint256 rewardPerLiquidity;
        uint256 rewardRemaining;
        uint256 lastRewardTime;
        uint256 maxTime = block.timestamp < incentive.endTime ? block.timestamp : incentive.endTime;
        if (
            incentive.liquidityStaked > 0 &&
            incentive.lastRewardTime < maxTime
        ) {
            (rewardPerLiquidity, rewardRemaining, lastRewardTime) = _calculateAccrueChange(incentiveId);
        } else {
            rewardPerLiquidity = incentive.rewardPerLiquidity;
            rewardRemaining = incentive.rewardRemaining;
            if (incentive.lastRewardTime < block.timestamp){
              lastRewardTime = maxTime;
            } else {
              lastRewardTime = incentive.lastRewardTime;
            }
        }

        if (incentiveId > stakingContract.incentiveCount() || incentiveId <= 0) {
          vm.expectRevert(invalidInput);
          stakingContract.accrueRewards(incentiveId);
          return;
        }

        stakingContract.accrueRewards(incentiveId);
        StakingContractMainnet.Incentive memory updatedIncentive = _getIncentive(incentiveId);
        assertEq(updatedIncentive.rewardPerLiquidity, rewardPerLiquidity);
        assertEq(updatedIncentive.lastRewardTime, lastRewardTime);
        assertEq(updatedIncentive.rewardRemaining, rewardRemaining);
    }

    function _calculateAccrueChange(uint256 incentiveId) public view returns (uint256 rewardPerLiquidity, uint256 rewardRemaining, uint256 lastRewardTime) {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        uint256 totalTime = incentive.endTime - incentive.lastRewardTime;
        if (totalTime == 0 || incentive.liquidityStaked == 0 || block.timestamp < incentive.lastRewardTime) {
            return (incentive.rewardPerLiquidity, incentive.rewardRemaining, incentive.lastRewardTime);
        }
        uint256 maxTime = block.timestamp < incentive.endTime ? block.timestamp : incentive.endTime;
        uint256 passedTime = maxTime - incentive.lastRewardTime;
        uint256 reward = uint256(passedTime) * incentive.rewardRemaining / totalTime;
        uint256 rplChange = reward * type(uint112).max / incentive.liquidityStaked;
        rewardPerLiquidity = incentive.rewardPerLiquidity + rplChange;
        rewardRemaining = incentive.rewardRemaining - reward;
        lastRewardTime = maxTime;
    }

    function _claimReward(uint256 incentiveId, address from) public returns(uint256 reward) {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        uint256 oldBalance = Token(incentive.rewardToken).balanceOf(from);
        (uint256 rewardPerLiquidityLast, uint256 rewardPerLiquidity, uint256 expectedReward) = _calculateReward(incentiveId, from);
        uint256[] memory incentiveIds = new uint256[](1);
        incentiveIds[0] = incentiveId;
        if (rewardPerLiquidityLast == 0) {
            vm.prank(from);
            vm.expectRevert(notSubscribed);
            stakingContract.claimRewards(incentiveIds);
            return 0;
        }
        vm.prank(from);
        uint256[] memory rewards = stakingContract.claimRewards(incentiveIds);
        reward = rewards[0];
        assertEq(reward, expectedReward);
        uint256 newBalance = Token(incentive.rewardToken).balanceOf(from);
        assertEq(newBalance - oldBalance, reward);
        StakingContractMainnet.Incentive memory updatedIncentive = _getIncentive(incentiveId);
        assertEq(updatedIncentive.rewardPerLiquidity, rewardPerLiquidity);
        assertEq(stakingContract.rewardPerLiquidityLast(from, incentiveId), rewardPerLiquidity);
    }

    function _calculateReward(uint256 incentiveId, address from) internal view returns (uint256 rplLast, uint256 newRewardPerLiquidity, uint256 reward) {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        (newRewardPerLiquidity,,) = _calculateAccrueChange(incentiveId);
        uint256 usersLiquidity = _getUsersLiquidityStaked(from, incentive.token);
        rplLast = stakingContract.rewardPerLiquidityLast(from, incentiveId);
        if (rplLast != 0) {
            reward = (newRewardPerLiquidity - rplLast) * usersLiquidity / type(uint112).max;
        }
    }

    function _getUsersLiquidityStaked(address user, address token) public view returns (uint112) {
        (uint112 liquidity,) = stakingContract.userStakes(user, token);
        return liquidity;
    }

    function _getUsersSubscribedIncentives(address user, address token) public view returns (uint144) {
        (, uint144 incentiveIds) = stakingContract.userStakes(user, token);
        return incentiveIds;
    }

    function _rewardRate(uint256 incentiveId) public view returns (uint256) {
        StakingContractMainnet.Incentive memory incentive = _getIncentive(incentiveId);
        if (incentive.endTime != incentive.lastRewardTime) {
            return incentive.rewardRemaining * uint256(type(uint112).max) / (incentive.endTime - incentive.lastRewardTime);
        } else {
            return 0;
        }
    }

    function _getIncentive(uint256 id) public view returns (StakingContractMainnet.Incentive memory incentive) {
        (
            address creator,
            address token,
            address rewardToken,
            uint32 endTime,
            uint256 rewardPerLiquidity,
            uint32 lastRewardTime,
            uint112 rewardRemaining,
            uint112 liquidityStaked
        ) = stakingContract.incentives(id);
        incentive = StakingContractMainnet.Incentive(
            creator,
            token,
            rewardToken,
            endTime,
            rewardPerLiquidity,
            lastRewardTime,
            rewardRemaining,
            liquidityStaked
        );
    }

    function assertEqInexact(uint256 value, uint256 shouldBe, uint256 err) public {
        assertTrue(value <= shouldBe + err);
        assertTrue(value >= shouldBe - err);
    }
}

contracts/Router/IMagicSwapV2Router.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "../UniswapV2/periphery/interfaces/IUniswapV2Router01.sol";
import "../Vault/INftVault.sol";

/// @title Router contract for swapping, managing liquidity and interacting with vaults
interface IMagicSwapV2Router is IUniswapV2Router01 {
    /// @dev Amounts does not match
    error MagicSwapV2WrongAmounts();
    error MagicSwapV2WrongAmountDeposited();
    error MagicSwapV2WrongAmountADeposited();
    error MagicSwapV2WrongAmountBDeposited();
    error MagicSwapV2InvalidPath();

    /// @notice Struct that specifies data for liquidity-related operations on vault.
    /// @param token address of NFT vault
    /// @param collection list of NFT addresses
    /// @param tokenId list of token IDs
    /// @param amount list of token amounts. For ERC721 amount is always 1.
    struct NftVaultLiquidityData {
        INftVault token;
        address[] collection;
        uint256[] tokenId;
        uint256[] amount;
    }

    /// @notice Emitted when NFT-ERC20 liquidity is added
    /// @param to address that receives LP tokens
    /// @param pair address of pair where NFTs are deposited
    /// @param vault vault data of deposited NFTs
    event NFTLiquidityAdded(address indexed to, address pair, NftVaultLiquidityData vault);

    /// @notice Emitted when NFT-NFT liquidity is added
    /// @param to address that receives LP tokens
    /// @param pair address of pair where NFTs are deposited
    /// @param vaultA vault data of deposited NFTs for first side
    /// @param vaultB vault data of deposited NFTs for second side
    event NFTNFTLiquidityAdded(address indexed to, address pair, NftVaultLiquidityData vaultA, NftVaultLiquidityData vaultB);

    /// @notice Emitted when NFT-ERC20 liquidity is removed
    /// @param to address that receives withdrawn assets
    /// @param pair address of pair where NFTs are withdrawn
    /// @param vault vault data of withdrawn NFTs
    event NFTLiquidityRemoved(address indexed to, address pair, NftVaultLiquidityData vault);

    /// @notice Emitted when NFT-NFT liquidity is removed
    /// @param to address that receives withdrawn assets
    /// @param pair address of pair where NFTs are withdrawn
    /// @param vaultA vault data of withdrawn NFTs for first side
    /// @param vaultB vault data of withdrawn NFTs for second side
    event NFTNFTLiquidityRemoved(address indexed to, address pair, NftVaultLiquidityData vaultA, NftVaultLiquidityData vaultB);

    /// @notice Deposit NFTs to vault
    /// @dev All NFTs must be approved for transfer. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to deposit
    /// @param _tokenId list of token IDs to deposit
    /// @param _amount list of token amounts to deposit. For ERC721 amount is always 1.
    /// @param _vault address of the vault where NFTs are deposited
    /// @param _to address that gets ERC20 for deposited NFTs
    /// @return amountMinted amount of ERC20 minted for deposited NFTs
    function depositVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _to
    ) external returns (uint256 amountMinted);

    /// @dev Withdraw NFTs from vault
    /// @dev Vault token must be approved for transfer. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to withdraw
    /// @param _tokenId list of token IDs to withdraw
    /// @param _amount list of token amounts to withdraw. For ERC721 amount is always 1.
    /// @param _vault address of the vault to withdraw NFTs from
    /// @param _to address that gets withdrawn NFTs
    /// @return amountBurned amount of ERC20 redeemed for NFTs
    function withdrawVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _to
    ) external returns (uint256 amountBurned);

    /// @notice Add liquidity to UniV2 pool using NFTs and second ERC20 token
    /// @dev All NFTs and ERC20 token must be approved for transfer. `_vault.collection`,
    ///      `_vault.tokenId` and `_vault.amount` must be of the same length.
    /// @param _vault vault data for NFTs to deposit as liquidity
    /// @param _tokenB address of token B
    /// @param _amountBDesired desired amount of token B to be added as liquidity
    /// @param _amountBMin minimum amount of token B to be added as liquidity
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @return amountA amount of token A added as liquidity
    /// @return amountB amount of token B added as liquidity
    /// @return lpAmount amount of LP token minted and sent to `_to`
    function addLiquidityNFT(
        NftVaultLiquidityData calldata _vault,
        address _tokenB,
        uint256 _amountBDesired,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 lpAmount);

    /// @notice Add liquidity to UniV2 pool using NFTs and ETH
    /// @dev All NFTs and ERC20 token must be approved for transfer. `_vault.collection`,
    ///      `_vault.tokenId` and `_vault.amount` must be of the same length.
    /// @param _vault vault data for NFTs to deposit as liquidity
    /// @param _amountETHMin desired amount of ETH to be added as liquidity
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @return amountToken amount of vault token added as liquidity
    /// @return amountETH amount of ETH added as liquidity
    /// @return lpAmount amount of LP token minted and sent to `_to`
    function addLiquidityNFTETH(
        NftVaultLiquidityData calldata _vault,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256 amountToken, uint256 amountETH, uint256 lpAmount);

    /// @notice Add liquidity to UniV2 pool using two NFT vaults
    /// @dev All NFTs must be approved for transfer. `_vaultA.collection`,
    ///      `_vaultA.tokenId` and `_vaultA.amount` must be of the same length.
    ///      `_vaultB.collection`, `_vaultB.tokenId` and `_vaultB.amount` must be of the same length.
    /// @param _vaultA vault data for NFTs to deposit as liquidity for first side
    /// @param _vaultB vault data for NFTs to deposit as liquidity for second side
    /// @param _amountAMin minimum amount of token A to be deposited
    /// @param _amountBMin minimum amount of token B to be deposited
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @return amountA amount of token A added as liquidity
    /// @return amountB amount of token B added as liquidity
    /// @return lpAmount amount of LP token minted and sent to `_to`
    function addLiquidityNFTNFT(
        NftVaultLiquidityData calldata _vaultA,
        NftVaultLiquidityData calldata _vaultB,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 lpAmount);

    /// @notice Remove liquidity from UniV2 pool and get NFTs and ERC20 token
    /// @dev Lp token must be approved for transfer. `_vault.collection`,
    ///      `_vault.tokenId` and `_vault.amount` must be of the same length.
    /// @param _vault vault data for NFTs to withdraw from liquidity
    /// @param _tokenB address of token B
    /// @param _lpAmount amount of LP token to redeem
    /// @param _amountAMin minimum amount of token A to be redeemed
    /// @param _amountBMin minimum amount of token B to be redeemed
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @param _swapLeftover if true, fraction of vault token will be swaped to Token B
    /// @return amountA amount of token A redeemed
    /// @return amountB amount of token B redeemed
    function removeLiquidityNFT(
        NftVaultLiquidityData calldata _vault,
        address _tokenB,
        uint256 _lpAmount,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline,
        bool _swapLeftover
    ) external returns (uint256 amountA, uint256 amountB);

    /// @notice Remove liquidity from UniV2 pool and get NFTs and ETH
    /// @dev Lp token must be approved for transfer. `_vault.collection`,
    ///      `_vault.tokenId` and `_vault.amount` must be of the same length.
    /// @param _vault vault data for NFTs to withdraw from liquidity
    /// @param _lpAmount amount of LP token to redeem
    /// @param _amountTokenMin minimum amount of vault token to be redeemed
    /// @param _amountETHMin minimum amount of ETH to be redeemed
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @param _swapLeftover if true, fraction of vault token will be swaped to ETH
    /// @return amountToken amount of vault token redeemed
    /// @return amountETH amount of ETH redeemed
    function removeLiquidityNFTETH(
        NftVaultLiquidityData calldata _vault,
        uint256 _lpAmount,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline,
        bool _swapLeftover
    ) external returns (uint256 amountToken, uint256 amountETH);

    /// @notice Remove liquidity from UniV2 pool and get NFTs
    /// @dev Lp token must be approved for transfer. `_vaultA.collection`,
    ///      `_vaultA.tokenId` and `_vaultA.amount` must be of the same length.
    ///      `_vaultB.collection`, `_vaultB.tokenId` and `_vaultB.amount` must be of the same length.
    /// @param _vaultA vault data for NFTs to withdraw from liquidity for first side
    /// @param _vaultB vault data for NFTs to withdraw from liquidity for second side
    /// @param _lpAmount amount of LP token to redeem
    /// @param _amountAMin minimum amount of token A to be redeemed
    /// @param _amountBMin minimum amount of token B to be redeemed
    /// @param _to address that gets LP tokens
    /// @param _deadline transaction deadline
    /// @return amountA amount of token A redeemed
    /// @return amountB amount of token B redeemed
    function removeLiquidityNFTNFT(
        NftVaultLiquidityData calldata _vaultA,
        NftVaultLiquidityData calldata _vaultB,
        uint256 _lpAmount,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 amountA, uint256 amountB);

    /// @notice Swap NFTs for ERC20
    /// @dev All NFTs must be approved for transfer. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to swap for token
    /// @param _tokenId list of token IDs to swap for token
    /// @param _amount list of token amounts to swap for token. For ERC721 amount is always 1.
    /// @param _amountOutMin minimum amount of output token expected after swap
    /// @param _path list of token addresses to swap over
    /// @param _to address that gets output token
    /// @param _deadline transaction deadline
    /// @return amounts input and output amounts of swaps
    function swapNftForTokens(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts);

    /// @notice Swap NFTs for ETH
    /// @dev All NFTs must be approved for transfer. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to swap for ETH
    /// @param _tokenId list of token IDs to swap for ETH
    /// @param _amount list of token amounts to swap for ETH. For ERC721 amount is always 1.
    /// @param _amountOutMin minimum amount of ETH expected after swap
    /// @param _path list of token addresses to swap over
    /// @param _to address that gets ETH
    /// @param _deadline transaction deadline
    /// @return amounts input and output amounts of swaps
    function swapNftForETH(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256[] memory amounts);

    /// @notice Swap ERC20 for NFTs
    /// @dev ERC20 must be approved for transfer. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to receive for tokens
    /// @param _tokenId list of token IDs to receive for tokens
    /// @param _amount list of token amounts to receive for tokens. For ERC721 amount is always 1.
    /// @param _amountInMax maximum acceptable amount of token to swap for NFTs
    /// @param _path list of token addresses to swap over
    /// @param _to address that gets NFTs
    /// @param _deadline transaction deadline
    /// @return amounts input and output amounts of swaps
    function swapTokensForNft(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountInMax,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts);

    /// @notice Swap ETH for NFTs
    /// @dev Does not require any approvals. `_collection`, `_tokenId`
    ///      and `_amount` must be of the same length.
    /// @param _collection list of NFT addresses to receive for ETH
    /// @param _tokenId list of token IDs to receive for ETH
    /// @param _amount list of token amounts to receive for ETH. For ERC721 amount is always 1.
    /// @param _path list of token addresses to swap over
    /// @param _to address that gets NFTs
    /// @param _deadline transaction deadline
    /// @return amounts input and output amounts of swaps
    function swapETHForNft(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256[] memory amounts);

    /// @notice Swap NFTs for NFTs
    /// @dev All input NFTs must be approved for transfer. It is most likely that input NFTs create a leftover
    ///      during the swap. That leftover is returend to the pool as LP rewards. `_collectionIn`, `_tokenIdIn` and `_amountIn`
    ///      as well as `_collectionOut`, `_tokenIdOut` and `_amountOut` must be of the same length.
    /// @param _collectionIn list of input NFT addresses
    /// @param _tokenIdIn list of input token IDs
    /// @param _amountIn list of input token amounts. For ERC721 amount is always 1.
    /// @param _collectionOut list of output NFT addresses
    /// @param _tokenIdOut list of output token IDs
    /// @param _amountOut list of output token amounts. For ERC721 amount is always 1.
    /// @param _path list of token addresses to swap over
    /// @param _to address that gets NFTs
    /// @param _deadline transaction deadline
    /// @return amounts input and output amounts of swaps
    function swapNftForNft(
        address[] memory _collectionIn,
        uint256[] memory _tokenIdIn,
        uint256[] memory _amountIn,
        address[] memory _collectionOut,
        uint256[] memory _tokenIdOut,
        uint256[] memory _amountOut,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts);

}

contracts/Router/MagicSwapV2Router.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/token/ERC1155/IERC1155.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC721/IERC721.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC20/utils/SafeERC20.sol";

import "./IMagicSwapV2Router.sol";
import "../UniswapV2/periphery/UniswapV2Router02.sol";
import "../UniswapV2/core/interfaces/IUniswapV2Pair.sol";

contract MagicSwapV2Router is IMagicSwapV2Router, UniswapV2Router02 {
    uint256 public constant ONE = 1e18;
    address public constant BURN_ADDRESS = address(0xdead);

    constructor(address _factory, address _WETH) UniswapV2Router02(_factory, _WETH) {}

    /// @inheritdoc IMagicSwapV2Router
    function depositVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _to
    ) external returns (uint256 amountMinted) {
        amountMinted = _depositVault(_collection, _tokenId, _amount, _vault, _to);
    }

    function _depositVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _to
    ) internal returns (uint256 amountMinted) {
        for (uint256 i = 0; i < _collection.length; i++) {
            INftVault.CollectionData memory collectionData = _vault.getAllowedCollectionData(_collection[i]);
            if (collectionData.nftType == INftVault.NftType.ERC721) {
                IERC721(_collection[i]).safeTransferFrom(msg.sender, address(_vault), _tokenId[i]);
            } else if (collectionData.nftType == INftVault.NftType.ERC1155) {
                IERC1155(_collection[i]).safeTransferFrom(
                    msg.sender, address(_vault), _tokenId[i], _amount[i], bytes("")
                );
            } else {
                revert INftVault.UnsupportedNft();
            }
        }

        amountMinted = _vault.depositBatch(_to, _collection, _tokenId, _amount);
    }

    /// @inheritdoc IMagicSwapV2Router
    function withdrawVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _to
    ) external returns (uint256 amountBurned) {
        amountBurned = _withdrawVault(_collection, _tokenId, _amount, _vault, msg.sender, _to);
    }

    function _withdrawVault(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        INftVault _vault,
        address _from,
        address _to
    ) internal returns (uint256 amountBurned) {
        IERC20 vaultToken = IERC20(address(_vault));
        uint256 amountToBurn = nftAmountToERC20(_amount);
        uint256 fromBalance = vaultToken.balanceOf(_from);
        uint256 totalSupply = vaultToken.totalSupply();

        /// @dev if user withdraws all NFT tokens but does not have totalSupply of ERC20 tokens (some are locked
        ///      in UniV2 pool), we optimistically assume that user has enough and adjust `amountToBurn`
        ///      to user balance. If user balance does not meet required minimum then Vault will revert anyway.
        if (amountToBurn == totalSupply && fromBalance < totalSupply) {
            amountToBurn = fromBalance;
        }

        if (_from == address(this)) _approveIfNeeded(address(_vault), amountToBurn);

        vaultToken.transferFrom(_from, address(_vault), amountToBurn);
        amountBurned = _vault.withdrawBatch(_to, _collection, _tokenId, _amount);

        if (amountToBurn != amountBurned) revert MagicSwapV2WrongAmounts();
    }

    /// @inheritdoc IMagicSwapV2Router
    function addLiquidityNFT(
        NftVaultLiquidityData calldata _vault,
        address _tokenB,
        uint256 _amountBDesired,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256 amountA, uint256 amountB, uint256 lpAmount) {
        uint256 amountAMinted = _depositVault(_vault.collection, _vault.tokenId, _vault.amount, _vault.token, address(this));

        (amountA, amountB) =
            _addLiquidity(address(_vault.token), _tokenB, amountAMinted, _amountBDesired, amountAMinted, _amountBMin);

        if(amountAMinted != amountA) revert MagicSwapV2WrongAmountDeposited();

        address pair = UniswapV2Library.pairFor(factory, address(_vault.token), _tokenB);
        TransferHelper.safeTransfer(address(_vault.token), pair, amountA);
        TransferHelper.safeTransferFrom(_tokenB, msg.sender, pair, amountB);
        lpAmount = IUniswapV2Pair(pair).mint(_to);

        emit NFTLiquidityAdded(_to, pair, _vault);
    }

    /// @inheritdoc IMagicSwapV2Router
    function addLiquidityNFTETH(
        NftVaultLiquidityData calldata _vault,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256 amountToken, uint256 amountETH, uint256 lpAmount) {
        uint256 amountMinted = _depositVault(_vault.collection, _vault.tokenId, _vault.amount, _vault.token, address(this));

        _approveIfNeeded(address(_vault.token), amountMinted);

        (amountToken, amountETH, lpAmount) =
            _addLiquidityETH(address(_vault.token), amountMinted, amountMinted, _amountETHMin, address(this), _to, _deadline);

        address pair = UniswapV2Library.pairFor(factory, address(_vault.token), WETH);

        emit NFTLiquidityAdded(_to, pair, _vault);
    }

    /// @inheritdoc IMagicSwapV2Router
    function addLiquidityNFTNFT(
        NftVaultLiquidityData calldata _vaultA,
        NftVaultLiquidityData calldata _vaultB,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external ensure(_deadline) returns (uint256 amountA, uint256 amountB, uint256 lpAmount) {
        uint256 amountAMinted = _depositVault(_vaultA.collection, _vaultA.tokenId, _vaultA.amount, _vaultA.token, address(this));
        uint256 amountBMinted = _depositVault(_vaultB.collection, _vaultB.tokenId, _vaultB.amount, _vaultB.token, address(this));

        (amountA, amountB) =
            _addLiquidity(address(_vaultA.token), address(_vaultB.token), amountAMinted, amountBMinted, _amountAMin, _amountBMin);

        if (amountAMinted != amountA) {
            if (amountAMinted < amountA) {
                revert MagicSwapV2WrongAmountADeposited();
            }

            TransferHelper.safeTransfer(address(_vaultA.token), BURN_ADDRESS, amountAMinted - amountA);
        }

        if (amountBMinted != amountB) {
            if (amountBMinted < amountB) {
                revert MagicSwapV2WrongAmountBDeposited();
            }

            TransferHelper.safeTransfer(address(_vaultB.token), BURN_ADDRESS, amountBMinted - amountB);
        }

        address pair = UniswapV2Library.pairFor(factory, address(_vaultA.token), address(_vaultB.token));
        TransferHelper.safeTransfer(address(_vaultA.token), pair, amountA);
        TransferHelper.safeTransfer(address(_vaultB.token), pair, amountB);
        lpAmount = IUniswapV2Pair(pair).mint(_to);

        emit NFTNFTLiquidityAdded(_to, pair, _vaultA, _vaultB);
    }

    /// @inheritdoc IMagicSwapV2Router
    function removeLiquidityNFT(
        NftVaultLiquidityData calldata _vault,
        address _tokenB,
        uint256 _lpAmount,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline,
        bool _swapLeftover
    ) external returns (uint256 amountA, uint256 amountB) {
        (amountA, amountB) =
            removeLiquidity(address(_vault.token), _tokenB, _lpAmount, _amountAMin, _amountBMin, address(this), _deadline);

        // withdraw NFTs and send to user
        uint256 amountBurned = _withdrawVault(_vault.collection, _vault.tokenId, _vault.amount, _vault.token, address(this), _to);

        amountA -= amountBurned;

        if (_swapLeftover) {
            uint256 amountOut = swapLeftover(address(_vault.token), _tokenB, amountA);

            amountA = 0;
            amountB += amountOut;
        } else if (amountA > 0) {
            TransferHelper.safeTransfer(address(_vault.token), _to, amountA);
        }

        TransferHelper.safeTransfer(_tokenB, _to, amountB);

        address pair = UniswapV2Library.pairFor(factory, address(_vault.token), _tokenB);

        emit NFTLiquidityRemoved(_to, pair, _vault);
    }

    /// @inheritdoc IMagicSwapV2Router
    function removeLiquidityNFTETH(
        NftVaultLiquidityData calldata _vault,
        uint256 _lpAmount,
        uint256 _amountTokenMin,
        uint256 _amountETHMin,
        address _to,
        uint256 _deadline,
        bool _swapLeftover
    ) external returns (uint256 amountToken, uint256 amountETH) {
        (amountToken, amountETH) =
            removeLiquidity(address(_vault.token), WETH, _lpAmount, _amountTokenMin, _amountETHMin, address(this), _deadline);

        // withdraw NFTs and send to user
        uint256 amountBurned = _withdrawVault(_vault.collection, _vault.tokenId, _vault.amount, _vault.token, address(this), _to);

        amountToken -= amountBurned;

        if (_swapLeftover) {
            uint256 amountOut = swapLeftover(address(_vault.token), WETH, amountToken);

            amountToken = 0;
            amountETH += amountOut;
        } else if (amountToken > 0) {
            TransferHelper.safeTransfer(address(_vault.token), _to, amountToken);
        }

        IWETH(WETH).withdraw(amountETH);
        TransferHelper.safeTransferETH(_to, amountETH);

        address pair = UniswapV2Library.pairFor(factory, address(_vault.token), WETH);

        emit NFTLiquidityRemoved(_to, pair, _vault);
    }

    /// @inheritdoc IMagicSwapV2Router
    function removeLiquidityNFTNFT(
        NftVaultLiquidityData calldata _vaultA,
        NftVaultLiquidityData calldata _vaultB,
        uint256 _lpAmount,
        uint256 _amountAMin,
        uint256 _amountBMin,
        address _to,
        uint256 _deadline
    ) external returns (uint256 amountA, uint256 amountB) {
        (amountA, amountB) =
            removeLiquidity(address(_vaultA.token), address(_vaultB.token), _lpAmount, _amountAMin, _amountBMin, address(this), _deadline);

        // withdraw NFTs and send to user
        uint256 amountBurnedA = _withdrawVault(_vaultA.collection, _vaultA.tokenId, _vaultA.amount, _vaultA.token, address(this), _to);
        uint256 amountBurnedB = _withdrawVault(_vaultB.collection, _vaultB.tokenId, _vaultB.amount, _vaultB.token, address(this), _to);

        amountA -= amountBurnedA;
        amountB -= amountBurnedB;

        if (amountA > 0) {
            TransferHelper.safeTransfer(address(_vaultA.token), BURN_ADDRESS, amountA);
        }

        if (amountB > 0) {
            TransferHelper.safeTransfer(address(_vaultB.token), BURN_ADDRESS, amountB);
        }

        address pair = UniswapV2Library.pairFor(factory, address(_vaultA.token), address(_vaultB.token));

        emit NFTNFTLiquidityRemoved(_to, pair, _vaultA, _vaultB);
    }

    /// @inheritdoc IMagicSwapV2Router
    function swapNftForTokens(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts) {
        uint256 amountIn = _depositVault(_collection, _tokenId, _amount, INftVault(_path[0]), address(this));

        _approveIfNeeded(_path[0], amountIn);

        amounts = _swapExactTokensForTokens(amountIn, _amountOutMin, _path, address(this), _to, _deadline);
    }

    /// @inheritdoc IMagicSwapV2Router
    function swapNftForETH(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountOutMin,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256[] memory amounts) {
        if(_path[_path.length - 1] != WETH) revert MagicSwapV2InvalidPath();

        uint256 amountIn = _depositVault(_collection, _tokenId, _amount, INftVault(_path[0]), address(this));

        _approveIfNeeded(_path[0], amountIn);

        amounts = _swapExactTokensForTokens(amountIn, _amountOutMin, _path, address(this), address(this), _deadline);

        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        TransferHelper.safeTransferETH(_to, amounts[amounts.length - 1]);
    }

    /// @inheritdoc IMagicSwapV2Router
    function swapTokensForNft(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        uint256 _amountInMax,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts) {
        uint256 amountOut = nftAmountToERC20(_amount);

        amounts = _swapTokensForExactTokens(amountOut, _amountInMax, _path, msg.sender, address(this), _deadline);

        // withdraw NFTs and send to user
        _withdrawVault(_collection, _tokenId, _amount, INftVault(_path[_path.length - 1]), address(this), _to);
    }

    /// @inheritdoc IMagicSwapV2Router
    function swapETHForNft(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external payable returns (uint256[] memory amounts) {
        uint256 amountOut = nftAmountToERC20(_amount);

        amounts = swapETHForExactTokens(amountOut, _path, address(this), _deadline);

        // withdraw NFTs and send to user
        _withdrawVault(_collection, _tokenId, _amount, INftVault(_path[_path.length - 1]), address(this), _to);
    }

    /// @inheritdoc IMagicSwapV2Router
    function swapNftForNft(
        address[] memory _collectionIn,
        uint256[] memory _tokenIdIn,
        uint256[] memory _amountIn,
        address[] memory _collectionOut,
        uint256[] memory _tokenIdOut,
        uint256[] memory _amountOut,
        address[] calldata _path,
        address _to,
        uint256 _deadline
    ) external returns (uint256[] memory amounts) {
        uint256 amountIn = _depositVault(_collectionIn, _tokenIdIn, _amountIn, INftVault(_path[0]), address(this));
        address vaultOut = _path[_path.length - 1];
        uint256 amountOutMin = nftAmountToERC20(_amountOut);

        _approveIfNeeded(_path[0], amountIn);

        amounts = _swapExactTokensForTokens(amountIn, amountOutMin, _path, address(this), address(this), _deadline);

        // withdraw NFTs and send to user
        _withdrawVault(_collectionOut, _tokenIdOut, _amountOut, INftVault(vaultOut), address(this), _to);

        uint256 dust = amounts[amounts.length - 1] - amountOutMin;

        // send leftover of input token back to the pool and sync
        if (dust > 0) {
            // refund user unused token
            address pair = UniswapV2Library.pairFor(factory, _path[_path.length - 2], vaultOut);
            TransferHelper.safeTransfer(vaultOut, pair, dust);
            IUniswapV2Pair(pair).sync();
        }
    }

    function swapLeftover(address _tokenA, address _tokenB, uint256 _amountIn) internal returns (uint256 amountOut) {
        if (_amountIn == 0) return 0;

        address[] memory path = new address[](2);
        path[0] = _tokenA;
        path[1] = _tokenB;

        _approveIfNeeded(_tokenA, _amountIn);

        // swap leftover to tokenB
        // TODO: can be front-run, issue?
        uint256[] memory amounts =
            _swapExactTokensForTokens(_amountIn, 1, path, address(this), address(this), block.timestamp);

        return amounts[1];
    }

    function nftAmountToERC20(uint256[] memory _list) internal pure returns (uint256 amount) {
        for (uint256 i = 0; i < _list.length; i++) {
            amount += _list[i];
        }

        amount *= ONE;
    }

    function _approveIfNeeded(address _token, uint256 _amount) internal {
        if (IERC20(_token).allowance(address(this), address(this)) < _amount) {
            SafeERC20.safeApprove(IERC20(_token), address(this), 0);
            SafeERC20.safeApprove(IERC20(_token), address(this), type(uint256).max);
        }
    }
}

contracts/Router/test/MagicSwapV2Router..content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";

import "lib/ERC721Mintable.sol";
import "lib/ERC1155Mintable.sol";

import "./mock/WETH.sol";
import "../MagicSwapV2Router.sol";
import "../../UniswapV2/core/UniswapV2Factory.sol";
import "../../Vault/NftVaultFactory.sol";

contract MagicSwapV2RouterTest is Test {
    WETH weth;
    UniswapV2Factory factory;
    MagicSwapV2Router magicSwapV2Router;
    NftVaultFactory nftVaultFactory;
    ERC721Mintable nft1;
    ERC1155Mintable nft2;

    uint256 ONE;

    address user1 = address(10000001);
    address user2 = address(10000002);
    address user3 = address(10000003);
    address user4 = address(10000004);

    address protocolFeeBeneficiary = address(10000005);

    INftVault.CollectionData public collectionERC721all;
    INftVault.CollectionData public collectionERC1155all;
    INftVault.CollectionData[] public collections1;
    INftVault.CollectionData[] public collections2;

    INftVault vault1;
    INftVault vault2;

    address[] public collectionArray;
    uint256[] public tokenIdArray;
    uint256[] public amountArray;

    address[] public collectionArray1;
    uint256[] public tokenIdArray1;
    uint256[] public amountArray1;

    function setUp() public {
        weth = new WETH();

        factory = new UniswapV2Factory(0, 30, protocolFeeBeneficiary);

        magicSwapV2Router = new MagicSwapV2Router(address(factory), address(weth));

        nftVaultFactory = new NftVaultFactory();

        collectionERC721all = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collections1.push(collectionERC721all);
        vault1 = nftVaultFactory.createVault(collections1);
        nft1 = ERC721Mintable(collectionERC721all.addr);

        collectionERC1155all = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collections2.push(collectionERC721all);
        collections2.push(collectionERC1155all);
        vault2 = nftVaultFactory.createVault(collections2);
        nft2 = ERC1155Mintable(collectionERC1155all.addr);

        ONE = vault1.ONE();
    }

    function _dealWeth(address _user, uint256 _amount) public {
        vm.deal(_user, _amount);
        vm.prank(_user);
        weth.deposit{value: _amount}();
        vm.prank(_user);
        weth.approve(address(magicSwapV2Router), _amount);
    }

    function _copyStorage()
        public
        view
        returns (address[] memory _collection, uint256[] memory _tokenId, uint256[] memory _amount)
    {
        _collection = collectionArray;
        _tokenId = tokenIdArray;
        _amount = amountArray;
    }

    function _mintTokens(address _user)
        public
        returns (address[] memory _collection, uint256[] memory _tokenId, uint256[] memory _amount)
    {
        (_collection, _tokenId, _amount) = _copyStorage();

        for (uint256 i = 0; i < _collection.length; i++) {
            if (_collection[i] == address(nft1)) {
                nft1.mint(_user, _tokenId[i]);
                vm.prank(_user);
                nft1.setApprovalForAll(address(magicSwapV2Router), true);
            } else {
                nft2.mint(_user, _tokenId[i], _amount[i]);
                vm.prank(_user);
                nft2.setApprovalForAll(address(magicSwapV2Router), true);
            }
        }
    }

    function _checkNftBalances(
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount,
        address _owner
    ) public {
        for (uint256 i = 0; i < _collection.length; i++) {
            if (_collection[i] == address(nft1)) {
                assertEq(nft1.ownerOf(_tokenId[i]), _owner);
            } else {
                assertEq(nft2.balanceOf(_owner, _tokenId[i]), _amount[i]);
            }
        }
    }

    function _checkERC20Balances(
        address[] memory _collection,
        uint256[] memory,
        uint256[] memory _amount,
        address _vault,
        address _owner,
        uint256 _prevBalance
    ) public {
        uint256 totalAmount;

        for (uint256 i = 0; i < _collection.length; i++) {
            totalAmount += _amount[i];
        }

        assertEq(IERC20(_vault).balanceOf(_owner), totalAmount * ONE + _prevBalance);
    }

    function nftAmountToERC20(uint256[] memory _list) internal view returns (uint256 amount) {
        for (uint256 i = 0; i < _list.length; i++) {
            amount += _list[i];
        }

        amount *= ONE;
    }

    function testDepositWithdrawVault(uint256 _tokenId, uint256 _amount) public {
        vm.assume(_tokenId < type(uint256).max - 50);
        vm.assume(_amount > 0);
        vm.assume(_amount < type(uint64).max);

        // deposit 1
        collectionArray = [address(nft1), address(nft1), address(nft1), address(nft1)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        amountArray = [uint256(1), 1, 1, 1];

        (address[] memory _collection1, uint256[] memory _tokenId1, uint256[] memory _amount1) = _mintTokens(user1);

        vm.prank(user1);
        uint256 amountMinted1 = magicSwapV2Router.depositVault(_collection1, _tokenId1, _amount1, vault1, user1);

        assertEq(amountMinted1, nftAmountToERC20(_amount1));

        _checkNftBalances(_collection1, _tokenId1, _amount1, address(vault1));
        _checkERC20Balances(_collection1, _tokenId1, _amount1, address(vault1), user1, 0);

        // deposit 2
        collectionArray = [address(nft1), address(nft1), address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        amountArray = [uint256(1), 1, _amount++, _amount++];

        (address[] memory _collection2, uint256[] memory _tokenId2, uint256[] memory _amount2) = _mintTokens(user2);

        vm.prank(user2);
        uint256 amountMinted2 = magicSwapV2Router.depositVault(_collection2, _tokenId2, _amount2, vault2, user2);

        assertEq(amountMinted2, nftAmountToERC20(_amount2));

        _checkNftBalances(_collection2, _tokenId2, _amount2, address(vault2));
        _checkERC20Balances(_collection2, _tokenId2, _amount2, address(vault2), user2, 0);

        // withdraw 1
        vm.startPrank(user1);
        IERC20(address(vault1)).approve(address(magicSwapV2Router), nftAmountToERC20(_amount1));

        uint256 amountBurned1 = magicSwapV2Router.withdrawVault(_collection1, _tokenId1, _amount1, vault1, user3);
        vm.stopPrank();

        assertEq(amountBurned1, amountMinted1);
        assertEq(IERC20(address(vault1)).balanceOf(user1), 0);
        assertEq(IERC20(address(vault1)).balanceOf(address(vault1)), 0);

        _checkNftBalances(_collection1, _tokenId1, _amount1, user3);

        // withdraw 2

        collectionArray = [address(nft1), address(nft1), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        (address[] memory _collection3,,) = _mintTokens(user2);

        vm.startPrank(user2);
        IERC20(address(vault2)).approve(address(magicSwapV2Router), nftAmountToERC20(_amount2));

        vm.expectRevert(IMagicSwapV2Router.MagicSwapV2WrongAmounts.selector);
        magicSwapV2Router.withdrawVault(_collection3, _tokenId2, _amount2, vault2, user4);

        uint256 leftover = vault2.ONE() - vault2.LAST_NFT_AMOUNT();
        IERC20(address(vault2)).transfer(user4, leftover + 1);

        vm.expectRevert("ERC20: burn amount exceeds balance");
        magicSwapV2Router.withdrawVault(_collection2, _tokenId2, _amount2, vault2, user4);
        vm.stopPrank();

        vm.prank(user4);
        IERC20(address(vault2)).transfer(user2, 1);

        vm.prank(user2);
        uint256 amountBurned2 = magicSwapV2Router.withdrawVault(_collection2, _tokenId2, _amount2, vault2, user4);

        assertEq(amountBurned2 + leftover, amountMinted2);
        assertEq(IERC20(address(vault2)).balanceOf(user2), 0);
        assertEq(IERC20(address(vault2)).balanceOf(address(vault2)), 0);

        _checkNftBalances(_collection2, _tokenId2, _amount2, user4);
    }

    function testAddLiquidityNFT(uint256 _tokenId, uint256 _amount) public {
        vm.assume(_tokenId < type(uint256).max - 100);
        vm.assume(_amount > 0);
        // UniswapV2Pair balance is using uint112
        // and amount of NFTs is multiplied by 1e18 when transformed to ERC20
        // and we are depositing multple NFTs
        // so trying to avoid overflow revert
        vm.assume(_amount < type(uint112).max / ONE / 10);

        // user1 liquidity deposit
        collectionArray = [address(nft1), address(nft1), address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        amountArray = [uint256(1), 1, _amount++, _amount++];

        (address[] memory _collection1, uint256[] memory _tokenId1, uint256[] memory _amount1) = _mintTokens(user1);

        uint256 amountBDesired1 = 10e18;
        uint256 amountBMin1 = 9.5e18;

        _dealWeth(user1, amountBDesired1);

        vm.prank(user1);
        (uint256 amountA1, uint256 amountB1, uint256 lpAmount1) = magicSwapV2Router.addLiquidityNFT(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection1,
                tokenId: _tokenId1,
                amount: _amount1
            }),
            address(weth),
            amountBDesired1,
            amountBMin1,
            user1,
            block.timestamp
        );

        assertEq(amountA1, nftAmountToERC20(_amount1));
        assertEq(amountB1, amountBDesired1);

        address pair = UniswapV2Library.pairFor(address(factory), address(vault2), address(weth));
        assertEq(weth.balanceOf(pair), amountB1);

        _checkNftBalances(_collection1, _tokenId1, _amount1, address(vault2));
        _checkERC20Balances(_collection1, _tokenId1, _amount1, address(vault2), pair, 0);

        assertEq(IERC20(pair).balanceOf(user1), lpAmount1);

        // user2 liquidity deposit
        collectionArray = [address(nft1), address(nft2), address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        // I know, but it's easier to follow values this way
        amountArray = amountArray;

        (address[] memory _collection2, uint256[] memory _tokenId2, uint256[] memory _amount2) = _mintTokens(user2);

        uint256 amountBDesired2 = 10.1e18;
        uint256 amountBMin2 = amountBDesired2;

        _dealWeth(user2, amountBDesired2);

        vm.prank(user2);
        vm.expectRevert(IUniswapV2Router01.UniswapV2RouterInsufficientBAmount.selector);
        magicSwapV2Router.addLiquidityNFT(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection2,
                tokenId: _tokenId2,
                amount: _amount2
            }),
            address(weth),
            amountBDesired2,
            amountBMin2,
            user2,
            block.timestamp
        );

        uint256 prevBalance = IERC20(address(vault2)).balanceOf(pair);

        amountBMin2 = 10e18;

        vm.prank(user2);
        (uint256 amountA2, uint256 amountB2, uint256 lpAmount2) = magicSwapV2Router.addLiquidityNFT(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection2,
                tokenId: _tokenId2,
                amount: _amount2
            }),
            address(weth),
            amountBDesired2,
            amountBMin2,
            user2,
            block.timestamp
        );

        assertEq(amountA2, nftAmountToERC20(_amount2));
        assertEq(amountB2, amountBMin2);

        assertEq(weth.balanceOf(pair), amountB1 + amountBMin2);

        _checkNftBalances(_collection2, _tokenId2, _amount2, address(vault2));
        _checkERC20Balances(_collection2, _tokenId2, _amount2, address(vault2), pair, prevBalance);

        assertEq(IERC20(pair).balanceOf(user2), lpAmount2);
    }

    function testAddLiquidityNFTETH(uint256 _tokenId, uint256 _amount) public {
        console2.logBytes32(keccak256(type(UniswapV2Pair).creationCode));
        vm.assume(_tokenId < type(uint256).max - 100);
        vm.assume(_amount > 0);
        vm.assume(_amount < type(uint112).max / ONE / 10);

        // user1 liquidity deposit
        collectionArray = [address(nft1), address(nft1), address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        amountArray = [uint256(1), 1, _amount++, _amount++];

        (address[] memory _collection1, uint256[] memory _tokenId1, uint256[] memory _amount1) = _mintTokens(user1);

        uint256 amountBDesired1 = 10e18;
        uint256 amountBMin1 = 9.5e18;

        vm.deal(user1, amountBDesired1);

        vm.prank(user1);
        (uint256 amountA1, uint256 amountB1, uint256 lpAmount1) = magicSwapV2Router.addLiquidityNFTETH{
            value: amountBDesired1
        }(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection1,
                tokenId: _tokenId1,
                amount: _amount1
            }),
            amountBMin1,
            user1,
            block.timestamp
        );

        assertEq(amountA1, nftAmountToERC20(_amount1));
        assertEq(amountB1, amountBDesired1);

        address pair = UniswapV2Library.pairFor(address(factory), address(vault2), address(weth));
        assertEq(weth.balanceOf(pair), amountB1);

        _checkNftBalances(_collection1, _tokenId1, _amount1, address(vault2));
        _checkERC20Balances(_collection1, _tokenId1, _amount1, address(vault2), pair, 0);

        assertEq(IERC20(pair).balanceOf(user1), lpAmount1);

        // user2 liquidity deposit
        collectionArray = [address(nft1), address(nft2), address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++];
        // I know, but it's easier to follow values this way
        amountArray = amountArray;

        (address[] memory _collection2, uint256[] memory _tokenId2, uint256[] memory _amount2) = _mintTokens(user2);

        uint256 amountBDesired2 = 10.1e18;
        uint256 amountBMin2 = amountBDesired2;

        vm.deal(user2, amountBDesired2);

        vm.prank(user2);
        vm.expectRevert(IUniswapV2Router01.UniswapV2RouterInsufficientBAmount.selector);
        magicSwapV2Router.addLiquidityNFTETH{value: amountBDesired2}(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection2,
                tokenId: _tokenId2,
                amount: _amount2
            }),
            amountBMin2,
            user2,
            block.timestamp
        );

        uint256 prevBalance = IERC20(address(vault2)).balanceOf(pair);

        amountBMin2 = 10e18;

        vm.prank(user2);
        (uint256 amountA2, uint256 amountB2, uint256 lpAmount2) = magicSwapV2Router.addLiquidityNFTETH{
            value: amountBDesired2
        }(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collection2,
                tokenId: _tokenId2,
                amount: _amount2
            }),
            amountBMin2,
            user2,
            block.timestamp
        );

        assertEq(amountA2, nftAmountToERC20(_amount2));
        assertEq(amountB2, amountBMin2);

        assertEq(weth.balanceOf(pair), amountB1 + amountBMin2);

        _checkNftBalances(_collection2, _tokenId2, _amount2, address(vault2));
        _checkERC20Balances(_collection2, _tokenId2, _amount2, address(vault2), pair, prevBalance);

        assertEq(IERC20(pair).balanceOf(user2), lpAmount2);
        assertEq(address(magicSwapV2Router).balance, 0);
    }

    function testAddLiquidityNFTNFT(uint256 _tokenId) public {
        vm.assume(_tokenId < type(uint256).max - 100);

        // user1 liquidity deposit
        collectionArray = [address(nft1), address(nft1), address(nft1), address(nft1), address(nft1)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++, _tokenId++, _tokenId++];
        amountArray = [1, 1, 1, 1, 1];

        (address[] memory _collectionA1, uint256[] memory _tokenIdA1, uint256[] memory _amountA1) = _mintTokens(user1);

        collectionArray = [address(nft2), address(nft2)];
        tokenIdArray = [_tokenId++, _tokenId++];
        amountArray = [5, 5];

        (address[] memory _collectionB1, uint256[] memory _tokenIdB1, uint256[] memory _amountB1) = _mintTokens(user1);

        vm.prank(user1);

        address pair;
        uint256 amountA1;
        uint256 amountB1;
        {
            (uint256 __amountA1, uint256 __amountB1, uint256 lpAmount1) = magicSwapV2Router.addLiquidityNFTNFT(
                IMagicSwapV2Router.NftVaultLiquidityData({
                    token: vault1,
                    collection: _collectionA1,
                    tokenId: _tokenIdA1,
                    amount: _amountA1
                }),
                IMagicSwapV2Router.NftVaultLiquidityData({
                    token: vault2,
                    collection: _collectionB1,
                    tokenId: _tokenIdB1,
                    amount: _amountB1
                }),
                nftAmountToERC20(_amountA1),
                nftAmountToERC20(_amountB1),
                user1,
                block.timestamp
            );

            amountA1 = __amountA1;
            amountB1 = __amountB1;

            // All amounts were used
            assertEq(amountA1, nftAmountToERC20(_amountA1));
            assertEq(amountB1, nftAmountToERC20(_amountB1));

            // All vault tokens were transferred to the pair
            pair = UniswapV2Library.pairFor(address(factory), address(vault1), address(vault2));
            assertEq(IERC20(address(vault1)).balanceOf(pair), amountA1);
            assertEq(IERC20(address(vault2)).balanceOf(pair), amountB1);

            // All NFTs were transferred to the vault
            _checkNftBalances(_collectionA1, _tokenIdA1, _amountA1, address(vault1));
            _checkNftBalances(_collectionB1, _tokenIdB1, _amountB1, address(vault2));

            // LP tokens were transferred to the user
            assertEq(IERC20(pair).balanceOf(user1), lpAmount1);
        }

        // user2 liquidity deposit
        collectionArray = [address(nft1), address(nft1), address(nft1)];
        tokenIdArray = [_tokenId++, _tokenId++, _tokenId++];
        amountArray = [1, 1, 1];

        (address[] memory _collectionA2, uint256[] memory _tokenIdA2, uint256[] memory _amountA2) = _mintTokens(user2);

        collectionArray = [address(nft2)];
        tokenIdArray = [_tokenId++];
        amountArray = [5];

        (address[] memory _collectionB2, uint256[] memory _tokenIdB2, uint256[] memory _amountB2) = _mintTokens(user2);

        vm.prank(user2);
        (uint256 amountA2, uint256 amountB2, uint256 lpAmount2) = magicSwapV2Router.addLiquidityNFTNFT(
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault1,
                collection: _collectionA2,
                tokenId: _tokenIdA2,
                amount: _amountA2
            }),
            IMagicSwapV2Router.NftVaultLiquidityData({
                token: vault2,
                collection: _collectionB2,
                tokenId: _tokenIdB2,
                amount: _amountB2
            }),
            nftAmountToERC20(_amountA2) - ONE,
            nftAmountToERC20(_amountB2),
            user2,
            block.timestamp
        );

        // Optimal amounts where used
        assertEq(amountA2, 2.5 * 10 ** 18);
        assertEq(amountB2, nftAmountToERC20(_amountB2));

        // Optimal vault tokens were transferred to the pair
        assertEq(IERC20(address(vault1)).balanceOf(pair), amountA1 + amountA2);
        assertEq(IERC20(address(vault2)).balanceOf(pair), amountB1 + amountB2);

        // Leftover vault tokens were transferred to the burn address
        assertEq(IERC20(address(vault1)).balanceOf(address(0xdead)), nftAmountToERC20(_amountA2) - amountA2);

        // All NFTs were transferred to the vault
        _checkNftBalances(_collectionA2, _tokenIdA2, _amountA2, address(vault1));
        _checkNftBalances(_collectionB2, _tokenIdB2, _amountB2, address(vault2));

        // LP tokens were transferred to the user
        assertEq(IERC20(pair).balanceOf(user2), lpAmount2);
    }

