// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/access/Ownable2Step.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";

import "../../interfaces/IUniswapV2Pair.sol";
import "../../interfaces/IUniswapV2Callee.sol";
import "../../interfaces/IUniswapV2Factory.sol";

import "../../libraries/UniswapV2Math.sol";
import "../../libraries/UQ112x112.sol";
import "../../libraries/Oracle.sol";

import "../../UniswapV2ERC20.sol";

/// @dev Minimally adjusted vanilla UniswapV2Pair implementation for testing
/// @dev CHANGELOG:
/// - remove body of _mintFee function
/// - refactor uint112(-1) to type(uint112).max
contract UniswapV2PairOriginal is UniswapV2ERC20 {
    using SafeMath for uint256;
    using UQ112x112 for uint224;

    uint256 public constant MINIMUM_LIQUIDITY = 10 ** 3;
    bytes4 private constant SELECTOR = bytes4(keccak256(bytes("transfer(address,uint256)")));

    address public factory;
    address public token0;
    address public token1;

    uint112 private reserve0; // uses single storage slot, accessible via getReserves
    uint112 private reserve1; // uses single storage slot, accessible via getReserves
    uint32 private blockTimestampLast; // uses single storage slot, accessible via getReserves

    uint256 public price0CumulativeLast;
    uint256 public price1CumulativeLast;
    uint256 public kLast; // reserve0 * reserve1, as of immediately after the most recent liquidity event

    uint256 private unlocked = 1;

    modifier lock() {
        require(unlocked == 1, "UniswapV2: LOCKED");
        unlocked = 0;
        _;
        unlocked = 1;
    }

    function getReserves() public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) {
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    }

    function _safeTransfer(address token, address to, uint256 value) private {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(SELECTOR, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "UniswapV2: TRANSFER_FAILED");
    }

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(address indexed sender, uint256 amount0, uint256 amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    constructor() {
        factory = msg.sender;
    }

    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1) external {
        require(msg.sender == factory, "UniswapV2: FORBIDDEN"); // sufficient check
        token0 = _token0;
        token1 = _token1;
    }

    // update reserves and, on the first call per block, price accumulators
    function _update(uint256 balance0, uint256 balance1, uint112 _reserve0, uint112 _reserve1) private {
        require(balance0 <= type(uint112).max && balance1 <= type(uint112).max, "UniswapV2: OVERFLOW");
        uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
        uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
        if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
            // * never overflows, and + overflow is desired
            price0CumulativeLast += uint256(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) * timeElapsed;
            price1CumulativeLast += uint256(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) * timeElapsed;
        }
        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        blockTimestampLast = blockTimestamp;
        emit Sync(reserve0, reserve1);
    }

    // if fee is on, mint liquidity equivalent to 1/6th of the growth in sqrt(k)
    function _mintFee(uint112, uint112) private pure returns (bool) {
        return false;
    }

    // this low-level function should be called from a contract which performs important safety checks
    function mint(address to) external lock returns (uint256 liquidity) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        uint256 balance0 = IERC20(token0).balanceOf(address(this));
        uint256 balance1 = IERC20(token1).balanceOf(address(this));
        uint256 amount0 = balance0.sub(_reserve0);
        uint256 amount1 = balance1.sub(_reserve1);

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        if (_totalSupply == 0) {
            liquidity = UniswapV2Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
            _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
        } else {
            liquidity = UniswapV2Math.min(amount0.mul(_totalSupply) / _reserve0, amount1.mul(_totalSupply) / _reserve1);
        }
        require(liquidity > 0, "UniswapV2: INSUFFICIENT_LIQUIDITY_MINTED");
        _mint(to, liquidity);

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint256(reserve0).mul(reserve1); // reserve0 and reserve1 are up-to-date
        emit Mint(msg.sender, amount0, amount1);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function burn(address to) external lock returns (uint256 amount0, uint256 amount1) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        uint256 balance0 = IERC20(_token0).balanceOf(address(this));
        uint256 balance1 = IERC20(_token1).balanceOf(address(this));
        uint256 liquidity = balanceOf[address(this)];

        bool feeOn = _mintFee(_reserve0, _reserve1);
        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
        amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
        require(amount0 > 0 && amount1 > 0, "UniswapV2: INSUFFICIENT_LIQUIDITY_BURNED");
        _burn(address(this), liquidity);
        _safeTransfer(_token0, to, amount0);
        _safeTransfer(_token1, to, amount1);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        _update(balance0, balance1, _reserve0, _reserve1);
        if (feeOn) kLast = uint256(reserve0).mul(reserve1); // reserve0 and reserve1 are up-to-date
        emit Burn(msg.sender, amount0, amount1, to);
    }

    // this low-level function should be called from a contract which performs important safety checks
    function swap(uint256 amount0Out, uint256 amount1Out, address to, bytes calldata data) external lock {
        require(amount0Out > 0 || amount1Out > 0, "UniswapV2: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        require(amount0Out < _reserve0 && amount1Out < _reserve1, "UniswapV2: INSUFFICIENT_LIQUIDITY");

        uint256 balance0;
        uint256 balance1;
        {
            // scope for _token{0,1}, avoids stack too deep errors
            address _token0 = token0;
            address _token1 = token1;
            require(to != _token0 && to != _token1, "UniswapV2: INVALID_TO");
            if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
            if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
            if (data.length > 0) IUniswapV2Callee(to).uniswapV2Call(msg.sender, amount0Out, amount1Out, data);
            balance0 = IERC20(_token0).balanceOf(address(this));
            balance1 = IERC20(_token1).balanceOf(address(this));
        }
        uint256 amount0In = balance0 > _reserve0 - amount0Out ? balance0 - (_reserve0 - amount0Out) : 0;
        uint256 amount1In = balance1 > _reserve1 - amount1Out ? balance1 - (_reserve1 - amount1Out) : 0;
        require(amount0In > 0 || amount1In > 0, "UniswapV2: INSUFFICIENT_INPUT_AMOUNT");
        {
            // scope for reserve{0,1}Adjusted, avoids stack too deep errors
            uint256 balance0Adjusted = balance0.mul(1000).sub(amount0In.mul(3));
            uint256 balance1Adjusted = balance1.mul(1000).sub(amount1In.mul(3));
            require(
                balance0Adjusted.mul(balance1Adjusted) >= uint256(_reserve0).mul(_reserve1).mul(1000 ** 2),
                "UniswapV2: K"
            );
        }

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    // force balances to match reserves
    function skim(address to) external lock {
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        _safeTransfer(_token0, to, IERC20(_token0).balanceOf(address(this)).sub(reserve0));
        _safeTransfer(_token1, to, IERC20(_token1).balanceOf(address(this)).sub(reserve1));
    }

    // force reserves to match balances
    function sync() external lock {
        _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
    }
}

contracts/UniswapV2/core/test/Oracle.t.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/ERC20Mintable.sol";

import "../UniswapV2Pair.sol";
import "../../periphery/libraries/UniswapV2Library.sol";
import "../../periphery/libraries/OracleLibrary.sol";
import "../UniswapV2Factory.sol";

contract OracleImpl {
    function consult(address pair, uint32 period) public view returns (uint256) {
        return OracleLibrary.consult(pair, period);
    }
}

contract OracleTest is Test {
    uint256 public constant TIMESTAMP = 1668642357;
    uint256 public constant BLOCKTIME = 15;
    uint256 public START_PRICE;

    UniswapV2Pair public pair;
    UniswapV2Factory factory;

    ERC20Mintable public DAI = new ERC20Mintable();
    ERC20Mintable public WETH = new ERC20Mintable();
    OracleImpl public oracleImpl = new OracleImpl();

    address user1 = address(10000001);

    address protocolFeeBeneficiary = address(10000005);

    function setUp() public {
        vm.warp(TIMESTAMP);

        factory = new UniswapV2Factory(150, 30, protocolFeeBeneficiary);

        pair = UniswapV2Pair(factory.createPair(address(WETH), address(DAI)));
        WETH.mint(address(pair), 1000e18);
        DAI.mint(address(pair), 1500000e18);
        pair.mint(user1);

        START_PRICE = 1500000e18 * 1e18 / 1000e18;
    }

    function _mineBlock(uint256 _time) public {
        vm.roll(block.number + 1);
        vm.warp(block.timestamp + _time);
    }

    function _swap(uint256 _wethIn, uint256 _daiIn) public returns (uint256 amountOut) {
        _mineBlock(BLOCKTIME);

        (uint112 reserve0, uint112 reserve1,) = pair.getReserves();
        if (_wethIn > 0) {
            uint256 amount1Out =
                UniswapV2Library.getAmountOut(_wethIn, reserve0, reserve1, address(pair), address(factory));
            WETH.mint(address(pair), _wethIn);
            pair.swap(0, amount1Out, user1, bytes(""));
            amountOut = amount1Out;
        } else if (_daiIn > 0) {
            uint256 amount0Out =
                UniswapV2Library.getAmountOut(_daiIn, reserve1, reserve0, address(pair), address(factory));
            DAI.mint(address(pair), _daiIn);
            pair.swap(amount0Out, 0, user1, bytes(""));
            amountOut = amount0Out;
        }
    }

    function testTokensOrder() public {
        assertEq(address(WETH), pair.token0());
        assertEq(address(DAI), pair.token1());
    }

    function testConsultOld() public {
        vm.expectRevert(bytes("OLD"));
        oracleImpl.consult(address(pair), 1800);

        assertEq(pair.lastPrice(), 0);

        _swap(0.01e18, 0);
        vm.warp(block.timestamp + BLOCKTIME);
        assertEq(oracleImpl.consult(address(pair), 15), START_PRICE);

        vm.expectRevert(bytes("OLD"));
        oracleImpl.consult(address(pair), 16);

        vm.warp(block.timestamp + 1800 - BLOCKTIME);

        assertEq(oracleImpl.consult(address(pair), 1800), START_PRICE);

        _swap(1e18, 0);

        vm.expectRevert(bytes("OLD"));
        oracleImpl.consult(address(pair), 1);

        vm.warp(block.timestamp + BLOCKTIME);

        assertEq(oracleImpl.consult(address(pair), 15), pair.lastPrice());
    }

    struct TestAction {
        uint256 wethIn;
        uint256 daiIn;
        uint256 timetravelBefore;
        uint256 timetravelAfter;
        uint32 period;
        uint256 TWAP;
        uint16 observationCardinalityNext;
        uint256 observationIndex;
    }

    // workaround for "UnimplementedFeatureError: Copying of type struct memory to storage not yet supported."
    uint256 public constant depositTestCasesLength = 22;

    function getTestAction(uint256 _index) public view returns (TestAction memory) {
        TestAction[depositTestCasesLength] memory testSwapCases = [
            TestAction({
                wethIn: 0.01e18,
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 60,
                period: 60,
                TWAP: START_PRICE,
                observationCardinalityNext: 120,
                observationIndex: 0
            }),
            TestAction({
                wethIn: 0.01e18,
                daiIn: 0,
                timetravelBefore: 1800,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1499970495435267142183,
                observationCardinalityNext: 120,
                observationIndex: 1
            }),
            TestAction({
                wethIn: 100e18, // big trade
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 45,
                period: 1800,
                TWAP: 1499969511978807824364,
                observationCardinalityNext: 120,
                observationIndex: 2
            }),
            TestAction({
                wethIn: 1,
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 900,
                period: 1800,
                TWAP: 1363107217423634267487,
                observationCardinalityNext: 120,
                observationIndex: 3
            }),
            TestAction({
                wethIn: 0,
                daiIn: 1000000e18, // huge trade
                timetravelBefore: 900,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1243352310158670807355,
                observationCardinalityNext: 120,
                observationIndex: 4
            }),
            TestAction({
                wethIn: 1,
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1263646160072652749974,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 45, // 60 sec since huge trade
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1344821559728580520453,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 45, // 120 sec since huge trade
                timetravelAfter: 330, // 450 sec since huge trade
                period: 1800,
                TWAP: 1872461657492111028567,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 45, // 510 sec since huge trade
                timetravelAfter: 390, // 900 sec since huge trade
                period: 1800,
                TWAP: 2481277154911569307160,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 45, // 960 sec since huge trade
                timetravelAfter: 390, // 1350 sec since huge trade
                period: 1800,
                TWAP: 3090092652331027585753,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 15, // 1380 sec since huge trade
                timetravelAfter: 400, // 1780 sec since huge trade
                period: 1800,
                TWAP: 3671849683198509940853,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 5, // 1800 sec since huge trade
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3678614299836503921727,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 45, // 1860 sec since huge trade
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3678614299836503921727,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 145, // 2020 sec since huge trade
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3678614299836503921727,
                observationCardinalityNext: 120,
                observationIndex: 5
            }),
            TestAction({
                wethIn: 250e18,
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3678614299836503921715,
                observationCardinalityNext: 120,
                observationIndex: 6
            }),
            TestAction({
                wethIn: 1,
                daiIn: 0,
                timetravelBefore: 0,
                timetravelAfter: 900,
                period: 1800,
                TWAP: 2783849579014797350624,
                observationCardinalityNext: 120,
                observationIndex: 7
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1918421406416753290062,
                observationCardinalityNext: 120,
                observationIndex: 7
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1918421406416753290062,
                observationCardinalityNext: 120,
                observationIndex: 7
            }),
            TestAction({
                wethIn: 0,
                daiIn: 500000e18,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 1918421406416753290057,
                observationCardinalityNext: 120,
                observationIndex: 8
            }),
            TestAction({
                wethIn: 1,
                daiIn: 0,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 2554290763490825493524,
                observationCardinalityNext: 120,
                observationIndex: 9
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3190160120564897696992,
                observationCardinalityNext: 120,
                observationIndex: 9
            }),
            TestAction({
                wethIn: 0,
                daiIn: 0,
                timetravelBefore: 885,
                timetravelAfter: 0,
                period: 1800,
                TWAP: 3190160120564897696992,
                observationCardinalityNext: 120,
                observationIndex: 9
            })
        ];

        return testSwapCases[_index];
    }

    function testConsult() public {
        for (uint256 i = 0; i < depositTestCasesLength; i++) {
            TestAction memory testCase = getTestAction(i);

            vm.warp(block.timestamp + testCase.timetravelBefore);
            _swap(testCase.wethIn, testCase.daiIn);
            vm.warp(block.timestamp + testCase.timetravelAfter);

            assertEq(oracleImpl.consult(address(pair), testCase.period), testCase.TWAP);

            if (testCase.observationCardinalityNext > pair.observationCardinalityNext()) {
                pair.increaseObservationCardinalityNext(testCase.observationCardinalityNext);
            }

            assertEq(pair.observationIndex(), testCase.observationIndex);
        }
    }

    function testObservationsSingleCardinality() public {
        uint32 blockTimestamp;
        uint256 priceCumulative;
        bool initialized;

        (blockTimestamp, priceCumulative, initialized) = pair.observations(pair.observationIndex());
        assertEq(blockTimestamp, block.timestamp);
        assertEq(priceCumulative, 0);
        assertEq(initialized, true);

        _swap(0.01e18, 0);

        (blockTimestamp, priceCumulative, initialized) = pair.observations(pair.observationIndex());
        assertEq(blockTimestamp, block.timestamp);
        assertEq(priceCumulative, START_PRICE * BLOCKTIME);
        assertEq(initialized, true);

        _swap(1e18, 0);

        (blockTimestamp, priceCumulative, initialized) = pair.observations(pair.observationIndex());
        assertEq(blockTimestamp, block.timestamp);
        assertEq(priceCumulative, START_PRICE * BLOCKTIME + pair.lastPrice() * BLOCKTIME);
        assertEq(initialized, true);

        vm.warp(block.timestamp + BLOCKTIME);

        assertEq(oracleImpl.consult(address(pair), 15), pair.lastPrice());
    }

    function _assertCardinality(
        uint16 observationIndex,
        uint16 observationCardinality,
        uint16 observationCardinalityNext
    ) public {
        assertEq(pair.observationIndex(), observationIndex);
        assertEq(pair.observationCardinality(), observationCardinality);
        assertEq(pair.observationCardinalityNext(), observationCardinalityNext);
    }

    function testIncreaseObservationCardinalityNext() public {
        _swap(1e18, 0);
        _assertCardinality(0, 1, 1);

        _swap(1e18, 0);
        _assertCardinality(0, 1, 1);

        pair.increaseObservationCardinalityNext(3);
        _assertCardinality(0, 1, 3);

        _swap(1e18, 0);
        _assertCardinality(1, 3, 3);

        _swap(1e18, 0);
        _assertCardinality(2, 3, 3);

        _swap(1e18, 0);
        _assertCardinality(0, 3, 3);

        pair.increaseObservationCardinalityNext(10);
        _assertCardinality(0, 3, 10);

        _swap(1e18, 0);
        _assertCardinality(1, 3, 10);

        _swap(1e18, 0);
        _assertCardinality(2, 3, 10);

        _swap(1e18, 0);
        _assertCardinality(3, 10, 10);

        for (uint256 i = 0; i < 6; i++) {
            _swap(1e18, 0);
        }
        _assertCardinality(9, 10, 10);

        _swap(1e18, 0);
        _assertCardinality(0, 10, 10);

        pair.increaseObservationCardinalityNext(15);
        _assertCardinality(0, 10, 15);

        for (uint256 i = 0; i < 9; i++) {
            _swap(1e18, 0);
        }
        _assertCardinality(9, 10, 15);

        _swap(1e18, 0);
        _assertCardinality(10, 15, 15);

        for (uint256 i = 0; i < 4; i++) {
            _swap(1e18, 0);
        }
        _assertCardinality(14, 15, 15);

        _swap(1e18, 0);
        _assertCardinality(0, 15, 15);
    }

    function testObservationsMultipleCardinality() public {
        uint32 blockTimestamp;
        uint256 priceCumulative;
        bool initialized;

        _swap(0.01e18, 0);
        assertEq(pair.lastPrice(), START_PRICE);

        (uint32 blockTimestamp0, uint256 priceCumulative0, bool initialized0) = pair.observations(0);
        assertEq(blockTimestamp0, block.timestamp);
        assertEq(priceCumulative0, START_PRICE * BLOCKTIME);
        assertEq(initialized0, true);

        pair.increaseObservationCardinalityNext(3);

        _swap(100e18, 0);

        (blockTimestamp, priceCumulative, initialized) = pair.observations(0);
        assertEq(blockTimestamp, blockTimestamp0);
        assertEq(priceCumulative, priceCumulative0);
        assertEq(initialized, initialized0);

        (blockTimestamp, priceCumulative, initialized) = pair.observations(1);
        assertEq(blockTimestamp, blockTimestamp0 + BLOCKTIME);
        assertEq(priceCumulative, priceCumulative0 + pair.lastPrice() * BLOCKTIME);
        assertEq(initialized, true);
    }
}

contracts/UniswapV2/core/test/UniswapV2F.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/ERC20Mintable.sol";

import "../UniswapV2Factory.sol";
import "../../periphery/libraries/UniswapV2Library.sol";

contract UniswapV2FactoryTest is Test {
    UniswapV2Factory factory;

    address pool1;
    address hacker = address(10000004);
    address owner = address(10000005);

    address tokenA = address(10000006);
    address tokenB = address(10000007);

    address protocolFeeBeneficiary = address(10000005);

    uint256 tooBigFee;
    uint256 MAX_FEE;

    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );
    event DefaultFeesSet(IUniswapV2Factory.DefaultFees fees);
    event LpFeesSet(address indexed pair, uint256 lpFee, bool overrideFee);
    event RoyaltiesFeesSet(
        address indexed pair,
        address beneficiary,
        uint256 royaltiesFee
    );
    event ProtocolFeesSet(
        address indexed pair,
        uint256 protocolFee,
        bool overrideFee
    );
    event ProtocolFeeBeneficiarySet(address beneficiary);

    function setUp() public {
        vm.prank(owner);
        factory = new UniswapV2Factory(0, 0, protocolFeeBeneficiary);

        MAX_FEE = factory.MAX_FEE();
        tooBigFee = MAX_FEE + 1;

        pool1 = _createPair(tokenA, tokenB);
    }

    function _assertFees(
        address _expectedPool,
        address _expectedRoyaltiesBeneficiary,
        uint256 _expectedRoyaltiesFee,
        uint256 _expectedProtocolFee,
        uint256 _expectedLpFee,
        address _expectedProtocolFeeBeneficiary
    ) public {
        (
            uint256 lpFee,
            address royaltiesBeneficiary,
            uint256 royaltiesFee,
            address protocolBeneficiary,
            uint256 protocolFee
        ) = factory.getFeesAndRecipients(_expectedPool);

        assertEq(lpFee, _expectedLpFee);
        assertEq(royaltiesBeneficiary, _expectedRoyaltiesBeneficiary);
        assertEq(royaltiesFee, _expectedRoyaltiesFee);
        assertEq(protocolBeneficiary, _expectedProtocolFeeBeneficiary);
        assertEq(protocolFee, _expectedProtocolFee);
    }

    function _createPair(
        address _tokenA,
        address _tokenB
    ) public returns (address pair) {
        vm.mockCall(
            _tokenA,
            abi.encodeCall(ERC20.decimals, ()),
            abi.encode(18)
        );
        vm.mockCall(
            _tokenB,
            abi.encodeCall(ERC20.decimals, ()),
            abi.encode(18)
        );

        pair = factory.createPair(_tokenA, _tokenB);
    }

    function testSetDefaultFees(uint256 _lpFee, uint256 _protocolFee) public {
        vm.assume(_lpFee <= MAX_FEE);
        vm.assume(_protocolFee <= MAX_FEE);
        vm.assume(_protocolFee + _lpFee <= MAX_FEE);

        (uint256 protocolFee, uint256 lpFee) = factory.defaultFees();

        assertEq(protocolFee, 0);
        assertEq(lpFee, 0);

        _assertFees(pool1, address(0), 0, 0, 0, protocolFeeBeneficiary);

        IUniswapV2Factory.DefaultFees memory fees = IUniswapV2Factory
            .DefaultFees({protocolFee: _protocolFee, lpFee: _lpFee});

        vm.prank(hacker);
        vm.expectRevert("Ownable: caller is not the owner");
        factory.setDefaultFees(fees);

        vm.prank(owner);
        vm.expectEmit(true, true, true, true);
        emit DefaultFeesSet(fees);
        factory.setDefaultFees(fees);

        (protocolFee, lpFee) = factory.defaultFees();

        assertEq(protocolFee, _protocolFee);
        assertEq(lpFee, _lpFee);

        _assertFees(
            pool1,
            address(0),
            0,
            _protocolFee,
            _lpFee,
            protocolFeeBeneficiary
        );

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: protocolFee > MAX_FEE");
        factory.setDefaultFees(
            IUniswapV2Factory.DefaultFees({
                protocolFee: tooBigFee,
                lpFee: _lpFee
            })
        );

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: lpFee > MAX_FEE");
        factory.setDefaultFees(
            IUniswapV2Factory.DefaultFees({
                protocolFee: _protocolFee,
                lpFee: tooBigFee
            })
        );

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: protocolFee + lpFee > MAX_FEE");
        factory.setDefaultFees(
            IUniswapV2Factory.DefaultFees({
                protocolFee: tooBigFee / 2 + 1,
                lpFee: tooBigFee / 2
            })
        );
    }

    function testSetLpFee(
        address _pair,
        uint256 _lpFee,
        address _tokenA,
        address _tokenB
    ) public {
        vm.assume(_lpFee <= MAX_FEE);

        vm.assume(_pair != pool1);
        vm.assume(_tokenA != address(0));
        vm.assume(_tokenB != address(0));
        vm.assume(_tokenA != _tokenB);

        vm.prank(hacker);
        vm.expectRevert("Ownable: caller is not the owner");
        factory.setLpFee(_pair, _lpFee, true);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _pair invalid");
        factory.setLpFee(_pair, _lpFee, true);

        _pair = _createPair(_tokenA, _tokenB);

        vm.prank(owner);
        vm.expectEmit(true, true, true, true);
        emit LpFeesSet(_pair, _lpFee, true);
        factory.setLpFee(_pair, _lpFee, true);

        (
            address royaltiesBeneficiary,
            uint256 royaltiesFee,
            uint256 protocolFee,
            uint256 lpFee,
            bool protocolFeeOverride,
            bool lpFeeOverride
        ) = factory.pairFees(_pair);

        assertEq(royaltiesBeneficiary, address(0));
        assertEq(royaltiesFee, 0);
        assertEq(protocolFee, 0);
        assertEq(lpFee, _lpFee);
        assertFalse(protocolFeeOverride);
        assertTrue(lpFeeOverride);

        _assertFees(_pair, address(0), 0, 0, _lpFee, protocolFeeBeneficiary);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _lpFee > MAX_FEE");
        factory.setLpFee(_pair, tooBigFee, true);
    }

    function testSetRoyaltiesFee(
        address _pair,
        address _royaltiesBeneficiary,
        uint256 _royaltiesFee,
        address _tokenA,
        address _tokenB
    ) public {
        vm.assume(_royaltiesFee <= MAX_FEE);

        vm.assume(_pair != pool1);
        vm.assume(_tokenA != address(0));
        vm.assume(_tokenB != address(0));
        vm.assume(_tokenA != _tokenB);

        vm.prank(hacker);
        vm.expectRevert("Ownable: caller is not the owner");
        factory.setRoyaltiesFee(_pair, _royaltiesBeneficiary, _royaltiesFee);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _pair invalid");
        factory.setRoyaltiesFee(_pair, _royaltiesBeneficiary, _royaltiesFee);

        _pair = _createPair(_tokenA, _tokenB);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _beneficiary invalid");
        factory.setRoyaltiesFee(_pair, address(0), _royaltiesFee);

        vm.assume(_royaltiesBeneficiary != address(0));

        vm.prank(owner);
        vm.expectEmit(true, true, true, true);
        emit RoyaltiesFeesSet(_pair, _royaltiesBeneficiary, _royaltiesFee);
        factory.setRoyaltiesFee(_pair, _royaltiesBeneficiary, _royaltiesFee);

        (
            address royaltiesBeneficiary,
            uint256 royaltiesFee,
            uint256 protocolFee,
            uint256 lpFee,
            bool protocolFeeOverride,
            bool lpFeeOverride
        ) = factory.pairFees(_pair);

        assertEq(royaltiesBeneficiary, _royaltiesBeneficiary);
        assertEq(royaltiesFee, _royaltiesFee);
        assertEq(protocolFee, 0);
        assertEq(lpFee, 0);
        assertFalse(protocolFeeOverride);
        assertFalse(lpFeeOverride);

        _assertFees(
            _pair,
            _royaltiesBeneficiary,
            _royaltiesFee,
            0,
            0,
            protocolFeeBeneficiary
        );

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _royaltiesFee > MAX_FEE");
        factory.setRoyaltiesFee(_pair, _royaltiesBeneficiary, tooBigFee);
    }

    function testSetProtocolFee(
        address _pair,
        uint256 _protocolFee,
        address _tokenA,
        address _tokenB
    ) public {
        vm.assume(_protocolFee <= MAX_FEE);
        vm.assume(_pair != pool1);

        vm.assume(_tokenA != address(0));
        vm.assume(_tokenB != address(0));
        vm.assume(_tokenA != _tokenB);

        vm.prank(hacker);
        vm.expectRevert("Ownable: caller is not the owner");
        factory.setProtocolFee(_pair, _protocolFee, true);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _pair invalid");
        factory.setProtocolFee(_pair, _protocolFee, true);

        _pair = _createPair(_tokenA, _tokenB);

        vm.prank(owner);
        vm.expectEmit(true, true, true, true);
        emit ProtocolFeesSet(_pair, _protocolFee, true);
        factory.setProtocolFee(_pair, _protocolFee, true);

        (
            address royaltiesBeneficiary,
            uint256 royaltiesFee,
            uint256 protocolFee,
            uint256 lpFee,
            bool protocolFeeOverride,
            bool lpFeeOverride
        ) = factory.pairFees(_pair);

        assertEq(royaltiesBeneficiary, address(0));
        assertEq(royaltiesFee, 0);
        assertEq(protocolFee, _protocolFee);
        assertEq(lpFee, 0);
        assertTrue(protocolFeeOverride);
        assertFalse(lpFeeOverride);

        _assertFees(
            _pair,
            address(0),
            0,
            _protocolFee,
            0,
            protocolFeeBeneficiary
        );

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: _protocolFee > MAX_FEE");
        factory.setProtocolFee(_pair, tooBigFee, true);
    }

    function testSetProtocolFeeBeneficiary(address _beneficiary) public {
        vm.assume(_beneficiary != address(0));

        vm.prank(hacker);
        vm.expectRevert("Ownable: caller is not the owner");
        factory.setProtocolFeeBeneficiary(_beneficiary);

        vm.prank(owner);
        vm.expectEmit(true, true, true, true);
        emit ProtocolFeeBeneficiarySet(_beneficiary);
        factory.setProtocolFeeBeneficiary(_beneficiary);

        assertEq(factory.protocolFeeBeneficiary(), _beneficiary);
        _assertFees(pool1, address(0), 0, 0, 0, _beneficiary);

        vm.prank(owner);
        vm.expectRevert("MagicswapV2: BENEFICIARY");
        factory.setProtocolFeeBeneficiary(address(0));
    }

    function testGetFees(
        uint256 _lpFee,
        uint256 _royaltiesFee,
        uint256 _protocolFee
    ) public {
        vm.assume(_lpFee <= MAX_FEE);
        vm.assume(_royaltiesFee <= MAX_FEE);
        vm.assume(_protocolFee <= MAX_FEE);

        vm.startPrank(owner);
        factory.setLpFee(pool1, _lpFee, true);
        factory.setRoyaltiesFee(pool1, owner, _royaltiesFee);
        factory.setProtocolFee(pool1, _protocolFee, true);
        vm.stopPrank();

        (uint256 lpFee, uint256 royaltiesFee, uint256 protocolFee) = factory
            .getFees(pool1);
        uint256 totalFee = lpFee + royaltiesFee + protocolFee;

        assertEq(lpFee, _lpFee);

        /// logic below should check that:
        /// - totalFee is never above MAX_FEE
        /// - if _lpFee + _royaltiesFee + _protocolFee > MAX_FEE
        ///   then we fill totalFee with fees in following priority:
        ///   1. lpFee
        ///   2. royaltiesFee
        ///   3. protocolFee
        ///   until we get to MAX_FEE

        if (_lpFee < MAX_FEE) {
            if (_lpFee + _royaltiesFee < MAX_FEE) {
                assertEq(royaltiesFee, _royaltiesFee);

                if (_lpFee + _royaltiesFee + _protocolFee <= MAX_FEE) {
                    assertEq(protocolFee, _protocolFee);
                    assertEq(
                        _lpFee + _royaltiesFee + _protocolFee,
                        factory.getTotalFee(pool1)
                    );
                } else {
                    assertEq(protocolFee, MAX_FEE - _lpFee - _royaltiesFee);
                }
            } else {
                assertEq(royaltiesFee, MAX_FEE - _lpFee);
                assertEq(protocolFee, 0);
            }
        } else {
            assertEq(royaltiesFee, 0);
            assertEq(protocolFee, 0);
        }

        assertEq(totalFee, factory.getTotalFee(pool1));
        assertTrue(totalFee <= MAX_FEE);
    }

    function testCreatePair(address _tokenA, address _tokenB) public {
        vm.assume(_tokenA != address(0));
        vm.assume(_tokenB != address(0));
        vm.assume(_tokenA != _tokenB);

        vm.mockCall(
            _tokenA,
            abi.encodeCall(ERC20.decimals, ()),
            abi.encode(18)
        );
        vm.mockCall(
            _tokenB,
            abi.encodeCall(ERC20.decimals, ()),
            abi.encode(18)
        );

        (address token0, address token1) = UniswapV2Library.sortTokens(
            _tokenA,
            _tokenB
        );
        address expectedPair = UniswapV2Library.pairFor(
            address(factory),
            _tokenA,
            _tokenB
        );

        vm.expectEmit(true, true, true, true);
        emit PairCreated(token0, token1, expectedPair, 2);
        address pair = factory.createPair(_tokenA, _tokenB);

        assertEq(pair, expectedPair);
        assertEq(factory.getPair(_tokenA, _tokenB), pair);
        assertEq(factory.getPair(_tokenB, _tokenA), pair);
        assertEq(factory.allPairs(1), pair);
        assertEq(factory.allPairsLength(), 2);

        address[] memory allPairs = factory.allPairs();
        assertEq(allPairs[0], pool1);
        assertEq(allPairs[1], pair);
    }
}

contracts/UniswapV2/core/test/UniswapV2P.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/ERC20Mintable.sol";

import "../UniswapV2Pair.sol";
import "../../periphery/libraries/UniswapV2Library.sol";
import "../UniswapV2Factory.sol";
import "./mock/UniswapV2PairOriginal.sol";

contract UniswapV2PairTest is Test {
    UniswapV2Pair pair;
    UniswapV2Pair pairWithFees;
    UniswapV2PairOriginal pairOriginal;
    UniswapV2Factory factory;

    ERC20Mintable token0;
    ERC20Mintable token1;

    address user1 = address(10000001);
    address user2 = address(10000002);
    address user3 = address(10000003);
    address user4 = address(10000004);

    address protocolFeeBeneficiary = address(10000005);
    address royaltiesBeneficiary = address(10000006);

    uint256 royaltiesFee = 50;
    uint256 protocolFee = 50;

    function setUp() public {
        address tokenA = address(new ERC20Mintable());
        address tokenB = address(new ERC20Mintable());
        (tokenA, tokenB) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        token0 = ERC20Mintable(tokenA);
        token1 = ERC20Mintable(tokenB);

        factory = new UniswapV2Factory(0, 30, protocolFeeBeneficiary);

        vm.startPrank(address(factory));

        pair = new UniswapV2Pair();
        pair.initialize(address(token0), address(token1));

        pairOriginal = new UniswapV2PairOriginal();
        pairOriginal.initialize(address(token0), address(token1));

        pairWithFees = UniswapV2Pair(factory.createPair(address(token0), address(token1)));

        vm.stopPrank();

        factory.setRoyaltiesFee(address(pairWithFees), royaltiesBeneficiary, royaltiesFee);
        factory.setProtocolFee(address(pairWithFees), protocolFee, true);
        factory.setProtocolFeeBeneficiary(protocolFeeBeneficiary);
    }

    function _assertPairs(UniswapV2Pair _pair, UniswapV2PairOriginal _pairOriginal) public {
        assertEq(_pair.token0(), _pairOriginal.token0());
        assertEq(_pair.token1(), _pairOriginal.token1());

        (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) = _pair.getReserves();
        (uint112 _reserve0Org, uint112 _reserve1Org, uint32 _blockTimestampLastOrg) = _pairOriginal.getReserves();

        assertEq(_reserve0, _reserve0Org);
        assertEq(_reserve1, _reserve1Org);
        assertEq(_blockTimestampLast, _blockTimestampLastOrg);

        assertEq(_pair.factory(), _pairOriginal.factory());

        assertEq(token0.balanceOf(address(_pair)), token0.balanceOf(address(_pairOriginal)));
        assertEq(token1.balanceOf(address(_pair)), token1.balanceOf(address(_pairOriginal)));
    }

    function _addLiquidity(address _pair, uint256 _amount0, uint256 _amount1, address _to)
        public
        returns (uint256 liquidity)
    {
        token0.mint(_pair, _amount0);
        token1.mint(_pair, _amount1);

        liquidity = UniswapV2Pair(_pair).mint(_to);
    }

    function testMintBurnRegression(uint112 _amount0, uint112 _amount1) public {
        vm.assume(_amount0 > pair.MINIMUM_LIQUIDITY());
        vm.assume(_amount1 > pair.MINIMUM_LIQUIDITY());

        uint256 liquidity = _addLiquidity(address(pair), _amount0, _amount1, user1);
        assertEq(liquidity, pair.totalSupply() - pair.MINIMUM_LIQUIDITY());

        uint256 liquidityOriginal = _addLiquidity(address(pairOriginal), _amount0, _amount1, user1);
        assertEq(liquidityOriginal, pairOriginal.totalSupply() - pairOriginal.MINIMUM_LIQUIDITY());

        assertEq(liquidity, liquidityOriginal);

        _assertPairs(pair, pairOriginal);

        vm.prank(user1);
        pair.transfer(address(pair), liquidity);
        (uint256 amount0, uint256 amount1) = pair.burn(user1);

        vm.prank(user1);
        pairOriginal.transfer(address(pairOriginal), liquidity);
        (uint256 amount0Org, uint256 amount1Org) = pairOriginal.burn(user2);

        assertEq(amount0, amount0Org);
        assertEq(amount1, amount1Org);

        _assertPairs(pair, pairOriginal);
    }

    function _swap(address _pair, uint256 _amount0In, uint256 _amount1In, address _to)
        public
        returns (uint256 amountOut)
    {
        (uint112 reserve0, uint112 reserve1,) = UniswapV2Pair(_pair).getReserves();
        token0.mint(_pair, _amount0In);
        token1.mint(_pair, _amount1In);

        if (_amount0In > _amount1In) {
            uint256 amount1Out = UniswapV2Library.getAmountOut(_amount0In, reserve0, reserve1, _pair, address(factory));
            UniswapV2Pair(_pair).swap(0, amount1Out, _to, bytes(""));
            amountOut = amount1Out;
        } else {
            uint256 amount0Out = UniswapV2Library.getAmountOut(_amount1In, reserve1, reserve0, _pair, address(factory));
            UniswapV2Pair(_pair).swap(amount0Out, 0, _to, bytes(""));
            amountOut = amount0Out;
        }
    }

    function testSwapRegression(uint96 _reserve0, uint96 _reserve1, uint72 _amount0In, uint72 _amount1In) public {
        vm.assume(_reserve0 > 10000e18);
        vm.assume(_reserve1 > 10000e18);
        vm.assume(_amount0In > 0.001e18);
        vm.assume(_amount1In > 0.001e18);

        _addLiquidity(address(pair), _reserve0, _reserve1, user3);
        _addLiquidity(address(pairOriginal), _reserve0, _reserve1, user3);

        _assertPairs(pair, pairOriginal);

        assertEq(token0.balanceOf(user1), 0);
        assertEq(token1.balanceOf(user1), 0);

        uint256 amount1Out = _swap(address(pair), _amount0In, 0, user1);
        assertEq(token0.balanceOf(user1), 0);
        assertEq(token1.balanceOf(user1), amount1Out);

        assertEq(token0.balanceOf(user2), 0);
        assertEq(token1.balanceOf(user2), 0);

        uint256 amount1OutOrg = _swap(address(pairOriginal), _amount0In, 0, user2);
        assertEq(token0.balanceOf(user2), 0);
        assertEq(token1.balanceOf(user2), amount1OutOrg);

        assertEq(amount1Out, amount1OutOrg);
        _assertPairs(pair, pairOriginal);

        for (uint256 i = 0; i < 20; i++) {
            if (i % 2 == 0) {
                _swap(address(pair), _amount0In, 0, user1);
                _swap(address(pairOriginal), _amount0In, 0, user2);
            } else {
                _swap(address(pair), 0, _amount1In, user1);
                _swap(address(pairOriginal), 0, _amount1In, user2);
            }
        }

        _assertPairs(pair, pairOriginal);
    }

    function testSkimRegression(uint96 _reserve0, uint96 _reserve1, uint72 _amount0In, uint72 _amount1In) public {
        vm.assume(_reserve0 > 10000e18);
        vm.assume(_reserve1 > 10000e18);
        vm.assume(_amount0In > 0.001e18);
        vm.assume(_amount1In > 0.001e18);

        _addLiquidity(address(pair), _reserve0, _reserve1, user3);
        _addLiquidity(address(pairOriginal), _reserve0, _reserve1, user3);

        _assertPairs(pair, pairOriginal);

        assertEq(token0.balanceOf(user1), 0);
        assertEq(token1.balanceOf(user1), 0);

        token0.mint(address(pair), _amount0In);
        token1.mint(address(pair), _amount1In);
        pair.skim(user1);
        assertEq(token0.balanceOf(user1), _amount0In);
        assertEq(token1.balanceOf(user1), _amount1In);

        assertEq(token0.balanceOf(user2), 0);
        assertEq(token1.balanceOf(user2), 0);

        token0.mint(address(pairOriginal), _amount0In);
        token1.mint(address(pairOriginal), _amount1In);
        pairOriginal.skim(user2);
        assertEq(token0.balanceOf(user2), _amount0In);
        assertEq(token1.balanceOf(user2), _amount1In);

        _assertPairs(pair, pairOriginal);
    }

    function testSyncRegression(uint96 _reserve0, uint96 _reserve1, uint72 _amount0In, uint72 _amount1In) public {
        vm.assume(_reserve0 > 10000e18);
        vm.assume(_reserve1 > 10000e18);
        vm.assume(_amount0In > 0.001e18);
        vm.assume(_amount1In > 0.001e18);

        _addLiquidity(address(pair), _reserve0, _reserve1, user3);
        _addLiquidity(address(pairOriginal), _reserve0, _reserve1, user3);

        _assertPairs(pair, pairOriginal);

        token0.mint(address(pair), _amount0In);
        token1.mint(address(pair), _amount1In);
        token0.mint(address(pairOriginal), _amount0In);
        token1.mint(address(pairOriginal), _amount1In);

        _assertPairs(pair, pairOriginal);

        pair.sync();
        pairOriginal.sync();

        _assertPairs(pair, pairOriginal);
    }

    function testSwapWithFees(uint96 _reserve0, uint96 _reserve1, uint72 _amount0In, uint72 _amount1In, uint256 _hijackAmount) public {
        vm.assume(_reserve0 > 10000e18);
        vm.assume(_reserve1 > 10000e18);
        vm.assume(_amount0In > 0.001e18);
        vm.assume(_amount1In > 0.001e18);
        vm.assume(_amount0In > _hijackAmount);

        _addLiquidity(address(pairWithFees), _reserve0, _reserve1, user3);
        _addLiquidity(address(pairOriginal), _reserve0, _reserve1, user3);

        _assertPairs(pairWithFees, pairOriginal);

        assertEq(token0.balanceOf(user1), 0);
        assertEq(token1.balanceOf(user1), 0);

        (, address beneficiary, uint256 royalties, address protocolBeneficiary, uint256 protocolBeneficiaryFee) =
            factory.getFeesAndRecipients(address(pairWithFees));

        assertEq(beneficiary, royaltiesBeneficiary);
        assertEq(royalties, royaltiesFee);
        assertEq(token0.balanceOf(beneficiary), 0);
        uint256 royaltiesAmount = _amount0In * royalties / 10000;

        assertEq(protocolBeneficiary, protocolFeeBeneficiary);
        assertEq(protocolBeneficiaryFee, protocolFee);
        assertEq(token0.balanceOf(protocolBeneficiary), 0);
        uint256 protocolFeeAmount = _amount0In * protocolBeneficiaryFee / 10000;

        uint256 amount1Out = _swap(address(pairWithFees), _amount0In, _hijackAmount, user1);
        assertEq(token0.balanceOf(user1), 0);
        assertEq(token1.balanceOf(user1), amount1Out);
        assertEq(token0.balanceOf(beneficiary), royaltiesAmount);
        assertEq(token0.balanceOf(protocolBeneficiary), protocolFeeAmount);
        assertEq(token1.balanceOf(beneficiary), _hijackAmount * royalties / 10000);
        assertEq(token1.balanceOf(protocolBeneficiary), _hijackAmount * protocolBeneficiaryFee / 10000);

        assertEq(token0.balanceOf(user2), 0);
        assertEq(token1.balanceOf(user2), 0);

        uint256 amount1OutOrg = _swap(address(pairOriginal), _amount0In, 0, user2);
        assertEq(token0.balanceOf(user2), 0);
        assertEq(token1.balanceOf(user2), amount1OutOrg);

        uint256 maxPercentDelta = (royaltiesFee + protocolFee + 1) * 1e18 / 10000;
        assertApproxEqRel(amount1Out, amount1OutOrg, maxPercentDelta);
    }
}

contracts/UniswapV2/core/UniswapV2ERC20..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./interfaces/IUniswapV2ERC20.sol";
import "./libraries/SafeMath.sol";

contract UniswapV2ERC20 is IUniswapV2ERC20 {
    using SafeMath for uint256;

    string public constant name = "Magicswap V2";
    string public constant symbol = "MAGIC-V2";
    uint8 public constant decimals = 18;
    uint256 public totalSupply;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    bytes32 public override DOMAIN_SEPARATOR;
    // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
    bytes32 public constant override PERMIT_TYPEHASH =
        0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
    mapping(address => uint256) public nonces;

    constructor() {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes(name)),
                keccak256(bytes("1")),
                chainId,
                address(this)
            )
        );
    }

    function _mint(address to, uint256 value) internal {
        totalSupply = totalSupply.add(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(address(0), to, value);
    }

    function _burn(address from, uint256 value) internal {
        balanceOf[from] = balanceOf[from].sub(value);
        totalSupply = totalSupply.sub(value);
        emit Transfer(from, address(0), value);
    }

    function _approve(address owner, address spender, uint256 value) private {
        allowance[owner][spender] = value;
        emit Approval(owner, spender, value);
    }

    function _transfer(address from, address to, uint256 value) private {
        balanceOf[from] = balanceOf[from].sub(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(from, to, value);
    }

    function approve(address spender, uint256 value) external returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    function transfer(address to, uint256 value) external returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        if (allowance[from][msg.sender] != type(uint256).max) {
            allowance[from][msg.sender] = allowance[from][msg.sender].sub(value);
        }
        _transfer(from, to, value);
        return true;
    }

    function permit(address owner, address spender, uint256 value, uint256 deadline, uint8 v, bytes32 r, bytes32 s)
        external
    {
        require(deadline >= block.timestamp, "MagicswapV2: EXPIRED");
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, nonces[owner]++, deadline))
            )
        );
        address recoveredAddress = ecrecover(digest, v, r, s);
        require(recoveredAddress != address(0) && recoveredAddress == owner, "MagicswapV2: INVALID_SIGNATURE");
        _approve(owner, spender, value);
    }
}

contracts/UniswapV2/core/UniswapV2Factor.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/access/Ownable2Step.sol";
import "lib/openzeppelin-contracts/contracts/utils/structs/EnumerableSet.sol";

import "./interfaces/IUniswapV2Factory.sol";
import "./UniswapV2Pair.sol";

contract UniswapV2Factory is IUniswapV2Factory, Ownable2Step {
    using EnumerableSet for EnumerableSet.AddressSet;

    /// @dev Fee is denominated in basis points so 5000 / 10000 = 50%
    uint256 public constant MAX_FEE = 5000;

    address public protocolFeeBeneficiary;

    mapping(address => mapping(address => address)) public getPair;
    EnumerableSet.AddressSet private _allPairs;

    DefaultFees public defaultFees;
    mapping(address => Fees) public pairFees;

    constructor(uint256 _defaultProtocolFee, uint256 _defaultLpFee, address _protocolFeeBeneficiary) {
        DefaultFees memory startFees = DefaultFees({protocolFee: _defaultProtocolFee, lpFee: _defaultLpFee});

        setDefaultFees(startFees);
        setProtocolFeeBeneficiary(_protocolFeeBeneficiary);
    }

    /// @inheritdoc IUniswapV2Factory
    function getTotalFee(address _pair) public view returns (uint256) {
        (uint256 lpFee, uint256 royaltiesFee, uint256 protocolFee) = _getFees(_pair);
        return lpFee + royaltiesFee + protocolFee;
    }

    /// @inheritdoc IUniswapV2Factory
    function getFees(address _pair) public view returns (uint256 lpFee, uint256 royaltiesFee, uint256 protocolFee) {
        return _getFees(_pair);
    }

    /// @inheritdoc IUniswapV2Factory
    function getFeesAndRecipients(address _pair)
        public
        view
        returns (
            uint256 lpFee,
            address royaltiesBeneficiary,
            uint256 royaltiesFee,
            address protocolBeneficiary,
            uint256 protocolFee
        )
    {
        (lpFee, royaltiesFee, protocolFee) = _getFees(_pair);

        royaltiesBeneficiary = pairFees[_pair].royaltiesBeneficiary;
        protocolBeneficiary = protocolFeeBeneficiary;
    }

    function allPairs() external view returns (address[] memory) {
        return _allPairs.values();
    }

    function allPairs(uint256 _index) external view returns (address) {
        return _allPairs.at(_index);
    }

    function allPairsLength() external view returns (uint256) {
        return _allPairs.length();
    }

    function createPair(address tokenA, address tokenB) external returns (address pair) {
        require(tokenA != tokenB, "MagicswapV2: IDENTICAL_ADDRESSES");
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "MagicswapV2: ZERO_ADDRESS");
        require(getPair[token0][token1] == address(0), "MagicswapV2: PAIR_EXISTS"); // single check is sufficient
        bytes memory bytecode = type(UniswapV2Pair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        IUniswapV2Pair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        _allPairs.add(pair);
        emit PairCreated(token0, token1, pair, _allPairs.length());
    }

    /// @inheritdoc IUniswapV2Factory
    function setDefaultFees(DefaultFees memory _fees) public onlyOwner {
        require(_fees.protocolFee <= MAX_FEE, "MagicswapV2: protocolFee > MAX_FEE");
        require(_fees.lpFee <= MAX_FEE, "MagicswapV2: lpFee > MAX_FEE");
        require(_fees.protocolFee + _fees.lpFee <= MAX_FEE, "MagicswapV2: protocolFee + lpFee > MAX_FEE");

        defaultFees = _fees;

        emit DefaultFeesSet(_fees);
    }

    /// @inheritdoc IUniswapV2Factory
    function setLpFee(address _pair, uint256 _lpFee, bool _overrideFee) external onlyOwner {
        require(_lpFee <= MAX_FEE, "MagicswapV2: _lpFee > MAX_FEE");
        require(_allPairs.contains(_pair), "MagicswapV2: _pair invalid");

        pairFees[_pair].lpFee = _lpFee;
        pairFees[_pair].lpFeeOverride = _overrideFee;

        emit LpFeesSet(_pair, _lpFee, _overrideFee);
    }

    /// @inheritdoc IUniswapV2Factory
    function setRoyaltiesFee(address _pair, address _beneficiary, uint256 _royaltiesFee) external onlyOwner {
        require(_royaltiesFee <= MAX_FEE, "MagicswapV2: _royaltiesFee > MAX_FEE");
        require(_allPairs.contains(_pair), "MagicswapV2: _pair invalid");
        require(_beneficiary != address(0), "MagicswapV2: _beneficiary invalid");

        pairFees[_pair].royaltiesBeneficiary = _beneficiary;
        pairFees[_pair].royaltiesFee = _royaltiesFee;

        emit RoyaltiesFeesSet(_pair, _beneficiary, _royaltiesFee);
    }

    /// @inheritdoc IUniswapV2Factory
    function setProtocolFee(address _pair, uint256 _protocolFee, bool _overrideFee) external onlyOwner {
        require(_protocolFee <= MAX_FEE, "MagicswapV2: _protocolFee > MAX_FEE");
        require(_allPairs.contains(_pair), "MagicswapV2: _pair invalid");

        pairFees[_pair].protocolFee = _protocolFee;
        pairFees[_pair].protocolFeeOverride = _overrideFee;

        emit ProtocolFeesSet(_pair, _protocolFee, _overrideFee);
    }

    /// @inheritdoc IUniswapV2Factory
    function setProtocolFeeBeneficiary(address _beneficiary) public onlyOwner {
        require(_beneficiary != address(0), "MagicswapV2: BENEFICIARY");
        protocolFeeBeneficiary = _beneficiary;

        emit ProtocolFeeBeneficiarySet(_beneficiary);
    }

    function _getLpFee(address _pair) internal view returns (uint256 lpFee) {
        if (pairFees[_pair].lpFeeOverride) {
            return pairFees[_pair].lpFee;
        } else {
            return defaultFees.lpFee;
        }
    }

    function _getRoyaltiesFee(address _pair) internal view returns (uint256 royaltiesFee) {
        return pairFees[_pair].royaltiesFee;
    }

    function _getProtocolFee(address _pair) internal view returns (uint256 protocolFee) {
        if (pairFees[_pair].protocolFeeOverride) {
            return pairFees[_pair].protocolFee;
        } else {
            return defaultFees.protocolFee;
        }
    }

    function _getFees(address _pair) internal view returns (uint256 lpFee, uint256 royaltiesFee, uint256 protocolFee) {
        lpFee = _getLpFee(_pair);
        /// lpFee should never be above MAX_FEE but never too safe.
        /// If lpFee is set to MAX_FEE then we know there's no more space for other fees
        if (lpFee >= MAX_FEE) {
            return (MAX_FEE, 0, 0);
        }

        royaltiesFee = _getRoyaltiesFee(_pair);
        /// if royaltiesFee + lpFee is greater than MAX_FEE, then decrease royaltiesFee
        /// and return as we know there's no more space for other fees
        if (royaltiesFee >= MAX_FEE - lpFee) {
            return (lpFee, MAX_FEE - lpFee, 0);
        }

        protocolFee = _getProtocolFee(_pair);
        /// if protocolFee + royaltiesFee + lpFee is greater than MAX_FEE, then decrease protocolFee
        if (protocolFee > MAX_FEE - lpFee - royaltiesFee) {
            protocolFee = MAX_FEE - lpFee - royaltiesFee;
        }
    }
}

contracts/UniswapV2/core/UniswapV2Pair.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IUniswapV2Pair.sol";
import "./interfaces/IUniswapV2Factory.sol";

import "./libraries/UniswapV2Math.sol";
import "./libraries/Oracle.sol";

import "./UniswapV2ERC20.sol";

contract UniswapV2Pair is IUniswapV2Pair, UniswapV2ERC20 {
    using SafeMath for uint256;
    using Oracle for Oracle.Observation[65535];

    uint256 public constant MINIMUM_LIQUIDITY = 10 ** 3;
    uint256 public constant BASIS_POINTS = 10000;
    bytes4 private constant SELECTOR = bytes4(keccak256(bytes("transfer(address,uint256)")));

    // decimal points of token0
    uint256 public TOKEN0_DECIMALS;

    address public factory;
    address public token0;
    address public token1;

    uint112 private reserve0; // uses single storage slot, accessible via getReserves
    uint112 private reserve1; // uses single storage slot, accessible via getReserves
    uint32 private blockTimestampLast; // uses single storage slot, accessible via getReserves

    // the most recent price of token1/token0. Inherits decimals of token1.
    uint256 public lastPrice;
    // the most-recently updated index of the observations array
    uint16 public observationIndex;
    // the current maximum number of observations that are being stored
    uint16 public observationCardinality;
    // the next maximum number of observations to store, triggered in observations.write
    uint16 public observationCardinalityNext;

    Oracle.Observation[65535] public override observations;

    uint256 private unlocked = 1;

    modifier lock() {
        require(unlocked == 1, "MagicswapV2: LOCKED");
        unlocked = 0;
        _;
        unlocked = 1;
    }

    function getReserves() public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) {
        _reserve0 = reserve0;
        _reserve1 = reserve1;
        _blockTimestampLast = blockTimestampLast;
    }

    function _safeTransfer(address token, address to, uint256 value) private {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(SELECTOR, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), "MagicswapV2: TRANSFER_FAILED");
    }

    constructor() {
        factory = msg.sender;
    }

    // called once by the factory at time of deployment
    function initialize(address _token0, address _token1) external {
        require(msg.sender == factory, "MagicswapV2: FORBIDDEN"); // sufficient check
        token0 = _token0;
        token1 = _token1;

        TOKEN0_DECIMALS = UniswapV2ERC20(_token0).decimals();

        (uint16 cardinality, uint16 cardinalityNext) = observations.initialize(_blockTimestamp());

        observationIndex = 0;
        observationCardinality = cardinality;
        observationCardinalityNext = cardinalityNext;
    }

    function _blockTimestamp() internal view virtual returns (uint32) {
        return uint32(block.timestamp); // truncation is desired
    }

    /// @dev update reserves and, on the first call per block, price accumulators
    function _update(uint256 balance0, uint256 balance1, uint112 _reserve0, uint112 _reserve1) private {
        require(balance0 <= type(uint112).max && balance1 <= type(uint112).max, "MagicswapV2: OVERFLOW");

        uint32 blockTimestamp;
        uint32 timeElapsed;
        unchecked {
            blockTimestamp = uint32(block.timestamp % 2 ** 32);
            timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
        }

        if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
            // this is first trade of the block and reserves are not yet updated
            lastPrice = 10 ** TOKEN0_DECIMALS * _reserve1 / _reserve0;

            // write an oracle entry
            (observationIndex, observationCardinality) = observations.write(
                observationIndex, _blockTimestamp(), lastPrice, observationCardinality, observationCardinalityNext
            );
        }

        reserve0 = uint112(balance0);
        reserve1 = uint112(balance1);
        blockTimestampLast = blockTimestamp;
        emit Sync(reserve0, reserve1);
    }

    /// @dev Calculates fees and sends them to beneficiaries
    function _takeFees(uint256 balance0Adjusted, uint256 balance1Adjusted, uint256 amount0In, uint256 amount1In)
        internal
        returns (uint256 balance0, uint256 balance1)
    {
        (, address royaltiesBeneficiary, uint256 royaltiesFee, address protocolFeeBeneficiary, uint256 protocolFee) =
            IUniswapV2Factory(factory).getFeesAndRecipients(address(this));

        address _token0 = token0;
        address _token1 = token1;

        for (uint8 i = 0; i < 2; i++) {
            address feeToken = i == 0 ? _token0 : _token1;
            uint256 swapAmount = i == 0 ? amount0In : amount1In;

            if (swapAmount > 0) {
                uint256 royaltiesFeeAmount = swapAmount * royaltiesFee / BASIS_POINTS;

                // send royalties
                if (royaltiesFeeAmount > 0) {
                    _safeTransfer(feeToken, royaltiesBeneficiary, royaltiesFeeAmount);
                }

                uint256 protocolFeeAmount = swapAmount * protocolFee / BASIS_POINTS;

                // send protocol fee
                if (protocolFeeAmount > 0) {
                    _safeTransfer(feeToken, protocolFeeBeneficiary, protocolFeeAmount);
                }
            }
        }

        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        // Make sure that either balance does not go below adjusted balance used for K calcualtions.
        // If balances after fee transfers are above or equal adjusted balances then K still holds.
        require(balance0 >= balance0Adjusted / BASIS_POINTS, "MagicswapV2: balance0Adjusted");
        require(balance1 >= balance1Adjusted / BASIS_POINTS, "MagicswapV2: balance1Adjusted");
    }

    /// @dev this low-level function should be called from a contract which performs important safety checks
    function mint(address to) external lock returns (uint256 liquidity) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        uint256 balance0 = IERC20(token0).balanceOf(address(this));
        uint256 balance1 = IERC20(token1).balanceOf(address(this));
        uint256 amount0 = balance0.sub(_reserve0);
        uint256 amount1 = balance1.sub(_reserve1);

        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        if (_totalSupply == 0) {
            liquidity = UniswapV2Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
            _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
        } else {
            liquidity = UniswapV2Math.min(amount0.mul(_totalSupply) / _reserve0, amount1.mul(_totalSupply) / _reserve1);
        }
        require(liquidity > 0, "MagicswapV2: INSUFFICIENT_LIQUIDITY_MINTED");
        _mint(to, liquidity);

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Mint(msg.sender, amount0, amount1);
    }

    /// @dev this low-level function should be called from a contract which performs important safety checks
    function burn(address to) external lock returns (uint256 amount0, uint256 amount1) {
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        uint256 balance0 = IERC20(_token0).balanceOf(address(this));
        uint256 balance1 = IERC20(_token1).balanceOf(address(this));
        uint256 liquidity = balanceOf[address(this)];

        uint256 _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
        amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
        amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
        require(amount0 > 0 && amount1 > 0, "MagicswapV2: INSUFFICIENT_LIQUIDITY_BURNED");
        _burn(address(this), liquidity);
        _safeTransfer(_token0, to, amount0);
        _safeTransfer(_token1, to, amount1);
        balance0 = IERC20(_token0).balanceOf(address(this));
        balance1 = IERC20(_token1).balanceOf(address(this));

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Burn(msg.sender, amount0, amount1, to);
    }

    /// @dev this low-level function should be called from a contract which performs important safety checks
    /// @dev keeping bytes parameter for backward compatibility of the interface
    function swap(uint256 amount0Out, uint256 amount1Out, address to, bytes calldata) external lock {
        require(amount0Out > 0 || amount1Out > 0, "MagicswapV2: INSUFFICIENT_OUTPUT_AMOUNT");
        (uint112 _reserve0, uint112 _reserve1,) = getReserves(); // gas savings
        require(amount0Out < _reserve0 && amount1Out < _reserve1, "MagicswapV2: INSUFFICIENT_LIQUIDITY");

        uint256 balance0;
        uint256 balance1;
        {
            // scope for _token{0,1}, avoids stack too deep errors
            address _token0 = token0;
            address _token1 = token1;
            require(to != _token0 && to != _token1, "MagicswapV2: INVALID_TO");
            if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
            if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
            balance0 = IERC20(_token0).balanceOf(address(this));
            balance1 = IERC20(_token1).balanceOf(address(this));
        }
        uint256 amount0In = balance0 > _reserve0 - amount0Out ? balance0 - (_reserve0 - amount0Out) : 0;
        uint256 amount1In = balance1 > _reserve1 - amount1Out ? balance1 - (_reserve1 - amount1Out) : 0;
        require(amount0In > 0 || amount1In > 0, "MagicswapV2: INSUFFICIENT_INPUT_AMOUNT");

        {
            // scope for reserve{0,1}Adjusted, avoids stack too deep errors
            uint256 totalFee = IUniswapV2Factory(factory).getTotalFee(address(this));
            uint256 balance0Adjusted = balance0.mul(BASIS_POINTS).sub(amount0In.mul(totalFee));
            uint256 balance1Adjusted = balance1.mul(BASIS_POINTS).sub(amount1In.mul(totalFee));
            require(
                balance0Adjusted.mul(balance1Adjusted) >= uint256(_reserve0).mul(_reserve1).mul(BASIS_POINTS ** 2),
                "MagicswapV2: K"
            );
            (balance0, balance1) = _takeFees(balance0Adjusted, balance1Adjusted, amount0In, amount1In);
        }

        _update(balance0, balance1, _reserve0, _reserve1);
        emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
    }

    /// @dev Read TWAP price
    function observe(uint32[] calldata secondsAgos)
        external
        view
        override
        returns (uint256[] memory priceCumulatives)
    {
        return observations.observe(_blockTimestamp(), secondsAgos, lastPrice, observationIndex, observationCardinality);
    }

    /// @dev Increase number of data points for price history
    function increaseObservationCardinalityNext(uint16 _observationCardinalityNext) external override lock {
        uint16 observationCardinalityNextOld = observationCardinalityNext; // for the event
        uint16 observationCardinalityNextNew =
            observations.grow(observationCardinalityNextOld, _observationCardinalityNext);
        observationCardinalityNext = observationCardinalityNextNew;
        if (observationCardinalityNextOld != observationCardinalityNextNew) {
            emit IncreaseObservationCardinalityNext(observationCardinalityNextOld, observationCardinalityNextNew);
        }
    }

    /// @dev force balances to match reserves
    function skim(address to) external lock {
        address _token0 = token0; // gas savings
        address _token1 = token1; // gas savings
        _safeTransfer(_token0, to, IERC20(_token0).balanceOf(address(this)).sub(reserve0));
        _safeTransfer(_token1, to, IERC20(_token1).balanceOf(address(this)).sub(reserve1));
    }

    /// @dev force reserves to match balances
    function sync() external lock {
        _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
    }
}

