// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "../../core/interfaces/IUniswapV2Pair.sol";

/// @title Oracle library
/// @notice Provides functions to integrate with MagicswapV2 pool oracle
library OracleLibrary {
    /// @notice Fetches time-weighted average price using MagicswapV2 oracle
    /// @param pool Address of Uniswap V3 pool that we want to observe
    /// @param period Number of seconds in the past to start calculating time-weighted average
    /// @return timeWeightedAveragePrice The time-weighted average tick from (block.timestamp - period) to block.timestamp
    function consult(address pool, uint32 period) internal view returns (uint256 timeWeightedAveragePrice) {
        require(period != 0, "BP");

        uint32[] memory secondAgos = new uint32[](2);
        secondAgos[0] = period;
        secondAgos[1] = 0;

        uint256[] memory priceCumulatives = IUniswapV2Pair(pool).observe(secondAgos);
        uint256 priceCumulativesDelta = priceCumulatives[1] - priceCumulatives[0];

        timeWeightedAveragePrice = priceCumulativesDelta / period;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

// import {IContractDeployer} from "zksync-system-contracts/interfaces/IContractDeployer.sol";
import "../../core/interfaces/IUniswapV2Pair.sol";
import "../../core/interfaces/IUniswapV2Factory.sol";
import {UniswapV2Pair} from "../../core/UniswapV2Pair.sol";
import "../../core/libraries/SafeMath.sol";

/**
 * @notice Modified UniswapV2 to work with zksync stack based CREATE2
 **/
library UniswapV2Library {
    using SafeMath for uint256;

    /// @dev returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
    }

    // // calculates the CREATE2 address for a pair without making any external calls
    // function pairFor2(
    //     address factory,
    //     address tokenA,
    //     address tokenB
    // ) internal pure returns (address pair) {
    //     (address token0, address token1) = sortTokens(tokenA, tokenB);
    //     pair = address(uint160(
    //         uint256(
    //             keccak256(
    //                 abi.encodePacked(
    //                     bytes32(0x2020dba91b30cc0006188af794c2fb30dd8520db7e2c088b7fc7c103c00ca494), // keccak256("zksyncCreate2")
    //                     bytes32(uint256(uint160(0x4bC46e1fEA69E83E6adDc98462963093AAbC5226))), // sender
    //                     keccak256(abi.encodePacked(0x1282f34438cB205D201DD357398b85E7729Dd3a2, 0x892d81728eB3B33E463AEEdc2dC167130E74Be8c)), // salt
    //                     hex'0100065f2f2a556816a482652f101ddda2947216a5720dd91a79c61709cbf2b8', // init code hash
    //                     bytes32(0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470) // constructor input hash: keccak256("")
    //                 )
    //             )
    //         )
    //     ));
    // }

    /// @dev calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair) {
        // (address token0, address token1) = sortTokens(tokenA, tokenB);
        // bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        pair = tokenA;
        // pair = IContractDeployer(0x0000000000000000000000000000000000008006).getNewAddressCreate2(  
        //     factory,
        //     Utils.hashL2Bytecode(type(UniswapV2Pair).creationCode),
        //     salt,
        //     abi.encode("")
        // );
    }

    /// @dev fetches and sorts the reserves for a pair
    function getReserves(address factory, address tokenA, address tokenB)
        internal
        view
        returns (uint256 reserveA, uint256 reserveB)
    {
        (address token0,) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1,) = IUniswapV2Pair(pairFor(factory, tokenA, tokenB)).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    /// @dev given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function quote(uint256 amountA, uint256 reserveA, uint256 reserveB) internal pure returns (uint256 amountB) {
        require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
        require(reserveA > 0 && reserveB > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
        amountB = amountA.mul(reserveB) / reserveA;
    }

    /// @dev given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
    function getAmountOut(uint256 amountIn, uint256 reserveIn, uint256 reserveOut, address pair, address factory)
        internal
        view
        returns (uint256 amountOut)
    {
        require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
        require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
        uint256 totalFee = IUniswapV2Factory(factory).getTotalFee(pair);
        uint256 amountInWithFee = amountIn.mul(10000 - totalFee);
        uint256 numerator = amountInWithFee.mul(reserveOut);
        uint256 denominator = reserveIn.mul(10000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    /// @dev given an output amount of an asset and pair reserves, returns a required input amount of the other asset
    function getAmountIn(uint256 amountOut, uint256 reserveIn, uint256 reserveOut, address pair, address factory)
        internal
        view
        returns (uint256 amountIn)
    {
        require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
        require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
        uint256 totalFee = IUniswapV2Factory(factory).getTotalFee(pair);
        uint256 numerator = reserveIn.mul(amountOut).mul(10000);
        uint256 denominator = reserveOut.sub(amountOut).mul(10000 - totalFee);
        amountIn = (numerator / denominator).add(1);
    }

    /// @dev performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(address factory, uint256 amountIn, address[] memory path)
        internal
        view
        returns (uint256[] memory amounts)
    {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint[](path.length);
        amounts[0] = amountIn;
        for (uint256 i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(factory, path[i], path[i + 1]);
            amounts[i + 1] =
                getAmountOut(amounts[i], reserveIn, reserveOut, pairFor(factory, path[i], path[i + 1]), factory);
        }
    }

    /// @dev performs chained getAmountIn calculations on any number of pairs
    function getAmountsIn(address factory, uint256 amountOut, address[] memory path)
        internal
        view
        returns (uint256[] memory amounts)
    {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint[](path.length);
        amounts[amounts.length - 1] = amountOut;
        for (uint256 i = path.length - 1; i > 0; i--) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(factory, path[i - 1], path[i]);
            address pair = pairFor(factory, path[i - 1], path[i]);
            amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut, pair, factory);
        }
    }
}

contracts/UniswapV2/periphery/UniswapV2R.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/token/ERC20/IERC20.sol";

import "../core/interfaces/IUniswapV2Factory.sol";
import "../core/interfaces/IUniswapV2ERC20.sol";
import "../core/libraries/SafeMath.sol";
import "../libraries/TransferHelper.sol";

import "./interfaces/IUniswapV2Router01.sol";
import "./libraries/UniswapV2Library.sol";
import "./interfaces/IWETH.sol";

contract UniswapV2Router02 is IUniswapV2Router01 {
    using SafeMath for uint256;

    address public immutable override factory;
    address public immutable override WETH;


    modifier ensure(uint256 deadline) {
        if(deadline < block.timestamp) revert UniswapV2RouterExpired();
        _;
    }

    constructor(address _factory, address _WETH) {
        factory = _factory;
        WETH = _WETH;
    }

    receive() external payable {
        if(msg.sender != WETH) revert UniswapV2RouterOnlyAcceptETHViaFallbackFromWETHContract();
    }

    // **** ADD LIQUIDITY ****
    function _addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin
    ) internal virtual returns (uint256 amountA, uint256 amountB) {
        // create the pair if it doesn't exist yet
        if (IUniswapV2Factory(factory).getPair(tokenA, tokenB) == address(0)) {
            IUniswapV2Factory(factory).createPair(tokenA, tokenB);
        }
        (uint256 reserveA, uint256 reserveB) = UniswapV2Library.getReserves(factory, tokenA, tokenB);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
        } else {
            uint256 amountBOptimal = UniswapV2Library.quote(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                if(amountBOptimal < amountBMin) revert UniswapV2RouterInsufficientBAmount();
                (amountA, amountB) = (amountADesired, amountBOptimal);
            } else {
                uint256 amountAOptimal = UniswapV2Library.quote(amountBDesired, reserveB, reserveA);
                assert(amountAOptimal <= amountADesired);
                if(amountAOptimal < amountAMin) revert UniswapV2RouterInsufficientAAmount();
                (amountA, amountB) = (amountAOptimal, amountBDesired);
            }
        }
    }

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external virtual override ensure(deadline) returns (uint256 amountA, uint256 amountB, uint256 liquidity) {
        (amountA, amountB) = _addLiquidity(tokenA, tokenB, amountADesired, amountBDesired, amountAMin, amountBMin);
        address pair = UniswapV2Library.pairFor(factory, tokenA, tokenB);
        TransferHelper.safeTransferFrom(tokenA, msg.sender, pair, amountA);
        TransferHelper.safeTransferFrom(tokenB, msg.sender, pair, amountB);
        liquidity = IUniswapV2Pair(pair).mint(to);
    }

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external payable virtual override returns (uint256 amountToken, uint256 amountETH, uint256 liquidity) {
        (amountToken, amountETH, liquidity) =
            _addLiquidityETH(token, amountTokenDesired, amountTokenMin, amountETHMin, msg.sender, to, deadline);
    }

    function _addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address from,
        address to,
        uint256 deadline
    ) internal ensure(deadline) returns (uint256 amountToken, uint256 amountETH, uint256 liquidity) {
        (amountToken, amountETH) =
            _addLiquidity(token, WETH, amountTokenDesired, msg.value, amountTokenMin, amountETHMin);
        address pair = UniswapV2Library.pairFor(factory, token, WETH);
        TransferHelper.safeTransferFrom(token, from, pair, amountToken);
        IWETH(WETH).deposit{value: amountETH}();
        assert(IWETH(WETH).transfer(pair, amountETH));
        liquidity = IUniswapV2Pair(pair).mint(to);
        // refund dust eth, if any
        if (msg.value > amountETH) TransferHelper.safeTransferETH(to, msg.value - amountETH);
    }

    // **** REMOVE LIQUIDITY ****
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) public virtual override ensure(deadline) returns (uint256 amountA, uint256 amountB) {
        address pair = UniswapV2Library.pairFor(factory, tokenA, tokenB);
        IUniswapV2ERC20(pair).transferFrom(msg.sender, pair, liquidity); // send liquidity to pair
        (uint256 amount0, uint256 amount1) = IUniswapV2Pair(pair).burn(to);
        (address token0,) = UniswapV2Library.sortTokens(tokenA, tokenB);
        (amountA, amountB) = tokenA == token0 ? (amount0, amount1) : (amount1, amount0);
        if(amountA < amountAMin) revert UniswapV2RouterInsufficientAAmount();
        if(amountB < amountBMin) revert UniswapV2RouterInsufficientBAmount();
    }

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) public virtual override ensure(deadline) returns (uint256 amountToken, uint256 amountETH) {
        (amountToken, amountETH) =
            removeLiquidity(token, WETH, liquidity, amountTokenMin, amountETHMin, address(this), deadline);
        TransferHelper.safeTransfer(token, to, amountToken);
        IWETH(WETH).withdraw(amountETH);
        TransferHelper.safeTransferETH(to, amountETH);
    }

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external virtual override returns (uint256 amountA, uint256 amountB) {
        address pair = UniswapV2Library.pairFor(factory, tokenA, tokenB);
        uint256 value = approveMax ? type(uint256).max : liquidity;
        IUniswapV2ERC20(pair).permit(msg.sender, address(this), value, deadline, v, r, s);
        (amountA, amountB) = removeLiquidity(tokenA, tokenB, liquidity, amountAMin, amountBMin, to, deadline);
    }

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external virtual override returns (uint256 amountToken, uint256 amountETH) {
        address pair = UniswapV2Library.pairFor(factory, token, WETH);
        uint256 value = approveMax ? type(uint256).max : liquidity;
        IUniswapV2ERC20(pair).permit(msg.sender, address(this), value, deadline, v, r, s);
        (amountToken, amountETH) = removeLiquidityETH(token, liquidity, amountTokenMin, amountETHMin, to, deadline);
    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(uint256[] memory amounts, address[] memory path, address _to) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = UniswapV2Library.sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) =
                input == token0 ? (uint256(0), amountOut) : (amountOut, uint256(0));
            address to = i < path.length - 2 ? UniswapV2Library.pairFor(factory, output, path[i + 2]) : _to;
            IUniswapV2Pair(UniswapV2Library.pairFor(factory, input, output)).swap(
                amount0Out, amount1Out, to, new bytes(0)
            );
        }
    }

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] memory path,
        address to,
        uint256 deadline
    ) public virtual override returns (uint256[] memory amounts) {
        amounts = _swapExactTokensForTokens(amountIn, amountOutMin, path, msg.sender, to, deadline);
    }

    function _swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] memory path,
        address from,
        address to,
        uint256 deadline
    ) internal ensure(deadline) returns (uint256[] memory amounts) {
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        if(amounts[amounts.length - 1] < amountOutMin) revert UniswapV2RouterInsufficientOutputAmount();
        TransferHelper.safeTransferFrom(path[0], from, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]);
        _swap(amounts, path, to);
    }

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external virtual override returns (uint256[] memory amounts) {
        amounts = _swapTokensForExactTokens(amountOut, amountInMax, path, msg.sender, to, deadline);
    }

    function _swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address from,
        address to,
        uint256 deadline
    ) internal ensure(deadline) returns (uint256[] memory amounts) {
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        if(amounts[0] > amountInMax) revert UniswapV2RouterExcessiveInputAmount();
        TransferHelper.safeTransferFrom(path[0], from, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]);
        _swap(amounts, path, to);
    }

    function swapExactETHForTokens(uint256 amountOutMin, address[] calldata path, address to, uint256 deadline)
        external
        payable
        virtual
        override
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        if(path[0] != WETH) revert UniswapV2RouterInvalidPath();
        amounts = UniswapV2Library.getAmountsOut(factory, msg.value, path);
        if(amounts[amounts.length - 1] < amountOutMin) revert UniswapV2RouterInsufficientOutputAmount();
        IWETH(WETH).deposit{value: amounts[0]}();
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, to);
    }

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external virtual override ensure(deadline) returns (uint256[] memory amounts) {
        if(path[path.length - 1] != WETH) revert UniswapV2RouterInvalidPath();
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        if(amounts[0] > amountInMax) revert UniswapV2RouterExcessiveInputAmount();
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
    }

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external virtual override ensure(deadline) returns (uint256[] memory amounts) {
        if(path[path.length - 1] != WETH) revert UniswapV2RouterInvalidPath();
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path);
        if(amounts[amounts.length - 1] < amountOutMin) revert UniswapV2RouterInsufficientOutputAmount();
        TransferHelper.safeTransferFrom(
            path[0], msg.sender, UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]
        );
        _swap(amounts, path, address(this));
        IWETH(WETH).withdraw(amounts[amounts.length - 1]);
        TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
    }

    function swapETHForExactTokens(uint256 amountOut, address[] calldata path, address to, uint256 deadline)
        public
        payable
        virtual
        override
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        if(path[0] != WETH) revert UniswapV2RouterInvalidPath();
        amounts = UniswapV2Library.getAmountsIn(factory, amountOut, path);
        if(amounts[0] > msg.value) revert UniswapV2RouterExcessiveInputAmount();
        IWETH(WETH).deposit{value: amounts[0]}();
        assert(IWETH(WETH).transfer(UniswapV2Library.pairFor(factory, path[0], path[1]), amounts[0]));
        _swap(amounts, path, to);
        // refund dust eth, if any
        if (msg.value > amounts[0]) TransferHelper.safeTransferETH(msg.sender, msg.value - amounts[0]);
    }

    // **** LIBRARY FUNCTIONS ****
    function quote(uint256 amountA, uint256 reserveA, uint256 reserveB)
        public
        pure
        virtual
        override
        returns (uint256 amountB)
    {
        return UniswapV2Library.quote(amountA, reserveA, reserveB);
    }

    function getAmountOut(uint256 amountIn, uint256 reserveIn, uint256 reserveOut, address pair)
        public
        view
        virtual
        override
        returns (uint256 amountOut)
    {
        return UniswapV2Library.getAmountOut(amountIn, reserveIn, reserveOut, pair, factory);
    }

    function getAmountIn(uint256 amountOut, uint256 reserveIn, uint256 reserveOut, address pair)
        public
        view
        virtual
        override
        returns (uint256 amountIn)
    {
        return UniswapV2Library.getAmountIn(amountOut, reserveIn, reserveOut, pair, factory);
    }

    function getAmountsOut(uint256 amountIn, address[] memory path)
        public
        view
        virtual
        override
        returns (uint256[] memory amounts)
    {
        return UniswapV2Library.getAmountsOut(factory, amountIn, path);
    }

    function getAmountsIn(uint256 amountOut, address[] memory path)
        public
        view
        virtual
        override
        returns (uint256[] memory amounts)
    {
        return UniswapV2Library.getAmountsIn(factory, amountOut, path);
    }
}

contracts/Vault/INftVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

/// @title Vault contract for wrapping NFTs (ERC721/ERC1155) to ERC20
interface INftVault {
    enum NftType {
        ERC721,
        ERC1155
    }

    /// @notice Vault configuration struct that specifies which NFTs are accepted in vault.
    /// @param addr address of nft contract
    /// @param nftType standard that NFT supports { ERC721, ERC1155 }
    /// @param allowAllIds if true, all tokens are allowed in the vault. If false, tokenIds must be
    ///        listed one by one.
    /// @param tokenIds list of tokens supported by vault. If allowAllIds is true, list must be empty.
    struct CollectionData {
        address addr;
        NftType nftType;
        bool allowAllIds;
        uint256[] tokenIds;
    }

    /// @notice Struct for allowed tokens. Stores data in an optimized way to read it in vault.
    /// @param tokenIds mapping from tokenid to is-allowed
    /// @param tokenIdList list of all tokens that are allowed
    /// @param allowAllIds if true, all tokens are allowed
    struct AllowedTokenIds {
        mapping(uint256 => bool) tokenIds;
        uint256[] tokenIdList;
        bool allowAllIds;
    }

    /// @notice Emitted during initiation when collection added to allowed list
    /// @param collection collection details
    event CollectionAllowed(CollectionData collection);

    /// @notice Emitted on depositing NFT to vault
    /// @param to address that gets vault ERC20 tokens
    /// @param collection NFT address that is deposited
    /// @param tokenId token id that is deposited
    /// @param amount amount of token that is deposited, for ERC721 always 1
    event Deposit(address indexed to, address indexed collection, uint256 tokenId, uint256 amount);

    /// @notice Emitted on withdrawing NFT from vault
    /// @param to address that gets withdrawn NFTs
    /// @param collection NFT address that is withdrawn
    /// @param tokenId token id that is withdrawn
    /// @param amount amount of token that is withdrawn, for ERC721 always 1
    event Withdraw(address indexed to, address indexed collection, uint256 tokenId, uint256 amount);

    /// @dev Contract is already initialized
    error Initialized();
    /// @dev Collection data is empty
    error InvalidCollections();
    /// @dev Collection already added
    error DuplicateCollection();
    /// @dev Token id is listed twice in CollectionData.tokenIds array
    error TokenIdAlreadySet();
    /// @dev Token ids in CollectionData.tokenIds array are not sorted
    error TokenIdsMustBeSorted();
    /// @dev ERC165 suggests that NFT is supporting ERC721 but ERC1155 is claimed
    error ExpectedERC721();
    /// @dev ERC165 suggests that NFT is supporting ERC1155 but ERC721 is claimed
    error ExpectedERC1155();
    /// @dev Collection does not support all token IDs however list of IDs is empty.
    ///      CollectionData.tokenIds is empty and CollectionData.allowAllIds is false.
    error MissingTokenIds();
    /// @dev CollectionData.tokenIds is not empty however Collection supports all token IDs.
    error TokenIdsMustBeEmpty();
    /// @dev Token is not allowed in vault
    error DisallowedToken();
    /// @dev Token amount is invalid eg. amount == 0
    error WrongAmount();
    /// @dev Token amount is invalid for ERC721, amount != 1
    error WrongERC721Amount();
    /// @dev Trying to interact with token that does not support ERC721 nor ERC1155
    error UnsupportedNft();
    /// @dev Token is allowed in vault but must not be
    error MustBeDisallowedToken();

    /// @notice value of 1 token, including decimals
    function ONE() external view returns (uint256);

    /// @notice amount of token required for last NFT to be redeemed
    function LAST_NFT_AMOUNT() external view returns (uint256);

    /// @notice unique id of the vault generated using its configuration
    function VAULT_HASH() external view returns (bytes32);

    /// @notice Initialize Vault with collection config
    /// @dev Called by factory during deployment
    /// @param collections struct array of allowed collections and token IDs
    function init(CollectionData[] memory collections) external;

    /// @notice Returns hash of vault configuration
    /// @param collections struct array of allowed collections and token IDs
    /// @return configuration hash
    function hashVault(CollectionData[] memory collections) external pure returns (bytes32);

    /// @notice Returns balances of NFT deposited to the vault
    /// @param collectionAddr NFT address
    /// @param tokenId NFT's token ID
    /// @return amount amount of NFT deposited to the vault
    function balances(address collectionAddr, uint256 tokenId) external view returns (uint256 amount);

    /// @notice Get array of NFT addresses that are allowed to be deposited to the vault
    /// @dev Keep in mind that returned address(es) can be further restricted on token ID level
    /// @return collections array of NFT addresses that are allowed to be deposited to the vault
    function getAllowedCollections() external view returns (address[] memory collections);

    /// @return number of NFT addresses that are allowed to be deposited to the vault
    function getAllowedCollectionsLength() external view returns (uint256);

    /// @notice Get details of allowed collection
    /// @return struct with details of allowed collection
    function getAllowedCollectionData(address collectionAddr) external view returns (CollectionData memory);

    /// @notice Validates type of collection (ERC721 or ERC1155)
    /// @dev It uses ERC165 to check interface support. If support can not be detected without doubt, user input is trusted.
    /// @param collectionAddr NFT address
    /// @param nftType NFT type, ERC721 or ERC1155
    /// @return validatedNftType returns validated enum NftType as uint256
    function validateNftType(address collectionAddr, NftType nftType)
        external
        view
        returns (uint256 validatedNftType);

    /// @notice Returns if true token can be deposited
    /// @param collection NFT address
    /// @param tokenId NFT token ID
    /// @return true if allowed
    function isTokenAllowed(address collection, uint256 tokenId) external view returns (bool);

    /// @notice Returns balance of token sent to the vault
    /// @dev Reads balance of tokens freshy sent to the vault
    /// @param collection NFT address
    /// @param tokenId NFT token ID
    /// @return balance of sent token, for ERC721 it's always 1
    function getSentTokenBalance(address collection, uint256 tokenId) external view returns (uint256);

    /// @notice Deposit NFT to vault
    /// @dev This low-level function should be called from a contract which performs important safety checks
    /// @param to address that gets minted ERC20 token
    /// @param collection address of deposited NFT
    /// @param tokenId token ID of deposited NFT
    /// @param amount amount of deposited NFT, for ERC721 it's always 1
    /// @return amountMinted amount of minted ERC20 token
    function deposit(address to, address collection, uint256 tokenId, uint256 amount)
        external
        returns (uint256 amountMinted);

    /// @notice Deposit NFTs to vault
    /// @dev This low-level function should be called from a contract which performs important safety checks
    /// @param to address that gets minted ERC20 token
    /// @param collection array of addresses of deposited NFTs
    /// @param tokenId array of token IDs of deposited NFTs
    /// @param amount array if amounts of deposited NFTs, for ERC721 it's always 1
    /// @return amountMinted amount of minted ERC20 token
    function depositBatch(address to, address[] memory collection, uint256[] memory tokenId, uint256[] memory amount)
        external
        returns (uint256 amountMinted);

    /// @notice Withdraw NFT from vault
    /// @dev This low-level function should be called from a contract which performs important safety checks
    /// @param to address that gets NFT
    /// @param collection address of NFT to withdraw
    /// @param tokenId token ID of NFT to withdraw
    /// @param amount amount of NFT to withdraw, for ERC721 it's always 1
    /// @return amountBurned amount of burned ERC20
    function withdraw(address to, address collection, uint256 tokenId, uint256 amount)
        external
        returns (uint256 amountBurned);

    /// @notice Withdraw NFTs from vault
    /// @dev This low-level function should be called from a contract which performs important safety checks
    /// @param to address that gets NFT
    /// @param collection array of addresses of NFTs to withdraw
    /// @param tokenId array of token IDs of NFTs to withdraw
    /// @param amount array of amounts of NFTs to withdraw, for ERC721 it's always 1
    /// @return amountBurned amount of burned ERC20
    function withdrawBatch(address to, address[] memory collection, uint256[] memory tokenId, uint256[] memory amount)
        external
        returns (uint256 amountBurned);

    /// @notice Allow anyone to withdraw tokens sent to this vault by accident
    ///         Only unsupported NFTs can be skimmed.
    /// @param to address that gets NFT
    /// @param nftType NftType of skimmed NFT
    /// @param collection address of NFT to skim
    /// @param tokenId token ID of NFT to skim
    /// @param amount amount of NFT to skim, for ERC721 it's always 1
    function skim(address to, NftType nftType, address collection, uint256 tokenId, uint256 amount) external;
}

contracts/Vault/INftVaultFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./INftVault.sol";

/// @title Vault factory contract
interface INftVaultFactory {
    /// @notice Emitted when new vault is deployed
    /// @param name vault's name
    /// @param symbol vault's name
    /// @param vault vault's address as INftVault
    /// @param vaultId vault's index in `vaults` AddressSet
    /// @param collections configuration used for vault creation
    /// @param creator address of vault creator
    event VaultCreated(
        string name,
        string symbol,
        INftVault indexed vault,
        uint256 indexed vaultId,
        INftVault.CollectionData[] collections,
        address creator
    );

    /// @dev Vault does not exist
    error VaultDoesNotExist();

    /// @dev Vault with identical configuration is already deployed
    error VaultAlreadyDeployed();

    /// @notice Get vault by its config hash
    /// @param hash vault's config hash
    /// @return vault address
    function vaultHashMap(bytes32 hash) external view returns (INftVault vault);

    /// @return all deployed vaults
    function getAllVaults() external view returns (address[] memory);

    /// @notice Get vault by its EnumerableSet vaultId
    /// @param index vaultId or index in NftVaultFactory.vaults array
    /// @return vault address
    function getVaultAt(uint256 index) external view returns (address);

    /// @return length of vault's EnumerableSet
    function getVaultLength() external view returns (uint256);

    /// @notice Returns true if vault has been deployed by factory
    /// @param vault address
    /// @return true if vault is deployed by the factory
    function isVault(address vault) external view returns (bool);

    /// @notice Get vault by it's config
    /// @param collections vault's config
    /// @return vault address
    function getVault(INftVault.CollectionData[] memory collections) external view returns (INftVault vault);

    /// @notice Returns true if vault with given config exists
    /// @param collections vault's config
    /// @return true if vault with given config exists
    function exists(INftVault.CollectionData[] memory collections) external view returns (bool);

    /// @notice Get config hash
    /// @param collections vault's config
    /// @return config hash
    function hashVault(INftVault.CollectionData[] memory collections) external pure returns (bytes32);

    /// @notice Create new vault
    /// @dev If vault already exists, function reverts
    /// @param collections vault's config
    /// @return vault address of deployed vault
    function createVault(INftVault.CollectionData[] memory collections) external returns (INftVault vault);
}

contracts/Vault/NftVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/token/ERC20/ERC20.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC721/IERC721.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC721/utils/ERC721Holder.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC1155/IERC1155.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "lib/openzeppelin-contracts/contracts/utils/structs/EnumerableSet.sol";
import "lib/openzeppelin-contracts/contracts/utils/structs/EnumerableMap.sol";
import "lib/openzeppelin-contracts/contracts/utils/introspection/ERC165Checker.sol";
import "lib/openzeppelin-contracts/contracts/access/Ownable2Step.sol";
import "lib/openzeppelin-contracts/contracts/utils/Address.sol";

import "./INftVault.sol";
import "./INftVaultFactory.sol";

contract NftVault is INftVault, ERC20, ERC721Holder, ERC1155Holder {
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    /// @notice value of 1 token, including decimals
    uint256 public immutable ONE;

    /// @notice amount of token required for last NFT to be redeemed
    uint256 public immutable LAST_NFT_AMOUNT;

    /// @notice unique ID of the vault generated using its configuration
    bytes32 public VAULT_HASH;

    /// @notice maps collection address to nft type
    EnumerableMap.AddressToUintMap private allowedCollections;

    /// @notice maps collection address to allowed tokens
    mapping(address => AllowedTokenIds) private allowedTokenIds;

    /// @notice maps collection address to tokenId to amount wrapped
    mapping(address => mapping(uint256 => uint256)) public balances;

    /// @param _name name of ERC20 Vault token
    /// @param _symbol symbol of ERC20 Vault token
    constructor(string memory _name, string memory _symbol) ERC20(_name, _symbol) {
        ONE = 10 ** decimals();
        /// @dev last NFT can be redeemed for 99.9%
        LAST_NFT_AMOUNT = ONE * 999 / 1000;
    }

    /// @inheritdoc INftVault
    function init(CollectionData[] memory _collections) external {
        if (_collections.length == 0) revert InvalidCollections();
        if (allowedCollections.length() > 0) revert Initialized();

        VAULT_HASH = hashVault(_collections);

        for (uint256 i = 0; i < _collections.length; i++) {
            CollectionData memory collection = _collections[i];

            /// @dev if all Ids are allowed tokenIds must be empty, otherwise VAULT_HASH will not be correct
            if (collection.allowAllIds && collection.tokenIds.length > 0) revert TokenIdsMustBeEmpty();

            uint256 nftType = validateNftType(collection.addr, collection.nftType);

            if (!allowedCollections.set(collection.addr, nftType)) revert DuplicateCollection();
            allowedTokenIds[collection.addr].allowAllIds = collection.allowAllIds;

            emit CollectionAllowed(collection);

            if (collection.allowAllIds) continue;
            if (collection.tokenIds.length == 0) revert MissingTokenIds();

            uint256 lastTokenId = 0;

            for (uint256 j = 0; j < collection.tokenIds.length; j++) {
                uint256 tokenId = collection.tokenIds[j];

                /// @dev Make sure `uint256[] tokenIds` array is sorted,
                ///      otherwise VAULT_HASH will not be correct
                if (tokenId < lastTokenId) {
                    revert TokenIdsMustBeSorted();
                } else {
                    lastTokenId = tokenId;
                }

                /// @dev Check for duplicates
                if (allowedTokenIds[collection.addr].tokenIds[tokenId]) revert TokenIdAlreadySet();

                allowedTokenIds[collection.addr].tokenIds[tokenId] = true;
                allowedTokenIds[collection.addr].tokenIdList.push(tokenId);
            }
        }
    }

    /// @inheritdoc INftVault
    function hashVault(INftVault.CollectionData[] memory _collections) public pure returns (bytes32) {
        return keccak256(abi.encode(_collections));
    }

    /// @inheritdoc INftVault
    function getAllowedCollections() external view returns (address[] memory collections) {
        collections = new address[](allowedCollections.length());

        for (uint256 i = 0; i < collections.length; i++) {
            (address addr,) = allowedCollections.at(i);
            collections[i] = addr;
        }
    }

    /// @inheritdoc INftVault
    function getAllowedCollectionsLength() external view returns (uint256) {
        return allowedCollections.length();
    }

    /// @inheritdoc INftVault
    function getAllowedCollectionData(address _collectionAddr) external view returns (CollectionData memory) {
        return CollectionData({
            addr: _collectionAddr,
            nftType: NftType(allowedCollections.get(_collectionAddr)),
            allowAllIds: allowedTokenIds[_collectionAddr].allowAllIds,
            tokenIds: allowedTokenIds[_collectionAddr].tokenIdList
        });
    }

    /// @inheritdoc INftVault
    function validateNftType(address _collectionAddr, NftType _nftType) public view returns (uint256 nftType) {
        bool supportsERC721 = ERC165Checker.supportsInterface(_collectionAddr, type(IERC721).interfaceId);
        bool supportsERC1155 = ERC165Checker.supportsInterface(_collectionAddr, type(IERC1155).interfaceId);

        /// @dev if `_collectionAddr` supports both or neither token standard, trust user input
        ///      if `_collectionAddr` supports one of the token standards, NftType must match it
        if (supportsERC721 && !supportsERC1155 && _nftType != NftType.ERC721) revert ExpectedERC721();
        if (supportsERC1155 && !supportsERC721 && _nftType != NftType.ERC1155) revert ExpectedERC1155();

        nftType = uint256(_nftType);
    }

    /// @inheritdoc INftVault
    function isTokenAllowed(address _collection, uint256 _tokenId) public view returns (bool) {
        (bool isCollectionAllowed,) = allowedCollections.tryGet(_collection);

        return isCollectionAllowed
            && (allowedTokenIds[_collection].allowAllIds || allowedTokenIds[_collection].tokenIds[_tokenId]);
    }

    /// @inheritdoc INftVault
    function getSentTokenBalance(address _collection, uint256 _tokenId) public view returns (uint256) {
        uint256 currentBalance = balances[_collection][_tokenId];
        NftType nftType = NftType(allowedCollections.get(_collection));

        if (nftType == NftType.ERC721) {
            if (currentBalance == 0 && IERC721(_collection).ownerOf(_tokenId) == address(this)) {
                return 1;
            } else {
                return 0;
            }
        } else if (nftType == NftType.ERC1155) {
            return IERC1155(_collection).balanceOf(address(this), _tokenId) - currentBalance;
        } else {
            revert UnsupportedNft();
        }
    }

    /// @inheritdoc INftVault
    function deposit(address _to, address _collection, uint256 _tokenId, uint256 _amount)
        public
        returns (uint256 amountMinted)
    {
        if (!isTokenAllowed(_collection, _tokenId)) revert DisallowedToken();

        uint256 sentTokenBalance = getSentTokenBalance(_collection, _tokenId);
        if (_amount == 0 || sentTokenBalance < _amount) revert WrongAmount();

        balances[_collection][_tokenId] += _amount;
        emit Deposit(_to, _collection, _tokenId, _amount);

        amountMinted = ONE * _amount;
        uint256 totalSupply_ = totalSupply();

        /// @dev If vault ERC20 supply is "0 < totalSupply <= 0.01" it means that vault has been emptied and there
        ///      is leftover ERC20 token (most likely) locked in the univ2 pair. To prevent minting small amounts
        ///      of unbacked ERC20 tokens in a loop, which can lead to unexpected behaviour, vault mints
        ///      `ONE - totalSupply` amount of ERC20 token for the first NFT that is deposited after the vault was
        ///      emptied. This allows for the vault and univ2 pair to be reused safely.
        if (totalSupply_ > 0 && totalSupply_ <= ONE - LAST_NFT_AMOUNT) {
            amountMinted -= totalSupply_;
        }

        _mint(_to, amountMinted);
    }

    /// @inheritdoc INftVault
    function depositBatch(
        address _to,
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount
    ) external returns (uint256 amountMinted) {
        for (uint256 i = 0; i < _collection.length; i++) {
            amountMinted += deposit(_to, _collection[i], _tokenId[i], _amount[i]);
        }
    }

    /// @inheritdoc INftVault
    function withdraw(address _to, address _collection, uint256 _tokenId, uint256 _amount)
        public
        returns (uint256 amountBurned)
    {
        if (_amount == 0 || balances[_collection][_tokenId] < _amount) revert WrongAmount();

        balances[_collection][_tokenId] -= _amount;
        amountBurned = ONE * _amount;

        // when withdrawing the last NFT from the vault, allow redeemeing for LAST_NFT_AMOUNT instead of ONE
        if (totalSupply() == amountBurned && balanceOf(address(this)) >= amountBurned - ONE + LAST_NFT_AMOUNT) {
            amountBurned = balanceOf(address(this));
        }

        _burn(address(this), amountBurned);

        NftType nftType = NftType(allowedCollections.get(_collection));
        if (nftType == NftType.ERC721) {
            if (_amount != 1) revert WrongERC721Amount();

            IERC721(_collection).safeTransferFrom(address(this), _to, _tokenId);
        } else if (nftType == NftType.ERC1155) {
            IERC1155(_collection).safeTransferFrom(address(this), _to, _tokenId, _amount, bytes(""));
        } else {
            revert UnsupportedNft();
        }

        emit Withdraw(_to, _collection, _tokenId, _amount);
    }

    /// @inheritdoc INftVault
    function withdrawBatch(
        address _to,
        address[] memory _collection,
        uint256[] memory _tokenId,
        uint256[] memory _amount
    ) external returns (uint256 amountBurned) {
        for (uint256 i = 0; i < _collection.length; i++) {
            amountBurned += withdraw(_to, _collection[i], _tokenId[i], _amount[i]);
        }
    }

    /// @inheritdoc INftVault
    function skim(address _to, NftType nftType, address _collection, uint256 _tokenId, uint256 _amount) external {
        // Cannot skim supported token
        if (isTokenAllowed(_collection, _tokenId)) revert MustBeDisallowedToken();

        if (nftType == NftType.ERC721) {
            IERC721(_collection).safeTransferFrom(address(this), _to, _tokenId);
        } else if (nftType == NftType.ERC1155) {
            IERC1155(_collection).safeTransferFrom(address(this), _to, _tokenId, _amount, bytes(""));
        } else {
            revert UnsupportedNft();
        }
    }
}

contracts/Vault/NftVaultFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "lib/openzeppelin-contracts/contracts/utils/structs/EnumerableSet.sol";
import "lib/openzeppelin-contracts/contracts/utils/Strings.sol";

import "./INftVaultFactory.sol";
import "./NftVault.sol";

contract NftVaultFactory is INftVaultFactory {
    using EnumerableSet for EnumerableSet.AddressSet;
    using Strings for uint256;

    EnumerableSet.AddressSet private vaults;

    mapping(bytes32 => INftVault) public vaultHashMap;
    mapping(INftVault => uint256) public vaultIdMap;

    /// @inheritdoc INftVaultFactory
    function getAllVaults() external view returns (address[] memory) {
        return vaults.values();
    }

    /// @inheritdoc INftVaultFactory
    function getVaultAt(uint256 _index) external view returns (address) {
        return vaults.at(_index);
    }

    /// @inheritdoc INftVaultFactory
    function getVaultLength() external view returns (uint256) {
        return vaults.length();
    }

    /// @inheritdoc INftVaultFactory
    function isVault(address _vault) external view returns (bool) {
        return vaults.contains(_vault);
    }

    /// @inheritdoc INftVaultFactory
    function getVault(INftVault.CollectionData[] memory _collections) public view returns (INftVault vault) {
        vault = vaultHashMap[hashVault(_collections)];
        if (address(vault) == address(0)) revert VaultDoesNotExist();
    }

    /// @inheritdoc INftVaultFactory
    function exists(INftVault.CollectionData[] memory _collections) public view returns (bool) {
        return address(vaultHashMap[hashVault(_collections)]) != address(0);
    }

    /// @inheritdoc INftVaultFactory
    function hashVault(INftVault.CollectionData[] memory _collections) public pure returns (bytes32) {
        return keccak256(abi.encode(_collections));
    }

    /// @inheritdoc INftVaultFactory
    function createVault(INftVault.CollectionData[] memory _collections) external returns (INftVault vault) {
        bytes32 vaultHash = hashVault(_collections);
        vault = INftVault(vaultHashMap[vaultHash]);

        // if vault with _collections alredy exists, revert
        if (address(vault) != address(0)) revert VaultAlreadyDeployed();

        uint256 vaultId;
        string memory name;
        string memory symbol;

        vaultId = vaults.length();
        name = string.concat("Magic Vault ", vaultId.toString());
        symbol = string.concat("MagicVault", vaultId.toString());

        vault = INftVault(address(new NftVault(name, symbol)));
        vault.init(_collections);

        vaults.add(address(vault));
        vaultHashMap[vaultHash] = vault;
        vaultIdMap[vault] = vaultId;

        emit VaultCreated(name, symbol, vault, vaultId, _collections, msg.sender);
    }
}

contracts/Vault/test/NftVault.t.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/ERC721Mintable.sol";
import "lib/ERC1155Mintable.sol";

import "../INftVault.sol";
import "../NftVault.sol";
import "../NftVaultFactory.sol";

contract NftVaultTest is Test {
    NftVaultFactory public nftVaultFactory = new NftVaultFactory();

    address user1 = address(1001);
    address user2 = address(1002);
    address owner = address(1003);
    address erc721and1155 = address(888999);

    uint256[] public erc721tokenIds = [1, 6, 15, 22];
    uint256[] public erc721tokenIdsUnsorted = [1, 6, 16, 15, 22];
    uint256[] public erc721tokenIdsDuplicated = [1, 6, 15, 15, 22];
    uint256[] public erc1155tokenIds = [8, 21, 32, 33, 35];

    INftVault.CollectionData public collectionERC721all;
    INftVault.CollectionData public collectionERC1155all;
    INftVault.CollectionData public collectionERC721allowed;
    INftVault.CollectionData public collectionERC1155allowed;
    INftVault.CollectionData public collectionAllWithTokenIds;
    INftVault.CollectionData public collectionERC721allWrongNftType;
    INftVault.CollectionData public collectionERC1155allWrongNftType;
    INftVault.CollectionData public collectionERC721allowedMissingTokens;
    INftVault.CollectionData public collectionERC721allowedUnsortedTokens;
    INftVault.CollectionData public collectionERC721allowedDuplicatedTokens;

    INftVault.CollectionData[] public collections;

    event Deposit(address indexed to, address indexed collection, uint256 tokenId, uint256 amount);

    function setUp() public {
        collectionERC721all = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC1155all = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowed = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIds
        });

        collectionERC1155allowed = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: false,
            tokenIds: erc1155tokenIds
        });

        collectionAllWithTokenIds = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: erc1155tokenIds
        });

        collectionERC721allWrongNftType = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC1155allWrongNftType = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowedMissingTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowedUnsortedTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIdsUnsorted
        });

        collectionERC721allowedDuplicatedTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIdsDuplicated
        });
    }

    function _getConfig(uint256 configId) public returns (INftVault.CollectionData[] memory) {
        delete collections;

        // deploy fresh NFTs at every config request
        collectionERC721all.addr = address(new ERC721Mintable());
        collectionERC1155all.addr = address(new ERC1155Mintable());
        collectionERC721allowed.addr = address(new ERC721Mintable());
        collectionERC1155allowed.addr = address(new ERC1155Mintable());

        if (configId == 0) {
            collections.push(collectionERC721all);
        } else if (configId == 1) {
            collections.push(collectionERC1155all);
        } else if (configId == 2) {
            collections.push(collectionERC721allowed);
        } else if (configId == 3) {
            collections.push(collectionERC1155allowed);
        } else if (configId == 4) {
            collections.push(collectionERC721all);
            collections.push(collectionERC1155all);
        } else if (configId == 5) {
            collections.push(collectionERC721allowed);
            collections.push(collectionERC1155allowed);
        } else if (configId == 6) {
            collections.push(collectionERC721all);
            collections.push(collectionERC1155allowed);
        } else if (configId == 7) {
            collections.push(collectionERC721allowed);
            collections.push(collectionERC1155all);
        } else if (configId == 8) {
            collections.push(collectionERC721all);
            collections.push(collectionERC721all);
        } else {
            revert("WrongConfig");
        }

        return collections;
    }

    function _deployTestVault(uint256 configId)
        public
        returns (NftVault vault, INftVault.CollectionData[] memory _collections)
    {
        _collections = _getConfig(configId);
        vault = NftVault(address(nftVaultFactory.createVault(_collections)));
    }

    function _getAddressesFromCollections(INftVault.CollectionData[] memory _collections)
        public
        view
        returns (address[] memory addresses)
    {
        addresses = new address[](_collections.length);

        for (uint256 i = 0; i < collections.length; i++) {
            addresses[i] = _collections[i].addr;
        }
    }

    function testInitReverts() public {
        NftVault nftVault;

        delete collections;
        vm.expectRevert(INftVault.InvalidCollections.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionAllWithTokenIds);
        vm.expectRevert(INftVault.TokenIdsMustBeEmpty.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionERC721allWrongNftType);
        vm.expectRevert(INftVault.ExpectedERC721.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionERC1155allWrongNftType);
        vm.expectRevert(INftVault.ExpectedERC1155.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionERC721allowedMissingTokens);
        vm.expectRevert(INftVault.MissingTokenIds.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionERC721allowedUnsortedTokens);
        vm.expectRevert(INftVault.TokenIdsMustBeSorted.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        collections.push(collectionERC721allowedDuplicatedTokens);
        vm.expectRevert(INftVault.TokenIdAlreadySet.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        delete collections;
        INftVault.CollectionData[] memory _collections = _getConfig(8);
        vm.expectRevert(INftVault.DuplicateCollection.selector);
        nftVault = NftVault(address(nftVaultFactory.createVault(_collections)));

        nftVault = new NftVault("name", "symbol");

        assertEq(nftVault.VAULT_HASH(), bytes32(0));
        assertEq(nftVault.getAllowedCollections(), new address[](0));
        assertEq(nftVault.getAllowedCollectionsLength(), 0);
        assertEq(nftVault.ONE(), 10 ** nftVault.decimals());
        assertEq(nftVault.ONE(), 1e18);
        assertEq(nftVault.name(), "name");
        assertEq(nftVault.symbol(), "symbol");
    }

    function testInitStorage() public {
        for (uint256 configId = 0; configId < 8; configId++) {
            console2.log("configId", configId);
            INftVault.CollectionData[] memory _collections = _getConfig(configId);
            NftVault vault = NftVault(address(nftVaultFactory.createVault(_collections)));

            assertEq(vault.hashVault(_collections), vault.VAULT_HASH());
            assertEq(vault.getAllowedCollections(), _getAddressesFromCollections(_collections));
            assertEq(vault.getAllowedCollectionsLength(), _collections.length);

            for (uint256 i = 0; i < _collections.length; i++) {
                INftVault.CollectionData memory c = vault.getAllowedCollectionData(_collections[i].addr);
                assertEq(c.addr, _collections[i].addr);
                assertEq(uint256(c.nftType), uint256(_collections[i].nftType));
                assertEq(c.allowAllIds, _collections[i].allowAllIds);
                assertEq(c.tokenIds, _collections[i].tokenIds);

                for (uint256 j = 0; j < _collections[i].tokenIds.length; j++) {
                    assertTrue(vault.isTokenAllowed(_collections[i].addr, _collections[i].tokenIds[j]));
                }
            }

            vm.expectRevert(INftVault.Initialized.selector);
            vault.init(_collections);
        }
    }

    function testValidateNftType() public {
        NftVault nftVault = new NftVault("name", "symbol");

        assertEq(
            uint256(nftVault.validateNftType(collectionERC721all.addr, collectionERC721all.nftType)),
            uint256(collectionERC721all.nftType)
        );

        assertEq(
            uint256(nftVault.validateNftType(collectionERC1155all.addr, collectionERC1155all.nftType)),
            uint256(collectionERC1155all.nftType)
        );

        assertEq(
            uint256(nftVault.validateNftType(collectionERC721allowed.addr, collectionERC721allowed.nftType)),
            uint256(collectionERC721allowed.nftType)
        );

        assertEq(
            uint256(nftVault.validateNftType(collectionERC1155allowed.addr, collectionERC1155allowed.nftType)),
            uint256(collectionERC1155allowed.nftType)
        );

        vm.expectRevert(INftVault.ExpectedERC721.selector);
        nftVault.validateNftType(collectionERC721allWrongNftType.addr, collectionERC721allWrongNftType.nftType);

        vm.expectRevert(INftVault.ExpectedERC1155.selector);
        nftVault.validateNftType(collectionERC1155allWrongNftType.addr, collectionERC1155allWrongNftType.nftType);

        // test hypothetical contract that supports ERC721 and ERC1155
        vm.mockCall(
            erc721and1155, abi.encodeCall(ERC165.supportsInterface, (type(IERC721).interfaceId)), abi.encode(true)
        );
        vm.mockCall(
            erc721and1155, abi.encodeCall(ERC165.supportsInterface, (type(IERC1155).interfaceId)), abi.encode(true)
        );
        assertEq(
            uint256(nftVault.validateNftType(erc721and1155, INftVault.NftType.ERC721)),
            uint256(INftVault.NftType.ERC721)
        );
        assertEq(
            uint256(nftVault.validateNftType(erc721and1155, INftVault.NftType.ERC1155)),
            uint256(INftVault.NftType.ERC1155)
        );
        vm.clearMockedCalls();
    }

    function testIsTokenAllowed(address collectionAddr, uint256 tokenId) public {
        delete collections;
        INftVault.CollectionData memory config = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIds
        });
        collections.push(config);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        vm.assume(collectionAddr != config.addr);

        for (uint256 i = 0; i < erc721tokenIds.length; i++) {
            vm.assume(tokenId != erc721tokenIds[i]);
        }

        assertFalse(nftVault.isTokenAllowed(collectionAddr, tokenId));
    }

    function testGetSentTokenBalance(uint256 tokenId, uint256 amount) public {
        delete collections;
        collections.push(collectionERC721all);
        collections.push(collectionERC1155all);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        ERC721Mintable erc721 = ERC721Mintable(collectionERC721all.addr);
        ERC1155Mintable erc1155 = ERC1155Mintable(collectionERC1155all.addr);

        erc721.mint(user1, tokenId);
        vm.prank(user1);
        erc721.transferFrom(user1, address(nftVault), tokenId);
        assertEq(nftVault.getSentTokenBalance(address(erc721), tokenId), 1);

        erc1155.mint(user1, tokenId, amount);
        vm.prank(user1);
        erc1155.safeTransferFrom(user1, address(nftVault), tokenId, amount, bytes(""));
        assertEq(nftVault.getSentTokenBalance(address(erc1155), tokenId), amount);
    }

    function testDepositRevert(uint256 _tokenId, uint256 _amount) public {
        uint256 otherTokenId = 56464987645;
        vm.assume(_tokenId != otherTokenId);
        vm.assume(_amount < type(uint256).max);

        delete collections;
        collections.push(collectionERC721all);
        collections.push(collectionERC1155all);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        vm.expectRevert(INftVault.DisallowedToken.selector);
        nftVault.deposit(user1, address(5566), _tokenId, 1);

        ERC721Mintable(collections[0].addr).mint(user2, otherTokenId);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.deposit(user1, collections[0].addr, otherTokenId, 1);

        ERC721Mintable(collections[0].addr).mint(address(nftVault), _tokenId);
        assertEq(nftVault.getSentTokenBalance(collections[0].addr, _tokenId), 1);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.deposit(user1, collections[0].addr, _tokenId, 0);

        ERC1155Mintable(collections[1].addr).mint(address(nftVault), _tokenId, _amount);
        assertEq(nftVault.getSentTokenBalance(collections[1].addr, _tokenId), _amount);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.deposit(user1, collections[1].addr, _tokenId, _amount + 1);
    }

    function testNoDepositGriefing() public {
        delete collections;
        collections.push(collectionERC1155all);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        // User deposited 1 token to the vault
        ERC1155Mintable(collections[0].addr).mint(address(nftVault), 1, 1);
        nftVault.deposit(user1, collections[0].addr, 1, 1);

        // Malicious user manually transferred 1 token to the vault
        vm.startPrank(user2);
        ERC1155Mintable(collections[0].addr).mint(user2, 1, 1);
        ERC1155Mintable(collections[0].addr).safeTransferFrom(user2, address(nftVault), 1, 1, bytes(""));
        vm.stopPrank();

        // User deposited 1 more token to the vault
        ERC1155Mintable(collections[0].addr).mint(address(nftVault), 1, 1);
        nftVault.deposit(user1, collections[0].addr, 1, 1);
    }

    function testDepositAllConfigs(uint256 _tokenId, uint256 _amount) public {
        for (uint256 configId = 0; configId < 8; configId++) {
            console2.log("configId", configId);
            INftVault.CollectionData[] memory _collections = _getConfig(configId);
            NftVault vault = NftVault(address(nftVaultFactory.createVault(_collections)));

            for (uint256 i = 0; i < _collections.length; i++) {
                if (!collections[i].allowAllIds) {
                    // if not all allowed, take random tokenId that is allowed with fuzzing for randomness
                    _tokenId = collections[i].tokenIds[_tokenId % collections[i].tokenIds.length];
                }

                if (collections[i].nftType == INftVault.NftType.ERC721) {
                    _amount = 1;
                    ERC721Mintable(collections[i].addr).mint(address(vault), _tokenId);
                } else {
                    ERC1155Mintable(collections[i].addr).mint(address(vault), _tokenId, _amount);
                }

                uint256 balancesBefore = vault.balances(collections[i].addr, _tokenId);
                uint256 erc20balanceBefore = vault.balanceOf(user1);

                vm.expectEmit(true, true, true, true);
                emit Deposit(user1, collections[i].addr, _tokenId, _amount);
                uint256 amountMinted = vault.deposit(user1, collections[i].addr, _tokenId, _amount);

                assertEq(amountMinted / vault.ONE(), _amount);
                assertEq(vault.balanceOf(user1), erc20balanceBefore + amountMinted);
                assertEq(vault.balances(collections[i].addr, _tokenId), balancesBefore + _amount);
            }
        }
    }

    function testDepositBatchWithdrawBatch(uint256 _tokenId, uint256[] memory _amounts) public {
        delete collections;
        collections.push(collectionERC721all);
        collections.push(collectionERC1155all);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        vm.assume(_amounts.length > 10);

        assertEq(nftVault.balanceOf(user1), 0);

        address[] memory collectionBatch = new address[](10);
        uint256[] memory tokenIdBatch = new uint256[](10);
        uint256[] memory amountBatch = new uint256[](10);
        uint256 expectedAmountMinted = 0;

        for (uint256 i = 0; i < 10; i++) {
            vm.assume(_tokenId < type(uint64).max);
            uint256 tokenId = _tokenId + i;

            vm.assume(_amounts[i] > 0);
            vm.assume(_amounts[i] < type(uint64).max);
            uint256 amount = _amounts[i];

            if (i % 2 == 0) {
                ERC721Mintable(collections[0].addr).mint(address(nftVault), tokenId);
                collectionBatch[i] = collections[0].addr;
                tokenIdBatch[i] = tokenId;
                amountBatch[i] = 1;
            } else {
                ERC1155Mintable(collections[1].addr).mint(address(nftVault), tokenId, amount);
                collectionBatch[i] = collections[1].addr;
                tokenIdBatch[i] = tokenId;
                amountBatch[i] = amount;
            }

            expectedAmountMinted += amountBatch[i];
        }

        uint256 amountMinted = nftVault.depositBatch(user1, collectionBatch, tokenIdBatch, amountBatch);

        assertEq(amountMinted / nftVault.ONE(), expectedAmountMinted);
        assertEq(nftVault.balanceOf(user1), expectedAmountMinted * nftVault.ONE());
        assertEq(nftVault.balanceOf(user1), amountMinted);

        for (uint256 i = 0; i < 10; i++) {
            assertEq(nftVault.balances(collectionBatch[i], tokenIdBatch[i]), amountBatch[i]);
        }

        vm.prank(user1);
        nftVault.transfer(address(nftVault), amountMinted);

        uint256 amountBurned = nftVault.withdrawBatch(user2, collectionBatch, tokenIdBatch, amountBatch);

        assertEq(amountBurned, amountMinted);
        assertEq(nftVault.balanceOf(user1), 0);
        assertEq(nftVault.balanceOf(user2), 0);

        for (uint256 i = 0; i < 10; i++) {
            if (i % 2 == 0) {
                assertEq(ERC721Mintable(collections[0].addr).ownerOf(tokenIdBatch[i]), user2);
            } else {
                assertEq(ERC1155Mintable(collections[1].addr).balanceOf(user2, tokenIdBatch[i]), amountBatch[i]);
            }
            assertEq(nftVault.balances(collectionBatch[i], tokenIdBatch[i]), 0);
        }
    }

    function testWithdrawRevert(uint256 _tokenId, uint256 _amount) public {
        vm.assume(_amount > 0);
        vm.assume(_amount < type(uint128).max);

        INftVault.CollectionData[] memory _collections = _getConfig(4);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(_collections)));

        ERC721Mintable(collections[0].addr).mint(address(nftVault), _tokenId);
        ERC1155Mintable(collections[1].addr).mint(address(nftVault), _tokenId, _amount);

        uint256 amountMinted721 = nftVault.deposit(user1, collections[0].addr, _tokenId, 1);

        assertEq(amountMinted721, 1 * nftVault.ONE());

        uint256 amountMinted1155 = nftVault.deposit(user1, collections[1].addr, _tokenId, _amount);

        assertEq(amountMinted1155, _amount * nftVault.ONE());

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.withdraw(user1, collections[0].addr, _tokenId, 0);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.withdraw(user1, collections[1].addr, _tokenId, 0);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.withdraw(user1, collections[0].addr, _tokenId, 2);

        vm.expectRevert(INftVault.WrongAmount.selector);
        nftVault.withdraw(user1, collections[1].addr, _tokenId, _amount + 1);

        vm.expectRevert("ERC20: burn amount exceeds balance");
        nftVault.withdraw(user1, collections[0].addr, _tokenId, 1);

        vm.expectRevert("ERC20: burn amount exceeds balance");
        nftVault.withdraw(user1, collections[1].addr, _tokenId, _amount);
    }

    function testWithdraw(uint256 _tokenId, uint256 _amount) public {
        vm.assume(_amount > 1);
        vm.assume(_amount < type(uint128).max);

        INftVault.CollectionData[] memory _collections = _getConfig(4);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(_collections)));

        ERC721Mintable(collections[0].addr).mint(address(nftVault), _tokenId);
        ERC1155Mintable(collections[1].addr).mint(address(nftVault), _tokenId, _amount);

        uint256 amountMinted721 = nftVault.deposit(user1, collections[0].addr, _tokenId, 1);

        assertEq(amountMinted721, 1 * nftVault.ONE());

        uint256 amountMinted1155 = nftVault.deposit(user2, collections[1].addr, _tokenId, _amount);

        assertEq(amountMinted1155, _amount * nftVault.ONE());

        vm.prank(user1);
        nftVault.transfer(address(nftVault), amountMinted721);

        uint256 amountBurned = nftVault.withdraw(user1, collections[1].addr, _tokenId, 1);

        assertEq(amountBurned, amountMinted721);
        assertEq(nftVault.balanceOf(user1), 0);
        assertEq(ERC721Mintable(_collections[0].addr).ownerOf(_tokenId), address(nftVault));
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(user1, _tokenId), 1);
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(address(nftVault), _tokenId), _amount - 1);

        uint256 transferAmount = amountMinted1155 - nftVault.ONE();
        vm.prank(user2);
        nftVault.transfer(address(nftVault), transferAmount);

        amountBurned = nftVault.withdraw(user2, collections[1].addr, _tokenId, _amount - 1);

        assertEq(amountBurned, transferAmount);
        assertEq(nftVault.balanceOf(user2), nftVault.ONE());
        assertEq(ERC721Mintable(_collections[0].addr).ownerOf(_tokenId), address(nftVault));
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(user2, _tokenId), _amount - 1);
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(address(nftVault), _tokenId), 0);

        transferAmount = nftVault.ONE();
        vm.prank(user2);
        nftVault.transfer(address(nftVault), transferAmount);

        amountBurned = nftVault.withdraw(user2, collections[0].addr, _tokenId, 1);

        assertEq(amountBurned, transferAmount);
        assertEq(nftVault.balanceOf(user2), 0);
        assertEq(ERC721Mintable(_collections[0].addr).ownerOf(_tokenId), user2);
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(user1, _tokenId), 1);
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(user2, _tokenId), _amount - 1);
        assertEq(ERC1155Mintable(_collections[1].addr).balanceOf(address(nftVault), _tokenId), 0);
    }

    function testWithdrawLast(uint256 _tokenId, uint256 _amount) public {
        vm.assume(_amount > 1);
        vm.assume(_amount < type(uint128).max);

        INftVault.CollectionData[] memory _collections = _getConfig(4);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(_collections)));

        ERC721Mintable(collections[0].addr).mint(address(nftVault), _tokenId);
        ERC1155Mintable(collections[1].addr).mint(address(nftVault), _tokenId, _amount);

        address[] memory lastCollections = new address[](2);
        uint256[] memory lastTokenIds = new uint256[](2);
        uint256[] memory lastAmounts = new uint256[](2);

        lastCollections[0] = collections[0].addr;
        lastCollections[1] = collections[1].addr;

        lastTokenIds[0] = _tokenId;
        lastTokenIds[1] = _tokenId;

        lastAmounts[0] = 1;
        lastAmounts[1] = _amount;

        uint256 amountMinted = nftVault.depositBatch(user1, lastCollections, lastTokenIds, lastAmounts);

        assertEq(amountMinted, (_amount + 1) * nftVault.ONE());
        assertEq(nftVault.balanceOf(user1), amountMinted);

        vm.startPrank(user1);
        nftVault.transfer(owner, nftVault.ONE() - nftVault.LAST_NFT_AMOUNT());
        nftVault.transfer(address(nftVault), nftVault.balanceOf(user1));
        vm.stopPrank();

        nftVault.withdrawBatch(user1, lastCollections, lastTokenIds, lastAmounts);

        assertEq(ERC721Mintable(collections[0].addr).ownerOf(_tokenId), user1);
        assertEq(ERC1155Mintable(collections[1].addr).balanceOf(user1, _tokenId), _amount);
        assertEq(nftVault.balanceOf(user1), 0);
        assertEq(nftVault.balanceOf(address(nftVault)), 0);
        assertEq(nftVault.totalSupply(), nftVault.ONE() - nftVault.LAST_NFT_AMOUNT());

        vm.startPrank(user1);
        ERC721Mintable(collections[0].addr).transferFrom(user1, address(nftVault), _tokenId);
        ERC1155Mintable(collections[1].addr).safeTransferFrom(user1, address(nftVault), _tokenId, _amount, bytes(""));
        vm.stopPrank();

        uint256 amountMinted2 = nftVault.depositBatch(user1, lastCollections, lastTokenIds, lastAmounts);
        assertEq(amountMinted2, (_amount + 1) * nftVault.ONE() - nftVault.ONE() + nftVault.LAST_NFT_AMOUNT());
    }

    function testSkim(uint256 tokenId, uint256 amount) public {
        delete collections;
        collections.push(collectionERC721all);
        collections.push(collectionERC1155all);
        NftVault nftVault = NftVault(address(nftVaultFactory.createVault(collections)));

        ERC721Mintable erc721 = ERC721Mintable(collectionERC721all.addr);
        ERC1155Mintable erc1155 = ERC1155Mintable(collectionERC1155all.addr);

        assertTrue(nftVault.isTokenAllowed(address(erc721), tokenId));
        assertEq(erc721.balanceOf(user2), 0);
        erc721.mint(user1, tokenId);
        vm.prank(user1);
        erc721.transferFrom(user1, address(nftVault), tokenId);
        vm.prank(user2);
        vm.expectRevert(INftVault.MustBeDisallowedToken.selector);
        nftVault.skim(user2, INftVault.NftType.ERC721, address(erc721), tokenId, 1);

        assertTrue(nftVault.isTokenAllowed(address(erc1155), tokenId));
        assertEq(erc1155.balanceOf(user2, tokenId), 0);
        erc1155.mint(user1, tokenId, amount);
        vm.prank(user1);
        erc1155.safeTransferFrom(user1, address(nftVault), tokenId, amount, bytes(""));
        vm.prank(user2);
        vm.expectRevert(INftVault.MustBeDisallowedToken.selector);
        nftVault.skim(user2, INftVault.NftType.ERC1155, address(erc1155), tokenId, amount);
        assertEq(erc1155.balanceOf(user2, tokenId), 0);

        ERC721Mintable newErc721 = new ERC721Mintable();

        newErc721.mint(address(nftVault), tokenId);
        assertFalse(nftVault.isTokenAllowed(address(newErc721), tokenId));
        vm.prank(user2);
        nftVault.skim(user2, INftVault.NftType.ERC721, address(newErc721), tokenId, amount);
        assertEq(newErc721.balanceOf(user2), 1);

        ERC1155Mintable newErc1155 = new ERC1155Mintable();

        newErc1155.mint(address(nftVault), tokenId, amount);
        assertFalse(nftVault.isTokenAllowed(address(newErc1155), tokenId));
        vm.prank(user2);
        nftVault.skim(user2, INftVault.NftType.ERC1155, address(newErc1155), tokenId, amount);
        assertEq(newErc1155.balanceOf(user2, tokenId), amount);
    }
}

contracts/Vault/test/NftVaultFactory.t.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import "forge-std/Test.sol";

import "lib/ERC721Mintable.sol";
import "lib/ERC1155Mintable.sol";

import "../INftVault.sol";
import "../NftVaultFactory.sol";

contract NftVaultFactoryTest is Test {
    address user1 = address(1001);
    address user2 = address(1002);
    address owner1 = address(1003);
    address owner2 = address(1004);
    address erc721and1155 = address(888999);

    uint256[] public erc721tokenIds = [1, 6, 15, 22];
    uint256[] public erc721tokenIdsUnsorted = [1, 6, 16, 15, 22];
    uint256[] public erc721tokenIdsDuplicated = [1, 6, 15, 15, 22];
    uint256[] public erc1155tokenIds = [8, 21, 32, 33, 35];

    INftVault.CollectionData public collectionERC721all;
    INftVault.CollectionData public collectionERC1155all;
    INftVault.CollectionData public collectionERC721allowed;
    INftVault.CollectionData public collectionERC1155allowed;
    INftVault.CollectionData public collectionAllWithTokenIds;
    INftVault.CollectionData public collectionERC721allWrongNftType;
    INftVault.CollectionData public collectionERC1155allWrongNftType;
    INftVault.CollectionData public collectionERC721allowedMissingTokens;
    INftVault.CollectionData public collectionERC721allowedUnsortedTokens;
    INftVault.CollectionData public collectionERC721allowedDuplicatedTokens;

    INftVault.CollectionData[] public collections;

    event Deposit(address indexed to, address indexed collection, uint256 tokenId, uint256 amount);

    function setUp() public {
        collectionERC721all = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC1155all = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowed = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIds
        });

        collectionERC1155allowed = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: false,
            tokenIds: erc1155tokenIds
        });

        collectionAllWithTokenIds = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: erc1155tokenIds
        });

        collectionERC721allWrongNftType = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC1155,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC1155allWrongNftType = INftVault.CollectionData({
            addr: address(new ERC1155Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: true,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowedMissingTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: new uint256[](0)
        });

        collectionERC721allowedUnsortedTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIdsUnsorted
        });

        collectionERC721allowedDuplicatedTokens = INftVault.CollectionData({
            addr: address(new ERC721Mintable()),
            nftType: INftVault.NftType.ERC721,
            allowAllIds: false,
            tokenIds: erc721tokenIdsDuplicated
        });
    }

    function _getConfig(uint256 configId) public returns (INftVault.CollectionData[] memory) {
        delete collections;

        // deploy fresh NFTs at every config request
        collectionERC721all.addr = address(new ERC721Mintable());
        collectionERC1155all.addr = address(new ERC1155Mintable());
        collectionERC721allowed.addr = address(new ERC721Mintable());
        collectionERC1155allowed.addr = address(new ERC1155Mintable());

        if (configId == 0) {
            collections.push(collectionERC721all);
        } else if (configId == 1) {
            collections.push(collectionERC1155all);
        } else if (configId == 2) {
            collections.push(collectionERC721allowed);
        } else if (configId == 3) {
            collections.push(collectionERC1155allowed);
        } else if (configId == 4) {
            collections.push(collectionERC721all);
            collections.push(collectionERC1155all);
        } else if (configId == 5) {
            collections.push(collectionERC721allowed);
            collections.push(collectionERC1155allowed);
        } else if (configId == 6) {
            collections.push(collectionERC721all);
            collections.push(collectionERC1155allowed);
        } else if (configId == 7) {
            collections.push(collectionERC721allowed);
            collections.push(collectionERC1155all);
        } else {
            revert("WrongConfig");
        }

        return collections;
    }

    function testVaultAlreadyDeployed() public {
        NftVaultFactory vaultFactory = new NftVaultFactory();

        INftVault.CollectionData[] memory _collections = _getConfig(0);

        vaultFactory.createVault(_collections);

        vm.expectRevert(INftVaultFactory.VaultAlreadyDeployed.selector);
        vaultFactory.createVault(_collections);
    }

    function testAllGetters() public {
        NftVaultFactory vaultFactory = new NftVaultFactory();

        address[] memory vaults = new address[](8);

        for (uint256 configId = 0; configId < 8; configId++) {
            INftVault.CollectionData[] memory _collections = _getConfig(configId);

            INftVault vault = vaultFactory.createVault(_collections);

            vaults[configId] = address(vault);

            address[] memory getAllVaults = new address[](configId + 1);
            for (uint256 i = 0; i < getAllVaults.length; i++) {
                if (vaults[i] != address(0)) {
                    getAllVaults[i] = vaults[i];
                }
            }

            assertEq(vaultFactory.getAllVaults(), getAllVaults);
            assertEq(vaultFactory.getVaultAt(configId), vaults[configId]);
            assertEq(vaultFactory.getVaultLength(), configId + 1);
            assertEq(vaultFactory.isVault(vaults[configId]), true);
            assertEq(vaultFactory.isVault(address(uint160(vaults[configId]) + 1)), false);
            assertEq(address(vaultFactory.getVault(_collections)), vaults[configId]);
            assertEq(vaultFactory.exists(_collections), true);
            assertEq(address(vaultFactory.vaultHashMap(vaultFactory.hashVault(_collections))), vaults[configId]);
            assertEq(vaultFactory.getVaultAt(vaultFactory.vaultIdMap(INftVault(vaults[configId]))), vaults[configId]);
        }
    }
}

contracts/VaultPermissioned/INftVaultFac.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "./INftVaultPermissioned.sol";

/// @title Vault factory contract
interface INftVaultFactoryPermissioned {
    /// @notice Emitted when new vault is deployed
    /// @param name vault's name
    /// @param symbol vault's name
    /// @param vault vault's address as INftVault
    /// @param vaultId vault's index in `vaults` AddressSet
    /// @param collections configuration used for vault creation
    /// @param creator address of vault creator
    event VaultCreated(
        string name,
        string symbol,
        INftVaultPermissioned vault,
        uint256 vaultId,
        INftVaultPermissioned.CollectionData[] collections,
        address creator,
        address owner
    );

    /// @dev Vault does not exist
    error VaultDoesNotExist();

    /// @dev Vault with identical configuration is already deployed
    error VaultAlreadyDeployed();

    /// @notice Get vault by its config hash
    /// @param hash vault's config hash
    /// @return vault address
    function vaultHashMap(bytes32 hash) external view returns (INftVaultPermissioned vault);

    /// @return all deployed vaults
    function getAllVaults() external view returns (address[] memory);

    /// @notice Get vault by its EnumerableSet vaultId
    /// @param i vaultId
    /// @return vault address
    function getVaultAt(uint256 i) external view returns (address);

    /// @return length of vault's EnumerableSet
    function getVaultLength() external view returns (uint256);

    /// @notice Returns true if vault has been deployed by factory
    /// @param vault address
    function isVault(address vault) external view returns (bool);

    /// @return all deployed permissioned vaults
    function getAllPermissionedVaults() external view returns (address[] memory);

    /// @notice Get permissioned vault by its EnumerableSet vaultId
    /// @param i vaultId
    /// @return vault address
    function getPermissionedVaultAt(uint256 i) external view returns (address);

    /// @return length of permissioned vault's EnumerableSet
    function getPermissionedVaultLength() external view returns (uint256);

    /// @notice Returns true if permissioned vault has been deployed by factory
    /// @param vault address
    function isPermissionedVault(address vault) external view returns (bool);

    /// @notice Get vault by it's config
    /// @param collections vault's config
    /// @return vault address
    function getVault(INftVaultPermissioned.CollectionData[] memory collections)
        external
        view
        returns (INftVaultPermissioned vault);

    /// @notice Returns true if vault with given config exists
    /// @param collections vault's config
    /// @return true if vault with given config exists
    function exists(INftVaultPermissioned.CollectionData[] memory collections) external view returns (bool);

    /// @notice Get config hash
    /// @param collections vault's config
    /// @return config hash
    function hashVault(INftVaultPermissioned.CollectionData[] memory collections) external pure returns (bytes32);

    /// @notice Create new vault
    /// @dev If vault already exists, function returned already deployed vault
    /// @param collections vault's config
    /// @param owner address of owner if vault is permissioned, otherwise address(0) and vault is permissionless
    /// @param isSoulbound if true, Vault is soulbound and its ERC20 token can only be transfered
    ///        to `allowedContracts` managed by `owner`
    /// @return vault address of (newly) deployed vault
    function createVault(INftVaultPermissioned.CollectionData[] memory collections, address owner, bool isSoulbound)
        external
        returns (INftVaultPermissioned vault);
}

contracts/VaultPermissioned/INftVaultPer.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

/// @title Vault contract for wrapping NFTs (ERC721/ERC1155) to ERC20
interface INftVaultPermissioned {
    enum NftType {
        ERC721,
        ERC1155
    }

    /// @notice Vault configuration struct that specifies which NFTs are accepted in vault.
    /// @param addr address of nft contract
    /// @param nftType standard that NFT supports { ERC721, ERC1155 }
    /// @param allowAllIds if true, all tokens are allowed in the vault. If false, tokenIds must be
    ///        listed one by one.
    /// @param tokenIds list of tokens supported by vault. If allowAllIds is true, list must be empty.
    struct CollectionData {
        address addr;
        NftType nftType;
        bool allowAllIds;
        uint256[] tokenIds;
    }

    /// @notice Struct for allowed tokens. Stores data in an optimized way to read it in vault.
    /// @param tokenIds mapping from tokenid to is-allowed
    /// @param tokenIdList list of all tokens that are allowed
    /// @param allowAllIds if true, all tokens are allowed
    struct AllowedTokenIds {
        mapping(uint256 => bool) tokenIds;
        uint256[] tokenIdList;
        bool allowAllIds;
    }

    /// @notice Emitted during initiation when collection added to allowed list
    /// @param collection collection details
    event CollectionAllowed(CollectionData collection);

    /// @notice Emitted on depositing NFT to vault
    /// @param to address that gets vault ERC20 tokens
    /// @param collection NFT address that is deposited
    /// @param tokenId token id that is deposited
    /// @param amount amount of token that is deposited, for ERC721 always 1
    event Deposit(address to, address collection, uint256 tokenId, uint256 amount);

    /// @notice Emitted on withdrawing NFT from vault
    /// @param to address that gets withdrawn NFTs
    /// @param collection NFT address that is withdrawn
    /// @param tokenId token id that is withdrawn
    /// @param amount amount of token that is withdrawn, for ERC721 always 1
    event Withdraw(address to, address collection, uint256 tokenId, uint256 amount);

    /// @notice Emitted when adding a wallet to deposit/withdraw allow list
    /// @param wallet address that is allowed to deposit/withdraw
    event AllowedDepositWithdraw(address wallet);

    /// @notice Emitted when removing a wallet from deposit/withdraw allow list
    /// @param wallet address that is disallowed to deposit/withdraw
    event DisallowedDepositWithdraw(address wallet);

    /// @notice Emitted when `contractAddress` is allowed to receive Vault ERC20 token
    /// @param contractAddress address that is allowed to receive Vault ERC20 token
    event AllowedContract(address contractAddress);

    /// @notice Emitted when `contractAddress` address that is disallowed to receive Vault ERC20 token
    /// @param contractAddress address that is disallowed to receive Vault ERC20 token
    event DisallowedContract(address contractAddress);

    /// @dev Contract is already initialized
    error Initialized();
    /// @dev Collection data is empty
    error InvalidCollections();
    /// @dev Token id is listed twice in CollectionData.tokenIds array
    error TokenIdAlreadySet();
    /// @dev Token ids in CollectionData.tokenIds array are not sorted
    error TokenIdsMustBeSorted();
    /// @dev ERC165 suggests that NFT is supporting ERC721 but ERC1155 is claimed
    error ExpectedERC721();
    /// @dev ERC165 suggests that NFT is supporting ERC1155 but ERC721 is claimed
    error ExpectedERC1155();
    /// @dev Collection does not support all token IDs however list of IDs is empty.
    ///      CollectionData.tokenIds is empty and CollectionData.allowAllIds is false.
    error MissingTokenIds();
    /// @dev CollectionData.tokenIds is not empty however Collection supports all token IDs.
    error TokenIdsMustBeEmpty();
    /// @dev Token is not allowed in vault
    error DisallowedToken();
    /// @dev Token amount is invalid eg. amount == 0
    error WrongAmount();
    /// @dev Token amount is invalid for ERC721, amount != 1
    error WrongERC721Amount();
    /// @dev Trying to interact with token that does not support ERC721 nor ERC1155
    error UnsupportedNft();
    /// @dev Token is allowed in vault but must not be
    error MustBeDisallowedToken();
    /// @dev User is not allowed to deposit or withdraw
    error NotAllowed();
    /// @dev Owner is required to manage `allowedContracts` when Vault is deployed as soulbound
    error OwnerRequiredForSoulbound();
    /// @dev Transfer of Vault ERC20 token to disallowed receiver
    error SoulboundTransferDisallowed();

    /// @notice value of 1 token, including decimals
    function ONE() external view returns (uint256);

    /// @notice minimum liquidity that is frozen in UniV2 pool
    function UNIV2_MINIMUM_LIQUIDITY() external view returns (uint256);

    /// @notice unique id of the vault generated using its configuration
    function VAULT_HASH() external view returns (bytes32);

    /// @notice if Vault is soulbound, its ERC20 token can only be transfered to `allowedContracts`
    /// @return true if Vault is soulbound, false otherwise
    function isSoulbound() external view returns (bool);

    /// @notice Initialize Vault with collection config
    /// @dev Called by factory during deployment
    /// @param collections struct array of allowed collections and token IDs
    function init(CollectionData[] memory collections) external;

    /// @notice Returns true if wallet is allowed to deposit/withdraw. Only applicable to permissioned vault.
    /// @dev Call `isPermissioned()` first to make sure vault is permissioned. Otherwise this function is irrelevant.
    /// @param wallet address that is checked
    /// @return true if wallet is allowed, false otherwise. For permissionless vault always returns false.
    function allowedWallets(address wallet) external view returns (bool);

    /// @notice Is vault permissioned
    /// @return true if vault has an owner and is permissioned. False otherwise.
    function isPermissioned() external view returns (bool);

    /// @notice Returns hash of vault configuration
    /// @param collections struct array of allowed collections and token IDs
    /// @return configuration hash
    function hashVault(CollectionData[] memory collections) external pure returns (bytes32);

    /// @notice Returns balances of NFT deposited to the vault
    /// @param collectionAddr NFT address
    /// @param tokenId NFT's token ID
    /// @return amount amount of NFT deposited to the vault
    function balances(address collectionAddr, uint256 tokenId) external view returns (uint256 amount);

    /// @notice Get array of NFT addresses that are allowed to be deposited to the vault
    /// @dev Keep in mind that returned address(es) can be further restricted on token ID level
    /// @return collections array of NFT addresses that are allowed to be deposited to the vault
    function getAllowedCollections() external view returns (address[] memory collections);

    /// @return number of NFT addresses that are allowed to be deposited to the vault
    function getAllowedCollectionsLength() external view returns (uint256);

    /// @notice Get details of allowed collection
    /// @return struct with details of allowed collection
    function getAllowedCollectionData(address collectionAddr) external view returns (CollectionData memory);

    /// @notice Validates type of collection (ERC721 or ERC1155)
    /// @dev It uses ERC165 to check interface support. If support can not be detected without doubt, user input is trusted.
    /// @param collectionAddr NFT address
    /// @param nftType NFT type, ERC721 or ERC1155
    /// @return validatedNftType returns validated enum NftType as uint256
    function validateNftType(address collectionAddr, NftType nftType)
        external
        view
        returns (uint256 validatedNftType);

    /// @notice Returns if true token can be deposited
    /// @param collection NFT address
    /// @param tokenId NFT token ID
    /// @return true if allowed
    function isTokenAllowed(address collection, uint256 tokenId) external view returns (bool);

    /// @notice Returns balance of token sent to the vault
    /// @dev Reads balance of tokens freshy sent to the vault
    /// @param collection NFT address
    /// @param tokenId NFT token ID
    /// @return balance of sent token, for ERC721 it's always 1
    function getSentTokenBalance(address collection, uint256 tokenId) external view returns (uint256);

    /// @notice Deposit NFT to vault
    /// @param to address that gets minted ERC20 token
    /// @param collection address of deposited NFT
    /// @param tokenId token ID of deposited NFT
    /// @param amount amount of deposited NFT, for ERC721 it's always 1
    /// @return amountMinted amount of minted ERC20 token
    function deposit(address to, address collection, uint256 tokenId, uint256 amount)
        external
        returns (uint256 amountMinted);

    /// @notice Deposit NFTs to vault
    /// @param to address that gets minted ERC20 token
    /// @param collection array of addresses of deposited NFTs
    /// @param tokenId array of token IDs of deposited NFTs
    /// @param amount array if amounts of deposited NFTs, for ERC721 it's always 1
    /// @return amountMinted amount of minted ERC20 token
    function depositBatch(address to, address[] memory collection, uint256[] memory tokenId, uint256[] memory amount)
        external
        returns (uint256 amountMinted);

    /// @notice Withdraw NFT from vault
    /// @param to address that gets NFT
    /// @param collection address of NFT to withdraw
    /// @param tokenId token ID of NFT to withdraw
    /// @param amount amount of NFT to withdraw, for ERC721 it's always 1
    /// @return amountBurned amount of burned ERC20
    function withdraw(address to, address collection, uint256 tokenId, uint256 amount)
        external
        returns (uint256 amountBurned);

    /// @notice Withdraw NFTs from vault
    /// @param to address that gets NFT
    /// @param collection array of addresses of NFTs to withdraw
    /// @param tokenId array of token IDs of NFTs to withdraw
    /// @param amount array of amounts of NFTs to withdraw, for ERC721 it's always 1
    /// @return amountBurned amount of burned ERC20
    function withdrawBatch(address to, address[] memory collection, uint256[] memory tokenId, uint256[] memory amount)
        external
        returns (uint256 amountBurned);

    /// @notice Allow anyone to withdraw tokens sent to this vault by accident
    ///         Only unsupported NFTs can be skimmed.
    /// @param to address that gets NFT
    /// @param nftType NftType of skimmed NFT
    /// @param collection address of NFT to skim
    /// @param tokenId token ID of NFT to skim
    /// @param amount amount of NFT to skim, for ERC721 it's always 1
    function skim(address to, NftType nftType, address collection, uint256 tokenId, uint256 amount) external;

    /// @notice Allow wallet to deposit/withdraw. Only applicable to permissioned vault.
    /// @param wallet address that is allowed to deposit/withdraw
    function allowDepositWithdraw(address wallet) external;

    /// @notice Disallow wallet to deposit/withdraw. Only applicable to permissioned vault.
    /// @param wallet address that is disallowed to deposit/withdraw
    function disallowDepositWithdraw(address wallet) external;

    /// @notice Allow Vault ERC20 token to be transfered to `contractAddress`
    /// @param contractAddress address that is allowed to receive Vault ERC20 token
    function allowVaultTokenTransfersTo(address contractAddress) external;

    /// @notice Disallow Vault ERC20 token to be transfered to `contractAddress`
    /// @param contractAddress address that is disallowed to receive Vault ERC20 token
    function disallowVaultTokenTransfersTo(address contractAddress) external;
}

